import {
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
  type ExecuteParams,
} from "@avail-project/nexus-core";
import React from "react";
import DepositModal from "./components/deposit-modal";
import { Address } from "viem";
import Container from "./components/container";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { useNexus } from "../nexus/NexusProvider";

export interface BaseDepositProps {
  address: Address;
  token?: SUPPORTED_TOKENS;
  chain: SUPPORTED_CHAINS_IDS;
  chainOptions?: {
    id: number;
    name: string;
    logo: string;
  }[];
  depositExecute?: (
    token: SUPPORTED_TOKENS,
    amount: string,
    chainId: SUPPORTED_CHAINS_IDS,
    userAddress: `0x${string}`
  ) => Omit<ExecuteParams, "toChainId">;
}

interface NexusDepositProps extends BaseDepositProps {
  heading?: string;
  embed?: boolean;
  destinationLabel?: string;
}

const NexusDeposit = ({
  address,
  token = "USDC",
  chain,
  chainOptions, // pass to customise sources displayed, if not provided, all sources will be shown
  heading = "Deposit USDC",
  embed = false,
  destinationLabel,
  depositExecute,
}: NexusDepositProps) => {
  const { supportedChainsAndTokens } = useNexus();
  const formatedChainOptions =
    chainOptions ??
    supportedChainsAndTokens?.map((chain) => {
      return {
        id: chain.id,
        name: chain.name,
        logo: chain.logo,
      };
    });
  if (embed) {
    return (
      <Card>
        <CardHeader className="px-3">
          <CardTitle>{heading}</CardTitle>
        </CardHeader>
        <CardContent className="px-3">
          <Container
            address={address}
            token={token}
            chain={chain}
            chainOptions={formatedChainOptions}
            destinationLabel={destinationLabel}
            depositExecute={depositExecute}
          />
        </CardContent>
      </Card>
    );
  }
  return (
    <DepositModal
      address={address}
      token={token}
      chain={chain}
      chainOptions={formatedChainOptions}
      heading={heading}
      destinationLabel={destinationLabel}
      depositExecute={depositExecute}
    />
  );
};

export default NexusDeposit;

"use client";

import { Tabs, TabsContent, TabsList, TabsTrigger } from "../../ui/tabs";
import SimpleDeposit from "./simple-deposit";
import { useNexus } from "../../nexus/NexusProvider";
import { BaseDepositProps } from "../deposit";

interface ContainerProps extends BaseDepositProps {
  fiatSubheading?: string;
  destinationLabel?: string;
}

const Container = ({
  address,
  fiatSubheading = "Cards, Apple Pay",
  token,
  chain,
  chainOptions,
  destinationLabel,
  depositExecute,
}: ContainerProps) => {
  const { nexusSDK } = useNexus();
  return (
    <Tabs defaultValue="simple" className="w-full min-w-sm">
      <TabsList className="h-12 p-0 w-full">
        <TabsTrigger value="simple" className="px-2 py-1">
          <div className="flex items-center flex-col gap-y-1">
            <p className="text-sm data-[state=active]:font-bold">Wallet</p>
            <p className="text-[10px]">
              {nexusSDK?.utils?.truncateAddress(address, 4, 4)}
            </p>
          </div>
        </TabsTrigger>
        <TabsTrigger value="qr" className="px-2 py-1">
          <div className="flex items-center flex-col gap-y-1">
            <p className="text-sm data-[state=active]:font-bold">Transfer QR</p>
            <p className="text-[10px]">{chainOptions?.length ?? "0"} chains</p>
          </div>
        </TabsTrigger>
        <TabsTrigger value="fiat" className="px-2 py-1">
          <div className="flex items-center flex-col gap-y-1">
            <p className="text-sm data-[state=active]:font-bold">Fiat</p>
            <p className="text-[10px]">{fiatSubheading}</p>
          </div>
        </TabsTrigger>
      </TabsList>
      <TabsContent value="simple">
        <SimpleDeposit
          address={address}
          token={token}
          chain={chain}
          chainOptions={chainOptions}
          destinationLabel={destinationLabel}
          depositExecute={depositExecute}
        />
      </TabsContent>
      <TabsContent value="qr">
        <p className="text-xl text-primary text-center">Coming soon</p>
      </TabsContent>
      <TabsContent value="fiat">
        <p className="text-xl text-primary text-center">Coming soon</p>
      </TabsContent>
    </Tabs>
  );
};

export default Container;

"use client";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../../ui/dialog";
import Container from "./container";
import { BaseDepositProps } from "../deposit";
import { Button } from "../../ui/button";

interface DepositModalProps extends BaseDepositProps {
  heading?: string;
  destinationLabel?: string;
}

const DepositModal = ({
  address,
  token,
  chain,
  chainOptions,
  heading,
  destinationLabel,
  depositExecute,
}: DepositModalProps) => {
  return (
    <Dialog>
      <DialogTrigger asChild>
        <Button>Deposit</Button>
      </DialogTrigger>
      <DialogContent>
        <DialogHeader>
          <DialogTitle>{heading}</DialogTitle>
        </DialogHeader>
        <Container
          address={address}
          token={token}
          chain={chain}
          chainOptions={chainOptions}
          destinationLabel={destinationLabel}
          depositExecute={depositExecute}
        />
      </DialogContent>
    </Dialog>
  );
};

export default DepositModal;

"use client";

import React, { useCallback, useEffect, useMemo } from "react";
import { BaseDepositProps } from "../deposit";
import AmountInput from "./amount-input";
import SourceSelect from "./source-select";
import { Button } from "../../ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "../../ui/dialog";
import AllowanceModal from "./allowance-modal";
import BridgeExecuteProgress from "./transaction-progress";
import DepositFeeBreakdown from "./fee-breakdown";
import SourceBreakdown from "./source-breakdown";
import { useNexus } from "../../nexus/NexusProvider";
import useDeposit from "../hooks/useDeposit";
import { LoaderPinwheel, X } from "lucide-react";
import { Skeleton } from "../../ui/skeleton";
import {
  CHAIN_METADATA,
  type SUPPORTED_TOKENS,
} from "@avail-project/nexus-core";
import { formatUnits } from "viem";

interface SimpleDepositProps extends BaseDepositProps {
  destinationLabel?: string;
}

const SimpleDeposit = ({
  address,
  token,
  chain,
  chainOptions,
  destinationLabel = "on Hyperliquid Perps",
  depositExecute,
}: SimpleDepositProps) => {
  const {
    nexusSDK,
    intent,
    setIntent,
    unifiedBalance,
    allowance,
    setAllowance,
  } = useNexus();

  const {
    inputs,
    setInputs,
    loading,
    simulating,
    refreshing,
    lastResult,
    isDialogOpen,
    setIsDialogOpen,
    txError,
    setTxError,
    timer,
    filteredUnifiedBalance,
    simulation,
    startTransaction,
    stopTimer,
    clearSimulation,
    simulate,
    cancelSimulation,
    steps,
  } = useDeposit({
    token: token ?? "USDC",
    chain,
    nexusSDK,
    intent,
    setIntent,
    unifiedBalance,
    allowance,
    setAllowance,
    chainOptions,
    address,
    executeBuilder: depositExecute,
  });
  const allCompleted = steps?.length > 0 && steps.every((s) => s.completed);
  useEffect(() => {
    if (allCompleted) stopTimer();
  }, [allCompleted, stopTimer]);

  const renderDepositButtonContent = useCallback(() => {
    if (isDialogOpen) return "Deposit";
    if (refreshing)
      return (
        <div className="flex items-center gap-x-2">
          <LoaderPinwheel className="animate-spin size-4" />
          <p>Refreshing Quote</p>
        </div>
      );
    if (simulating)
      return (
        <div className="flex items-center gap-x-2">
          <LoaderPinwheel className="animate-spin size-4" />
          <p>Preparing Quote</p>
        </div>
      );
    return "Deposit";
  }, [isDialogOpen, refreshing, simulating]);

  const renderTotalFeeBreakdown = useMemo(() => {
    if (simulation?.bridgeSimulation) {
      return `${
        simulation.bridgeSimulation?.intent?.fees?.total ?? "0"
      } ${token} + ${
        formatUnits(
          simulation.executeSimulation.gasFee,
          CHAIN_METADATA[chain].nativeCurrency.decimals
        ) ?? "0"
      } ${CHAIN_METADATA[chain].nativeCurrency.symbol}`;
    }
    return ` ${
      formatUnits(
        simulation?.executeSimulation?.gasFee ?? BigInt(0),
        CHAIN_METADATA[chain].nativeCurrency.decimals
      ) ?? "0"
    } ${CHAIN_METADATA[chain].nativeCurrency.symbol}`;
  }, [simulation, chain, token]);

  return (
    <div className="flex flex-col items-center w-full gap-y-3 rounded-lg">
      {/* Sources */}
      <SourceSelect
        chainOptions={chainOptions}
        selected={inputs?.selectedSources}
        onChange={(selected) =>
          setInputs({ ...inputs, selectedSources: selected })
        }
        disabled={Boolean(simulation && !simulation?.bridgeSimulation)}
      />
      <AmountInput
        value={inputs?.amount}
        onChange={(value) => {
          setInputs({ ...inputs, amount: value });
          if (!value) {
            cancelSimulation();
            setTxError(null);
          }
        }}
        unifiedBalance={filteredUnifiedBalance}
        disabled={loading || simulating}
        onCommit={(value) => {
          setInputs({ ...inputs, amount: value });
          void simulate(value);
        }}
      />

      {/* Shimmer while simulating */}
      {simulating && !simulation && (
        <>
          <SourceBreakdown
            isLoading
            tokenSymbol={filteredUnifiedBalance?.symbol as SUPPORTED_TOKENS}
          />
          <div className="w-full flex items-start justify-between gap-x-4">
            <p className="text-base font-semibold">You Receive</p>
            <div className="flex flex-col gap-y-1 min-w-fit items-end">
              <Skeleton className="h-5 w-28" />
              <Skeleton className="h-4 w-36" />
            </div>
          </div>
          <DepositFeeBreakdown
            isLoading
            total={"0"}
            bridge={"0"}
            execute={"0"}
          />
        </>
      )}

      {simulation && inputs?.amount && (
        <>
          {simulation.bridgeSimulation?.intent && (
            <SourceBreakdown
              intent={simulation.bridgeSimulation.intent}
              tokenSymbol={filteredUnifiedBalance?.symbol as SUPPORTED_TOKENS}
              isLoading={refreshing}
            />
          )}

          <div className="w-full flex items-start justify-between gap-x-4">
            <p className="text-base font-semibold">You Receive</p>
            <div className="flex flex-col gap-y-1 min-w-fit">
              {refreshing ? (
                <Skeleton className="h-5 w-28" />
              ) : (
                <p className="text-base font-semibold text-right">
                  {inputs?.amount} {filteredUnifiedBalance?.symbol}
                </p>
              )}
              <p className="text-sm font-medium text-right">
                {destinationLabel}
              </p>
            </div>
          </div>

          <DepositFeeBreakdown
            total={renderTotalFeeBreakdown}
            bridge={`${
              simulation.bridgeSimulation?.intent?.fees?.total ?? "0"
            } ${token}`}
            execute={`${
              formatUnits(
                simulation.executeSimulation.gasFee,
                CHAIN_METADATA[chain].nativeCurrency.decimals
              ) ?? "0"
            } ${CHAIN_METADATA[chain].nativeCurrency.symbol}`}
            isLoading={refreshing}
          />
          {!simulation.bridgeSimulation && (
            <div className="w-full text-sm text-muted-foreground">
              Bridge skipped, executing directly on destination chain
            </div>
          )}
        </>
      )}

      {!intent &&
        (simulation ? (
          <div className="w-full flex items-center justify-center gap-x-2 px-1">
            <Button
              variant={"destructive"}
              onClick={() => {
                clearSimulation();
                setTxError(null);
              }}
              className="w-1/2"
            >
              Cancel
            </Button>
            <Button
              onClick={startTransaction}
              disabled={isDialogOpen || loading || simulating || refreshing}
              className="w-1/2"
            >
              {renderDepositButtonContent()}
            </Button>
          </div>
        ) : (
          <Button disabled={true} className="w-full px-2">
            {renderDepositButtonContent()}
          </Button>
        ))}

      <Dialog
        open={isDialogOpen}
        onOpenChange={(open) => {
          if (loading) return;
          setIsDialogOpen(open);
        }}
      >
        <DialogContent>
          <DialogHeader className="sr-only">
            <DialogTitle>Transaction Progress</DialogTitle>
          </DialogHeader>
          <BridgeExecuteProgress
            timer={timer}
            steps={steps}
            intentUrl={lastResult ? lastResult.bridgeExplorerUrl : undefined}
            executeUrl={lastResult ? lastResult.executeExplorerUrl : undefined}
          />
        </DialogContent>
      </Dialog>

      {allowance && (
        <AllowanceModal
          allowanceModal={allowance}
          setAllowanceModal={setAllowance}
          callback={startTransaction}
          onCloseCallback={clearSimulation}
        />
      )}

      {txError && (
        <div className="rounded-md border border-destructive bg-destructive/80 px-3 py-2 text-sm text-destructive-foreground flex items-start justify-between gap-x-3 mt-3 w-full max-w-lg">
          <span className="flex-1 max-w-md truncate">{txError}</span>
          <Button
            type="button"
            size={"icon"}
            variant={"ghost"}
            onClick={() => {
              clearSimulation();
              setTxError(null);
            }}
            className="text-destructive-foreground/80 hover:text-destructive-foreground focus:outline-none"
            aria-label="Dismiss error"
          >
            <X className="size-4" />
          </Button>
        </div>
      )}
    </div>
  );
};

export default SimpleDeposit;

/**
 * change you spend amount to receive + fee
 * if total amount is not being bridged then also show how much the user already has on the destination chain in you spend breakdown
 * if the input is cleared while a simulation is in progress, then don't show the simulation result. Basically stop the inflight request somehow, or don't display it on the UI same thing with the cancel button
 * add total token balance on the right side of the amount input
 */

import { ChevronDown } from "lucide-react";
import { Popover, PopoverContent, PopoverTrigger } from "../../ui/popover";
import { Label } from "../../ui/label";
import { Checkbox } from "../../ui/checkbox";

interface SourceSelectProps {
  chainOptions?: {
    id: number;
    name: string;
    logo: string;
  }[];
  selected?: number[];
  onChange?: (selected: number[]) => void;
  disabled?: boolean;
}

const SourceSelect = ({
  chainOptions,
  selected = [],
  onChange,
  disabled = false,
}: SourceSelectProps) => {
  const isSelected = (id: number) => selected?.includes(id);
  const toggle = (id: number) => {
    if (!onChange) return;
    if (disabled) return;
    if (isSelected(id)) onChange(selected.filter((s) => s !== id));
    else onChange([...selected, id]);
  };
  return (
    <Popover>
      <PopoverTrigger
        disabled={disabled}
        aria-disabled={disabled}
        className={`flex items-center justify-between w-full px-0 py-2 ${
          disabled ? "cursor-not-allowed opacity-60" : "cursor-pointer"
        }`}
      >
        Customise source chains
        <ChevronDown className="size-4 text-primary data-[state=open]:rotate-180 shrink-0 translate-y-0.5 transition-transform duration-200" />
      </PopoverTrigger>
      <PopoverContent className="grid grid-cols-2 w-max">
        {chainOptions ? (
          chainOptions?.map((chain) => {
            return (
              <div key={chain.id} className="flex items-center gap-x-2">
                <Checkbox
                  checked={isSelected(chain.id)}
                  onCheckedChange={() => toggle(chain.id)}
                  value={chain.id}
                  disabled={disabled}
                  className={`${
                    disabled ? "cursor-not-allowed" : "cursor-pointer"
                  }`}
                />
                <div className="flex items-center gap-x-2 p-2">
                  <img
                    src={chain.logo}
                    alt={chain?.name}
                    width={24}
                    height={24}
                    className="rounded-full"
                  />
                  <Label
                    className="text-primary test-sm"
                    htmlFor={String(chain.id)}
                  >
                    {chain.name}
                  </Label>
                </div>
              </div>
            );
          })
        ) : (
          <p>No option available</p>
        )}
      </PopoverContent>
    </Popover>
  );
};
export default SourceSelect;

"use client";

import { type UserAsset } from "@avail-project/nexus-core";
import { Button } from "../../ui/button";
import { Input } from "../../ui/input";
import { useCallback, useEffect, useRef } from "react";

const RANGE_OPTIONS = [
  {
    label: "25%",
    value: 0.25,
  },
  {
    label: "50%",
    value: 0.5,
  },
  {
    label: "75%",
    value: 0.75,
  },
  {
    label: "MAX",
    value: 1,
  },
];

interface AmountInputProps {
  value?: string;
  onChange: (value: string) => void;
  unifiedBalance?: UserAsset;
  disabled?: boolean;
  onCommit?: (value: string) => void;
}

const AmountInput = ({
  value,
  onChange,
  unifiedBalance,
  disabled,
  onCommit,
}: AmountInputProps) => {
  const commitTimerRef = useRef<NodeJS.Timeout | null>(null);

  const scheduleCommit = useCallback(
    (val: string) => {
      if (!onCommit || disabled) return;
      if (commitTimerRef.current) clearTimeout(commitTimerRef.current);
      commitTimerRef.current = setTimeout(() => {
        onCommit(val);
      }, 800);
    },
    [onCommit, disabled]
  );

  useEffect(() => {
    return () => {
      if (commitTimerRef.current) {
        clearTimeout(commitTimerRef.current);
        commitTimerRef.current = null;
      }
    };
  }, []);

  return (
    <div className="flex flex-col items-start gap-y-1 w-full py-2">
      <div className="flex items-center justify-between gap-x-3 w-full">
        <Input
          type="number"
          inputMode="decimal"
          step="any"
          min="0"
          placeholder="1.002..."
          value={value ?? ""}
          onChange={(e) => {
            onChange(e.target.value);
            scheduleCommit(e.target.value);
          }}
          onBlur={(e) => {
            if (commitTimerRef.current) {
              clearTimeout(commitTimerRef.current);
              commitTimerRef.current = null;
            }
            onCommit?.(e.target.value);
          }}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              if (commitTimerRef.current) {
                clearTimeout(commitTimerRef.current);
                commitTimerRef.current = null;
              }
              onCommit?.(value ?? "");
            }
          }}
          className="p-0 text-2xl! placeholder:text-2xl w-full border-none focus-visible:ring-0 focus-visible:ring-offset-0 shadow-none bg-transparent!"
          disabled={disabled}
        />
        {unifiedBalance && (
          <p className="text-base font-semibold min-w-max">
            {Number.parseFloat(unifiedBalance?.balance)?.toFixed(6)}{" "}
            {unifiedBalance?.symbol}
          </p>
        )}
      </div>
      <div className="flex gap-x-3">
        {RANGE_OPTIONS.map((option) => (
          <Button
            size={"icon"}
            variant={"ghost"}
            key={option.label}
            className="text-xs py-0.5 px-0 size-max"
            disabled={disabled}
            onClick={() => {
              if (!unifiedBalance?.balance) return;
              const max = Number(unifiedBalance?.balance);
              const next = (max * option.value).toString();
              onChange(next);
              onCommit?.(next);
            }}
          >
            {option.label}
          </Button>
        ))}
      </div>
    </div>
  );
};

export default AmountInput;

import React from "react";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";
import { Skeleton } from "../../ui/skeleton";

interface DepositFeeBreakdownProps {
  total: string;
  bridge: string;
  execute: string;
  isLoading?: boolean;
}

const DepositFeeBreakdown = ({
  total,
  bridge,
  execute,
  isLoading = false,
}: DepositFeeBreakdownProps) => {
  return (
    <Accordion type="single" collapsible className="w-full">
      <AccordionItem value="deposit-fee-breakdown">
        <div className="w-full flex items-start justify-between">
          <p className="font-semibold text-base">Total Fees</p>

          <div className="flex flex-col items-end justify-end-safe gap-y-1">
            {isLoading ? (
              <Skeleton className="h-5 w-24" />
            ) : (
              <p className="font-semibold text-base min-w-max">{total}</p>
            )}
            <AccordionTrigger
              containerClassName="w-fit"
              className="p-0 items-center gap-x-1"
              hideChevron={false}
            >
              <p className="text-sm font-medium">View Breakdown</p>
            </AccordionTrigger>
          </div>
        </div>
        <AccordionContent>
          <div className="w-full flex flex-col items-center justify-between gap-y-3 bg-muted px-4 py-2 rounded-lg mt-2">
            <div className="flex items-center w-full justify-between">
              <p className="text-sm font-semibold">Transaction Fee</p>
              {isLoading ? (
                <Skeleton className="h-4 w-20" />
              ) : (
                <p className="text-sm font-semibold">{bridge}</p>
              )}
            </div>
            <div className="flex items-center w-full justify-between">
              <p className="text-sm font-semibold">Deposit Fee</p>
              {isLoading ? (
                <Skeleton className="h-4 w-20" />
              ) : (
                <p className="text-sm font-semibold">{execute}</p>
              )}
            </div>
          </div>
        </AccordionContent>
      </AccordionItem>
    </Accordion>
  );
};

export default DepositFeeBreakdown;

import { Check, LoaderPinwheel, Circle, Link as LinkIcon } from "lucide-react";
import React, { FC, useMemo } from "react";
import {
  type BridgeStepType,
  type SwapStepType,
} from "@avail-project/nexus-core";

type ProgressStep = BridgeStepType | SwapStepType;

interface BridgeExecuteProgressProps {
  timer: number;
  steps: Array<{ id: number; completed: boolean; step: ProgressStep }>;
  intentUrl?: string;
  executeUrl?: string;
}

type DisplayStep = { id: string; label: string; completed: boolean };

const StepList: FC<{ steps: DisplayStep[]; currentIndex: number }> = ({
  steps,
  currentIndex,
}) => {
  return (
    <div className="w-full mt-6 space-y-6">
      {steps.map((s, idx) => {
        const isCompleted = !!s.completed;
        const isCurrent = currentIndex === -1 ? false : idx === currentIndex;

        let rightIcon = <Circle className="size-5 text-muted-foreground" />;
        if (isCompleted) {
          rightIcon = <Check className="size-5 text-green-600" />;
        } else if (isCurrent) {
          rightIcon = (
            <LoaderPinwheel className="size-5 animate-spin text-muted-foreground" />
          );
        }

        return (
          <div key={s.id} className="flex items-center justify-between w-full">
            <div className="flex items-center gap-x-3">
              <span className="text-base font-semibold">{s.label}</span>
            </div>
            {rightIcon}
          </div>
        );
      })}
    </div>
  );
};

const BridgeExecuteProgress: FC<BridgeExecuteProgressProps> = ({
  timer,
  steps,
  intentUrl,
  executeUrl,
}) => {
  const totalSteps = Array.isArray(steps) ? steps.length : 0;
  const completedSteps = Array.isArray(steps)
    ? steps.reduce((acc, s) => acc + (s?.completed ? 1 : 0), 0)
    : 0;
  const percent = totalSteps > 0 ? completedSteps / totalSteps : 0;
  const allCompleted = percent >= 1;

  // Custom milestone copy for deposit flow
  const milestones = useMemo(
    () => [
      "Intent verified",
      "Collected on sources",
      "Filled on destination",
      "Depositing",
    ],
    []
  );
  const thresholds = useMemo(
    () => milestones.map((_, idx) => (idx + 1) / milestones.length),
    [milestones]
  );
  const displaySteps: DisplayStep[] = milestones.map((label, idx) => ({
    id: `M${idx}`,
    label,
    completed: percent >= thresholds[idx],
  }));
  const currentIndex = displaySteps.findIndex((st) => !st.completed);

  return (
    <div className="w-full flex flex-col items-center">
      <div className="flex flex-col items-center gap-y-3">
        {allCompleted ? (
          <Check className="size-6 text-green-600" />
        ) : (
          <LoaderPinwheel className="size-6 animate-spin" />
        )}
        <p>{allCompleted ? "Deposit Completed" : "Processing your deposit"}</p>
        <div className="flex items-center justify-center w-full">
          <span className="text-2xl font-semibold">{Math.floor(timer)}</span>
          <span className="text-base font-semibold">.</span>
          <span className="text-base font-semibold">
            {String(Math.floor((timer % 1) * 1000)).padStart(3, "0")}s
          </span>
        </div>
      </div>

      <StepList steps={displaySteps} currentIndex={currentIndex} />

      <div className="mt-6 w-full flex items-center justify-center gap-x-4">
        {intentUrl && (
          <a
            href={intentUrl}
            target="_blank"
            rel="noreferrer"
            className="underline text-sm inline-flex items-center gap-x-1"
          >
            <LinkIcon className="size-3" /> View Intent
          </a>
        )}
        {executeUrl && (
          <a
            href={executeUrl}
            target="_blank"
            rel="noreferrer"
            className="underline text-sm inline-flex items-center gap-x-1"
          >
            <LinkIcon className="size-3" /> View Execute Tx
          </a>
        )}
      </div>
    </div>
  );
};

export default BridgeExecuteProgress;

"use client";

import {
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
  type UserAsset,
  NexusSDK,
  type OnIntentHookData,
  type OnAllowanceHookData,
  type ExecuteParams,
  type BridgeAndExecuteParams,
  type BridgeAndExecuteResult,
  type BridgeAndExecuteSimulationResult,
  TOKEN_METADATA,
  parseUnits,
  NEXUS_EVENTS,
  type BridgeStepType,
} from "@avail-project/nexus-core";
import { useEffect, useMemo, useRef, useState } from "react";
import { useNexus } from "../../nexus/NexusProvider";
import { type Address } from "viem";

interface DepositInputs {
  chain: SUPPORTED_CHAINS_IDS;
  amount?: string;
  selectedSources: number[];
}

type ExecuteConfig = Omit<ExecuteParams, "toChainId">;

interface UseDepositProps {
  token: SUPPORTED_TOKENS;
  chain: SUPPORTED_CHAINS_IDS;
  nexusSDK: NexusSDK | null;
  intent: OnIntentHookData | null;
  setIntent: React.Dispatch<React.SetStateAction<OnIntentHookData | null>>;
  allowance: OnAllowanceHookData | null;
  setAllowance: React.Dispatch<
    React.SetStateAction<OnAllowanceHookData | null>
  >;
  unifiedBalance: UserAsset[] | null;
  chainOptions?: { id: number; name: string; logo: string }[];
  address: Address;
  executeBuilder?: (
    token: SUPPORTED_TOKENS,
    amount: string,
    chainId: SUPPORTED_CHAINS_IDS,
    userAddress: `0x${string}`
  ) => ExecuteConfig;
  executeConfig?: ExecuteConfig;
}

const useDeposit = ({
  token,
  chain,
  nexusSDK,
  intent,
  setIntent,
  allowance,
  setAllowance,
  unifiedBalance,
  chainOptions,
  address,
  executeBuilder,
  executeConfig,
}: UseDepositProps) => {
  const { fetchUnifiedBalance, handleNexusError } = useNexus();
  const allSourceIds = useMemo(
    () => chainOptions?.map((c) => c.id) ?? [],
    [chainOptions]
  );

  const [inputs, setInputs] = useState<DepositInputs>({
    chain,
    amount: undefined,
    selectedSources: allSourceIds,
  });

  useEffect(() => {
    setInputs((prev) => ({ ...prev, selectedSources: allSourceIds }));
  }, [allSourceIds]);

  const [timer, setTimer] = useState(0);
  const [startTxn, setStartTxn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [refreshing, setRefreshing] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [txError, setTxError] = useState<string | null>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const refreshIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const [simulation, setSimulation] =
    useState<BridgeAndExecuteSimulationResult | null>(null);
  const [simulating, setSimulating] = useState(false);
  const [autoAllow, setAutoAllow] = useState(false);
  const [lastResult, setLastResult] = useState<BridgeAndExecuteResult | null>(
    null
  );
  const [steps, setSteps] = useState<
    Array<{ id: number; completed: boolean; step: BridgeStepType }>
  >([]);

  const simulationRequestIdRef = useRef(0);
  const activeSimulationIdRef = useRef<number | null>(null);

  useMemo(() => {
    const hasChain = inputs?.chain !== undefined && inputs?.chain !== null;
    const hasAmount = Boolean(inputs?.amount) && Number(inputs?.amount) > 0;
    return hasChain && hasAmount;
  }, [inputs]);

  const filteredUnifiedBalance = useMemo(() => {
    return unifiedBalance?.find((bal) => bal?.symbol === token);
  }, [unifiedBalance, token]);

  const handleTransaction = async () => {
    if (!inputs?.amount || !inputs?.chain) return;
    setLoading(true);
    setTxError(null);
    try {
      if (!nexusSDK) throw new Error("Nexus SDK not initialized");
      const decimals = TOKEN_METADATA[token].decimals;
      const amountBigInt = parseUnits(inputs.amount, decimals);
      const executeParams: ExecuteConfig | undefined = executeBuilder
        ? executeBuilder(token, inputs.amount, inputs.chain, address)
        : executeConfig;
      const params: BridgeAndExecuteParams = {
        token,
        amount: amountBigInt,
        toChainId: inputs.chain,
        sourceChains: inputs.selectedSources?.length
          ? inputs.selectedSources
          : allSourceIds,
        execute: executeParams as ExecuteConfig,
        waitForReceipt: true,
      };

      const result: BridgeAndExecuteResult = await nexusSDK.bridgeAndExecute(
        params,
        {
          onEvent: (event) => {
            if (event.name === NEXUS_EVENTS.STEPS_LIST) {
              const list = Array.isArray(event.args) ? event.args : [];
              setSteps((prev) => {
                const completedTypes = new Set(
                  prev
                    .filter((s) => s.completed)
                    .map((s) => s.step?.typeID ?? "")
                );
                return list.map((step, index) => ({
                  id: index,
                  completed: completedTypes.has(step?.typeID ?? ""),
                  step,
                }));
              });
            }
            if (event.name === NEXUS_EVENTS.STEP_COMPLETE) {
              const step = event.args;
              setSteps((prev) =>
                prev.map((s) =>
                  s?.step?.typeID === step?.typeID
                    ? { ...s, completed: true }
                    : s
                )
              );
            }
          },
        }
      );

      if (!result) {
        setTxError("Transaction rejected by user");
        setIsDialogOpen(false);
        resetState();
        return;
      }
      setLastResult(result);
      await onSuccess();
    } catch (error) {
      const { message } = handleNexusError(error);
      setTxError(message);
      setIsDialogOpen(false);
    } finally {
      resetState();
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }
  };

  const simulate = async (overrideAmount?: string) => {
    if (!nexusSDK) return;
    const amountToUse = overrideAmount ?? inputs?.amount;
    if (!amountToUse || !inputs?.chain) {
      activeSimulationIdRef.current = null;
      setSimulation(null);
      return;
    }
    const requestId = ++simulationRequestIdRef.current;
    activeSimulationIdRef.current = requestId;
    setSimulating(true);
    try {
      const decimals = TOKEN_METADATA[token].decimals;
      const amountBigInt = parseUnits(amountToUse, decimals);
      const executeParams: ExecuteConfig | undefined = executeBuilder
        ? executeBuilder(token, amountToUse, inputs.chain, address)
        : executeConfig;
      const params: BridgeAndExecuteParams = {
        token,
        amount: amountBigInt,
        toChainId: inputs.chain,
        sourceChains: inputs.selectedSources?.length
          ? inputs.selectedSources
          : allSourceIds,
        execute: executeParams as ExecuteConfig,
        waitForReceipt: false,
      };
      const sim = await nexusSDK.simulateBridgeAndExecute(params);
      console.log("sim", sim);
      if (activeSimulationIdRef.current !== requestId) {
        return;
      }
      if (sim) {
        setTxError(null);
        setSimulation(sim);
      } else {
        setSimulation(null);
        setTxError("Simulation failed");
      }
    } catch (error) {
      if (activeSimulationIdRef.current !== requestId) {
        return;
      }
      setSimulation(null);
      const { message } = handleNexusError(error);
      setTxError(message);
    } finally {
      if (activeSimulationIdRef.current === requestId) {
        setSimulating(false);
      }
    }
  };

  const refreshSimulation = async () => {
    if (simulating || refreshing) return;
    if (!simulation?.bridgeSimulation?.intent) return;
    if (!inputs?.amount) return;
    setRefreshing(true);
    await simulate(inputs?.amount);
    setRefreshing(false);
  };

  const onSuccess = async () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
    await fetchUnifiedBalance();
  };

  const resetState = () => {
    setAllowance(null);
    setInputs({
      chain,
      amount: undefined,
      selectedSources: allSourceIds,
    });
    setLoading(false);
    setStartTxn(false);
    setRefreshing(false);
    setSimulation(null);
    setIntent(null);
    activeSimulationIdRef.current = null;
    setSimulating(false);
    if (refreshIntervalRef.current) {
      clearInterval(refreshIntervalRef.current);
      refreshIntervalRef.current = null;
    }
  };

  const reset = () => {
    intent?.deny();
    resetState();
  };

  const startTransaction = () => {
    setTimer(0);
    setStartTxn(true);
    setIsDialogOpen(true);
    setTxError(null);
    setAutoAllow(true);
    void handleTransaction();
    if (refreshIntervalRef.current) {
      clearInterval(refreshIntervalRef.current);
      refreshIntervalRef.current = null;
    }
  };

  useEffect(() => {
    if (autoAllow && intent) {
      intent.allow();
      setAutoAllow(false);
    }
  }, [autoAllow, intent]);

  useEffect(() => {
    if (startTxn) {
      timerRef.current = setInterval(() => setTimer((prev) => prev + 0.1), 100);
    }
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    };
  }, [startTxn]);

  useEffect(() => {
    if (!isDialogOpen) {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setStartTxn(false);
      setLastResult(null);
    }
  }, [isDialogOpen]);

  useEffect(() => {
    if (simulation?.bridgeSimulation?.intent && !isDialogOpen) {
      refreshIntervalRef.current ??= setInterval(refreshSimulation, 15000);
    } else if (refreshIntervalRef.current) {
      clearInterval(refreshIntervalRef.current);
      refreshIntervalRef.current = null;
    }
    return () => {
      if (refreshIntervalRef.current) {
        clearInterval(refreshIntervalRef.current);
        refreshIntervalRef.current = null;
      }
    };
  }, [simulation, isDialogOpen]);

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
    setStartTxn(false);
  };

  return {
    inputs,
    setInputs,
    loading,
    simulating,
    refreshing,
    isDialogOpen,
    setIsDialogOpen,
    txError,
    setTxError,
    timer,
    filteredUnifiedBalance,
    simulation,
    lastResult,
    steps,
    handleTransaction,
    startTransaction,
    reset,
    stopTimer,
    simulate,
    clearSimulation: () => reset(),
    cancelSimulation: () => {
      activeSimulationIdRef.current = null;
      setSimulating(false);
      setRefreshing(false);
      setSimulation(null);
      if (refreshIntervalRef.current) {
        clearInterval(refreshIntervalRef.current);
        refreshIntervalRef.current = null;
      }
    },
  };
};

export default useDeposit;

