"use client";
import React from "react";
import { Card, CardContent } from "../ui/card";
import ChainSelect from "./components/chain-select";
import TokenSelect from "./components/token-select";
import { Button } from "../ui/button";
import { LoaderPinwheel } from "lucide-react";
import { useNexus } from "../nexus/NexusProvider";
import ReceipientAddress from "./components/receipient-address";
import AmountInput from "./components/amount-input";
import FeeBreakdown from "./components/fee-breakdown";
import { FastBridgeProps } from "./types";
import { Dialog, DialogContent, DialogTrigger } from "../ui/dialog";
import TransactionProgress from "./components/transaction-progress";
import AllowanceModal from "./components/allowance-modal";
import useListenTransaction from "./hooks/useListenTransaction";
import useBridge from "./hooks/useBridge";
import SourceBreakdown from "./components/source-breakdown";
import { SUPPORTED_TOKENS } from "@avail-project/nexus-core";

const FastBridge: React.FC<FastBridgeProps> = ({ connectedAddress }) => {
  const {
    nexusSDK,
    intent,
    setIntent,
    unifiedBalance,
    allowance,
    setAllowance,
    network,
  } = useNexus();

  const {
    inputs,
    setInputs,
    timer,
    loading,
    refreshing,
    isDialogOpen,
    txError,
    handleTransaction,
    reset,
    filteredUnifiedBalance,
    startTransaction,
    setIsDialogOpen,
    setTxError,
  } = useBridge({
    network: network ?? "mainnet",
    connectedAddress,
    nexusSDK,
    intent,
    setIntent,
    unifiedBalance,
  });

  const { processing, latestCompletedIndex, explorerUrl } =
    useListenTransaction(nexusSDK);

  return (
    <Card className="w-full max-w-xl">
      <CardContent className="flex flex-col gap-y-4 w-full">
        <ChainSelect
          selectedChain={inputs?.chain}
          handleSelect={(chain) =>
            setInputs({
              ...inputs,
              chain,
            })
          }
          label="To"
        />
        <TokenSelect
          selectedChain={inputs?.chain}
          selectedToken={inputs?.token}
          handleTokenSelect={(token) => setInputs({ ...inputs, token })}
        />
        <AmountInput
          amount={inputs?.amount}
          onChange={(amount) => setInputs({ ...inputs, amount })}
          unifiedBalance={filteredUnifiedBalance}
        />
        <ReceipientAddress
          address={inputs?.recipient}
          onChange={(address) =>
            setInputs({ ...inputs, recipient: address as `0x${string}` })
          }
        />
        {intent?.intent && (
          <>
            <SourceBreakdown
              intent={intent?.intent}
              tokenSymbol={filteredUnifiedBalance?.symbol as SUPPORTED_TOKENS}
            />
            <div className="w-full flex items-start justify-between gap-x-4">
              <p className="text-base font-semibold">You receive</p>
              <div className="flex flex-col gap-y-1 min-w-fit">
                <p className="text-base font-semibold text-right">
                  {intent?.intent?.destination?.amount}{" "}
                  {filteredUnifiedBalance?.symbol}
                </p>
                <p className="text-sm font-medium text-right">
                  on {intent?.intent?.destination?.chainName}
                </p>
              </div>
            </div>
            <FeeBreakdown intent={intent?.intent} />
          </>
        )}

        {!intent && (
          <Button
            onClick={handleTransaction}
            disabled={
              !inputs?.amount ||
              !inputs?.recipient ||
              !inputs?.chain ||
              !inputs?.token ||
              loading
            }
          >
            {loading ? (
              <LoaderPinwheel className="animate-spin size-5" />
            ) : (
              "Bridge"
            )}
          </Button>
        )}
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          {intent && (
            <div className="w-full flex items-center gap-x-2 justify-between">
              <Button variant={"destructive"} onClick={reset} className="w-1/2">
                Deny
              </Button>
              <DialogTrigger asChild>
                <Button
                  onClick={startTransaction}
                  className="w-1/2"
                  disabled={refreshing}
                >
                  {refreshing ? "Refreshing..." : "Accept"}
                </Button>
              </DialogTrigger>
            </div>
          )}
          <DialogContent>
            <TransactionProgress
              timer={timer}
              steps={processing}
              latestCompletedIndex={latestCompletedIndex}
              viewIntentUrl={explorerUrl}
              operationType={"bridge"}
            />
          </DialogContent>
        </Dialog>
        {allowance && (
          <AllowanceModal
            allowanceModal={allowance}
            setAllowanceModal={setAllowance}
            callback={startTransaction}
          />
        )}

        {txError && (
          <div className="rounded-md border border-red-300 bg-red-50 px-3 py-2 text-sm text-red-700 flex items-start justify-between gap-x-3">
            <span className="flex-1">{txError}</span>
            <Button
              type="button"
              size={"icon"}
              variant={"ghost"}
              onClick={() => setTxError(null)}
              className="text-red-700/80 hover:text-red-900 focus:outline-none"
              aria-label="Dismiss error"
            >
              Ã—
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default FastBridge;

"use client";
import * as React from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "../../ui/dialog";
import { Button } from "../../ui/button";
import { Input } from "../../ui/input";
import { Label } from "../../ui/label";
import {
  AllowanceHookSource,
  CHAIN_METADATA,
  OnAllowanceHookData,
} from "@avail-project/nexus-core";
import { useNexus } from "../../nexus/NexusProvider";

interface AllowanceModalProps {
  allowanceModal: OnAllowanceHookData | null;
  setAllowanceModal: React.Dispatch<
    React.SetStateAction<OnAllowanceHookData | null>
  >;
  callback?: () => void;
}

const AllowanceModal: React.FC<AllowanceModalProps> = ({
  allowanceModal,
  setAllowanceModal,
  callback,
}) => {
  const { nexusSDK } = useNexus();
  const [selectedOption, setSelectedOption] = React.useState<string[]>([]);
  const [customValues, setCustomValues] = React.useState<string[]>([]);

  React.useEffect(() => {
    if (allowanceModal) {
      setSelectedOption(
        Array.from({ length: allowanceModal.sources.length }, () => "min"),
      );
      setCustomValues(
        Array.from({ length: allowanceModal.sources.length }, () => ""),
      );
    }
  }, [allowanceModal]);

  if (!allowanceModal) return null;

  const { sources, allow, deny } = allowanceModal;

  const onClose = () => {
    deny();
    setAllowanceModal(null);
  };

  const onApprove = () => {
    const processed = sources.map((_, i) => {
      const opt = selectedOption[i];
      if (opt === "min" || opt === "max") return opt;
      const v = customValues[i];
      if (v && !isNaN(Number(v))) return v;
      return "min";
    });
    allow(processed);
    setAllowanceModal(null);
    callback?.();
  };

  return (
    <Dialog
      open={!!allowanceModal}
      onOpenChange={(isOpen) => !isOpen && onClose()}
    >
      <DialogContent className="w-full sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Set Token Allowances</DialogTitle>
          <DialogDescription>
            Select minimum, maximum, or a custom allowance for each required
            token.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-2 max-h-[60vh] overflow-y-auto">
          {sources?.map((source: AllowanceHookSource, index: number) => (
            <div
              key={`${source.token.symbol}-${index}`}
              className="p-3 border rounded-lg"
            >
              <div className="flex items-center justify-between gap-x-2">
                <div className="flex items-center gap-x-2">
                  <img
                    src={CHAIN_METADATA[source.chain.id]?.logo}
                    alt={source.chain.name}
                    width={20}
                    height={20}
                    className="rounded-full"
                  />
                  <p className="font-semibold">
                    {source.token.symbol} on {source.chain.name}
                  </p>
                </div>
                <div className="text-sm">
                  <span className="mr-1 text-muted-foreground">Current:</span>
                  <span className="font-semibold">
                    {nexusSDK?.utils.formatBalance(
                      source.allowance.current,
                      source.token.decimals,
                    )}
                  </span>
                </div>
              </div>

              <div className="mt-3 grid grid-cols-1 gap-2">
                <Label className="flex items-center gap-x-2">
                  <Input
                    type="radio"
                    name={`allowance-${index}`}
                    value="min"
                    checked={selectedOption[index] === "min"}
                    onChange={() => {
                      const next = [...selectedOption];
                      next[index] = "min";
                      setSelectedOption(next);
                    }}
                    className="w-10"
                  />
                  <span>
                    Minimum (
                    {nexusSDK?.utils.formatBalance(
                      source.allowance.minimum,
                      source.token.decimals,
                    )}
                    )
                  </span>
                </Label>

                <Label className="flex items-center gap-x-2">
                  <Input
                    type="radio"
                    name={`allowance-${index}`}
                    value="max"
                    checked={selectedOption[index] === "max"}
                    onChange={() => {
                      const next = [...selectedOption];
                      next[index] = "max";
                      setSelectedOption(next);
                    }}
                    className="w-10"
                  />
                  <span>Maximum (Unlimited)</span>
                </Label>

                <div className="flex items-center gap-x-2">
                  <Input
                    type="radio"
                    name={`allowance-${index}`}
                    value="custom"
                    checked={selectedOption[index] === "custom"}
                    onChange={() => {
                      const next = [...selectedOption];
                      next[index] = "custom";
                      setSelectedOption(next);
                    }}
                    className="w-10"
                  />
                  <Label className="mr-2">Custom Amount</Label>
                  <Input
                    type="number"
                    step="any"
                    min="0"
                    placeholder="Enter custom amount"
                    value={
                      selectedOption[index] === "custom"
                        ? customValues[index]
                        : ""
                    }
                    onChange={(e) => {
                      const next = [...customValues];
                      next[index] = e.target.value;
                      setCustomValues(next);
                    }}
                    className="h-8 w-40"
                  />
                </div>
              </div>
            </div>
          ))}
        </div>

        <DialogFooter className="gap-2 sm:justify-end mt-2">
          <Button
            variant="destructive"
            onClick={onClose}
            className="font-semibold"
          >
            Deny
          </Button>
          <Button onClick={onApprove} className="font-semibold">
            Approve Selected
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

export default AllowanceModal;

import * as React from "react";
import { Input } from "../../ui/input";
import { Button } from "../../ui/button";
import { AmountInputProps } from "../types";

const AmountInput: React.FC<AmountInputProps> = ({
  amount,
  onChange,
  unifiedBalance,
}) => {
  const onMaxClick = () => {
    if (!unifiedBalance) return;
    const maxBalAvailable = unifiedBalance?.balance;
    onChange(maxBalAvailable);
  };
  return (
    <div className="w-full flex border border-border rounded-lg gap-y-2">
      <Input
        value={amount ?? ""}
        placeholder="Enter Amount"
        onChange={(e) => onChange(e.target.value)}
        className="w-full border-none focus-visible:ring-0 focus-visible:ring-offset-0"
      />
      <div className="flex items-center justify-end-safe gap-x-4 w-fit px-2 border-l border-border">
        <div className="flex items-center gap-x-3 min-w-max">
          {unifiedBalance && (
            <p className="text-base font-semibold">
              {unifiedBalance?.balance} {unifiedBalance?.symbol}
            </p>
          )}
          <Button
            size={"sm"}
            variant={"ghost"}
            onClick={onMaxClick}
            className="px-0"
          >
            Max
          </Button>
        </div>
      </div>
    </div>
  );
};

export default AmountInput;

import { ReadableIntent, SUPPORTED_TOKENS } from "@avail-project/nexus-core";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";

const SourceBreakdown = ({
  intent,
  tokenSymbol,
}: {
  intent: ReadableIntent;
  tokenSymbol: SUPPORTED_TOKENS;
}) => {
  return (
    <Accordion type="single" collapsible>
      <AccordionItem value="sources">
        <div className="flex items-start justify-between gap-x-4 w-full">
          {intent?.sources && (
            <>
              <div className="flex flex-col items-start gap-y-1 min-w-fit">
                <p className="text-base font-semibold">You Spend</p>
                <p className="text-sm font-medium">
                  {intent?.sources?.length > 1
                    ? `${intent?.sources?.length} assests on ${intent?.sources?.length} chains`
                    : `${intent?.sources?.length} asset on ${intent?.sources?.length} chain`}
                </p>
              </div>

              <div className="flex flex-col items-end gap-y-1 min-w-fit">
                <p className="text-base font-semibold">
                  {intent?.sourcesTotal} {tokenSymbol}
                </p>
                <AccordionTrigger
                  containerClassName="w-fit"
                  className="py-0 items-center gap-x-1"
                  hideChevron={false}
                >
                  <p className="text-sm font-medium">View Sources</p>
                </AccordionTrigger>
              </div>
            </>
          )}
        </div>
        {intent?.sources && (
          <AccordionContent className="my-4 bg-muted pb-0 px-4 py-2 rounded-lg w-full">
            <div className="flex flex-col items-center gap-y-3">
              {intent?.sources?.map((source) => (
                <div
                  key={source.chainID}
                  className="flex items-center justify-between w-full gap-x-2"
                >
                  <div className="flex items-center gap-x-2">
                    <img
                      src={source?.chainLogo}
                      alt={source?.chainName}
                      width={20}
                      height={20}
                      className="rounded-full"
                    />
                    <p className="text-sm font-semibold">{source.chainName}</p>
                  </div>

                  <p className="text-sm font-semibold">
                    {source.amount} {tokenSymbol}
                  </p>
                </div>
              ))}
            </div>
          </AccordionContent>
        )}
      </AccordionItem>
    </Accordion>
  );
};

export default SourceBreakdown;

import * as React from "react";
import { Label } from "../../ui/label";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../../ui/select";
import { SUPPORTED_CHAINS_IDS } from "@avail-project/nexus-core";
import { cn } from "@/lib/utils";
import { ChainSelectProps } from "../types";
import { useNexus } from "../../nexus/NexusProvider";

const ChainSelect: React.FC<ChainSelectProps> = ({
  selectedChain,
  disabled,
  hidden = false,
  className,
  label,
  handleSelect,
}) => {
  const { supportedChainsAndTokens } = useNexus();

  const selectedChainData = React.useMemo(() => {
    if (!supportedChainsAndTokens) return null;
    return supportedChainsAndTokens.find((c) => c.id === selectedChain);
  }, [selectedChain, supportedChainsAndTokens]);

  if (hidden) return null;
  return (
    <Select
      value={selectedChain?.toString() ?? ""}
      onValueChange={(value) => {
        if (!disabled) {
          handleSelect(parseInt(value) as SUPPORTED_CHAINS_IDS);
        }
      }}
    >
      <div className="flex flex-col items-start gap-y-1 w-full">
        {label && <Label className="text-sm font-semibold">{label}</Label>}
        <SelectTrigger disabled={disabled} className=" w-full">
          <SelectValue>
            {selectedChainData && (
              <div
                className={cn("flex items-center gap-x-2 w-full", className)}
              >
                <img
                  src={selectedChainData?.logo}
                  alt={selectedChainData?.name}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                <p className="text-primary test-sm">
                  {selectedChainData?.name}
                </p>
              </div>
            )}
          </SelectValue>
        </SelectTrigger>
      </div>

      <SelectContent>
        <SelectGroup>
          {supportedChainsAndTokens &&
            supportedChainsAndTokens.map((chain) => {
              return (
                <SelectItem key={chain.id} value={String(chain.id)}>
                  <div className="flex items-center gap-x-2 my-1">
                    <img
                      src={chain.logo}
                      alt={chain?.name}
                      width={24}
                      height={24}
                      className="rounded-full"
                    />
                    <p className="text-primary test-sm">{chain.name}</p>
                  </div>
                </SelectItem>
              );
            })}
        </SelectGroup>
      </SelectContent>
    </Select>
  );
};

export default ChainSelect;

import * as React from "react";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";
import { FeeBreakdownProps } from "../types";

const FeeBreakdown: React.FC<FeeBreakdownProps> = ({ intent }) => {
  return (
    <Accordion type="single" collapsible>
      <AccordionItem value="breakdown">
        <div className="w-full flex items-start justify-between">
          <p className="font-semibold text-base">Total fees</p>

          <div className="flex flex-col items-end justify-end-safe gap-y-1">
            <p className="font-semibold text-base min-w-max">
              {intent.fees?.total} {intent.token?.symbol}
            </p>
            <AccordionTrigger
              containerClassName="w-fit"
              className="p-0 items-center gap-x-1"
              hideChevron={false}
            >
              <p className="text-sm font-medium">View Breakup</p>
            </AccordionTrigger>
          </div>
        </div>
        <AccordionContent>
          <div className="w-full flex flex-col items-center justify-between gap-y-3 bg-muted px-4 py-2 rounded-lg mt-2">
            <div className="flex items-center w-full justify-between">
              <p className="text-sm font-semibold">Fast Bridge Gas Fee</p>
              <p className="text-sm font-semibold">
                {intent?.fees?.caGas} {intent?.token?.symbol}
              </p>
            </div>
            <div className="flex items-center w-full justify-between">
              <p className="text-sm font-semibold">Solver Fees</p>
              <p className="text-sm font-semibold">
                {intent?.fees?.solver} {intent?.token?.symbol}
              </p>
            </div>
            <div className="flex items-center w-full justify-between">
              <p className="text-sm font-semibold">Protocol Fees</p>
              <p className="text-sm font-semibold">
                {intent?.fees?.protocol} {intent?.token?.symbol}
              </p>
            </div>
          </div>
        </AccordionContent>
      </AccordionItem>
    </Accordion>
  );
};

export default FeeBreakdown;

"use client";
import * as React from "react";
import { Input } from "../../ui/input";
import { Check, Edit } from "lucide-react";
import { Button } from "../../ui/button";
import { useNexus } from "../../nexus/NexusProvider";
import { ReceipientAddressProps } from "../types";

const ReceipientAddress: React.FC<ReceipientAddressProps> = ({
  address,
  onChange,
}) => {
  const { nexusSDK } = useNexus();
  const [isEditing, setIsEditing] = React.useState(false);
  return (
    <div className="w-full">
      {isEditing ? (
        <div className="flex items-center w-full justify-between gap-x-4">
          <Input
            value={address}
            placeholder="Enter Recipient Address"
            onChange={(e) => onChange(e.target.value)}
            className="w-full"
          />
          <Button
            variant={"ghost"}
            size={"icon"}
            onClick={() => {
              setIsEditing(false);
            }}
          >
            <Check className="size-5" />
          </Button>
        </div>
      ) : (
        <div className="flex items-center w-full justify-between">
          <p className="font-semibold">Recipient Address</p>
          <div className="flex items-center gap-x-3 ">
            <p className="font-semibold">
              {nexusSDK?.utils?.truncateAddress(address ?? "", 6, 6)}
            </p>

            <Button
              variant={"ghost"}
              size={"icon"}
              onClick={() => {
                setIsEditing(true);
              }}
              className="px-0 size-6"
            >
              <Edit className="size-6" />
            </Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReceipientAddress;

import { SUPPORTED_TOKENS } from "@avail-project/nexus-core";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../../ui/select";
import { Label } from "../../ui/label";
import { useNexus } from "../../nexus/NexusProvider";
import { useMemo } from "react";
import { TokenSelectProps } from "../types";

const TokenSelect = ({
  selectedToken,
  selectedChain,
  handleTokenSelect,
  isTestnet = false,
  disabled = false,
  label,
}: TokenSelectProps) => {
  const { supportedChainsAndTokens } = useNexus();
  const tokenData = useMemo(() => {
    return supportedChainsAndTokens?.filter(
      (chain) => chain.id === selectedChain,
    )[0]?.tokens;
  }, [selectedChain, supportedChainsAndTokens]);

  const selectedTokenData = tokenData?.find((token) => {
    return token.symbol === selectedToken;
  });

  return (
    <Select
      value={selectedToken}
      onValueChange={(value) =>
        !disabled && handleTokenSelect(value as SUPPORTED_TOKENS)
      }
    >
      <div className="flex flex-col items-start gap-y-1">
        {label && <Label className="text-sm font-semibold">{label}</Label>}
        <SelectTrigger disabled={disabled} className="w-full">
          <SelectValue placeholder="Select a token" className="w-full">
            {selectedChain && selectedTokenData && (
              <div className="flex items-center gap-x-2 w-full">
                <img
                  src={selectedTokenData?.logo}
                  alt={selectedTokenData?.symbol}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                {selectedToken}
              </div>
            )}
          </SelectValue>
        </SelectTrigger>
      </div>

      <SelectContent>
        <SelectGroup>
          {tokenData?.map((token) => (
            <SelectItem key={token.symbol} value={token.symbol}>
              <div className="flex items-center gap-x-2 my-1">
                <img
                  src={token.logo}
                  alt={token.symbol}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                <div className="flex flex-col">
                  <span>
                    {isTestnet ? `${token.symbol} (Testnet)` : token.symbol}
                  </span>
                </div>
              </div>
            </SelectItem>
          ))}
        </SelectGroup>
      </SelectContent>
    </Select>
  );
};

export default TokenSelect;

import { Check, Circle, LoaderPinwheel } from "lucide-react";
import * as React from "react";
import { ProgressStep } from "@avail-project/nexus-core";

interface TransactionProgressProps {
  timer: number;
  steps: Array<{ id: number; completed: boolean; step: ProgressStep }>;
  latestCompletedIndex: number;
  viewIntentUrl?: string;
  operationType?: string;
}

export const getOperationText = (type: string) => {
  switch (type) {
    case "bridge":
      return "Transaction";
    case "transfer":
      return "Transferring";
    case "bridgeAndExecute":
      return "Bridge & Execute";
    case "swap":
      return "Swapping";
    default:
      return "Processing";
  }
};

export const getStatusText = (type: string, operationType: string) => {
  const opText = getOperationText(operationType);

  switch (type) {
    case "INTENT_ACCEPTED":
      return "Intent Accepted";
    case "INTENT_HASH_SIGNED":
      return "Signing Transaction";
    case "INTENT_SUBMITTED":
      return "Submitting Transaction";
    case "INTENT_COLLECTION":
      return "Collecting Confirmations";
    case "INTENT_COLLECTION_COMPLETE":
      return "Confirmations Complete";
    case "APPROVAL":
      return "Approving";
    case "TRANSACTION_SENT":
      return "Sending Transaction";
    case "RECEIPT_RECEIVED":
      return "Receipt Received";
    case "TRANSACTION_CONFIRMED":
    case "INTENT_FULFILLED":
      return `${opText} Complete`;
    default:
      return `Processing ${opText}`;
  }
};

const TransactionProgress: React.FC<TransactionProgressProps> = ({
  timer,
  steps,
  latestCompletedIndex,
  viewIntentUrl,
  operationType = "bridge",
}) => {
  const allCompleted = steps?.length > 0 && steps.every((s) => s.completed);
  const opText = getOperationText(operationType);
  const headerText = allCompleted
    ? `${opText} Completed`
    : `${opText} In Progress...`;
  const ctaText = allCompleted ? `View Explorer` : "View Intent";

  return (
    <div className="w-full flex flex-col items-center">
      <div className="flex flex-col items-center gap-y-3">
        {allCompleted ? (
          <Check className="size-6 text-green-600" />
        ) : (
          <LoaderPinwheel className="size-6 animate-spin" />
        )}
        <p>{headerText}</p>
        <div className="flex items-center justify-center w-full">
          <span className="text-2xl font-semibold font-nexus-primary text-nexus-black">
            {Math.floor(timer)}
          </span>
          <span className="text-base font-semibold font-nexus-primary text-nexus-black">
            .
          </span>
          <span className="text-base font-semibold font-nexus-primary text-nexus-muted-secondary">
            {String(Math.floor((timer % 1) * 1000)).padStart(3, "0")}s
          </span>
        </div>
      </div>

      <div className="w-full mt-6 space-y-6">
        {steps?.map((s, idx) => {
          const isCompleted = !!s.completed;
          const isCurrent = idx === latestCompletedIndex + 1;

          let rightIcon = <Circle className="size-5 text-muted-foreground" />;
          if (isCompleted) {
            rightIcon = <Check className="size-5 text-green-600" />;
          } else if (isCurrent) {
            rightIcon = (
              <LoaderPinwheel className="size-5 animate-spin text-muted-foreground" />
            );
          }

          return (
            <div
              key={s.id}
              className="flex items-center justify-between w-full"
            >
              <div className="flex items-center gap-x-3">
                <span className="text-base font-semibold">
                  {getStatusText(
                    s.step?.type as unknown as string,
                    operationType,
                  )}
                </span>
              </div>
              {rightIcon}
            </div>
          );
        })}
      </div>

      {viewIntentUrl && (
        <a
          href={viewIntentUrl}
          target="_blank"
          rel="noreferrer"
          className="mt-8 underline font-semibold"
        >
          {ctaText}
        </a>
      )}
    </div>
  );
};

