import {
  NEXUS_EVENTS,
  NexusSDK,
  ProgressStep,
} from "@avail-project/nexus-core";
import { useEffect, useMemo, useState } from "react";

type ProcessingState = Array<{
  id: number;
  completed: boolean;
  step: ProgressStep;
}>;

const useListenTransaction = (sdk: NexusSDK | null) => {
  const [processing, setProcessing] = useState<ProcessingState>([]);
  const [explorerUrl, setExplorerUrl] = useState("");
  const [latestCompleted, setLatestCompleted] = useState<ProgressStep | null>(
    null,
  );

  useEffect(() => {
    if (!sdk) return;

    const handleExpectedSteps = (expectedSteps: ProgressStep[]) => {
      const stepsArray = Array.isArray(expectedSteps) ? expectedSteps : [];
      const initialSteps: ProcessingState = stepsArray.map((step, index) => ({
        id: index,
        completed: false,
        step,
      }));
      setProcessing(initialSteps);
    };

    const handleStepComplete = (stepData: ProgressStep) => {
      setProcessing((prev) =>
        prev.map((s) =>
          s.step && s.step.typeID === stepData?.typeID
            ? { ...s, completed: true }
            : s,
        ),
      );

      setLatestCompleted(stepData);

      if (
        stepData?.typeID === "IS" &&
        stepData?.data &&
        "explorerURL" in stepData.data
      ) {
        setExplorerUrl(stepData?.data?.explorerURL);
      }
    };

    sdk?.nexusEvents?.on(NEXUS_EVENTS.EXPECTED_STEPS, handleExpectedSteps);
    sdk?.nexusEvents?.on(NEXUS_EVENTS.STEP_COMPLETE, handleStepComplete);
    return () => {
      sdk?.nexusEvents?.off(NEXUS_EVENTS.EXPECTED_STEPS, handleExpectedSteps);
      sdk?.nexusEvents?.off(NEXUS_EVENTS.STEP_COMPLETE, handleStepComplete);
    };
  }, [sdk]);

  const latestCompletedIndex = useMemo(() => {
    if (!processing?.length) return -1;
    for (let i = processing.length - 1; i >= 0; i -= 1) {
      if (processing[i]?.completed) return i;
    }
    return -1;
  }, [processing]);

  return { processing, latestCompleted, latestCompletedIndex, explorerUrl };
};

export default useListenTransaction;

import {
  NexusNetwork,
  NexusSDK,
  OnIntentHookData,
  SUPPORTED_CHAINS,
  UserAsset,
} from "@avail-project/nexus-core";
import { FastBridgeState } from "../types";
import { useEffect, useMemo, useRef, useState } from "react";
import { Address, isAddress } from "viem";

interface UseBridgeProps {
  network: NexusNetwork;
  connectedAddress: Address;
  nexusSDK: NexusSDK | null;
  intent: OnIntentHookData | null;
  setIntent: React.Dispatch<React.SetStateAction<OnIntentHookData | null>>;
  unifiedBalance: UserAsset[] | null;
}

const useBridge = ({
  network,
  connectedAddress,
  nexusSDK,
  intent,
  setIntent,
  unifiedBalance,
}: UseBridgeProps) => {
  const [inputs, setInputs] = useState<FastBridgeState>({
    chain:
      network === "testnet"
        ? SUPPORTED_CHAINS.SEPOLIA
        : SUPPORTED_CHAINS.ETHEREUM,
    token: "USDC",
    amount: undefined,
    recipient: connectedAddress,
  });
  const [timer, setTimer] = useState(0);
  const [startTxn, setStartTxn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [refreshing, setRefreshing] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [txError, setTxError] = useState<string | null>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  const areInputsValid = useMemo(() => {
    const hasToken = inputs?.token !== undefined && inputs?.token !== null;
    const hasChain = inputs?.chain !== undefined && inputs?.chain !== null;
    const hasAmount = Boolean(inputs?.amount) && Number(inputs?.amount) > 0;
    const hasValidRecipient =
      Boolean(inputs?.recipient) && isAddress(inputs?.recipient as string);
    return hasToken && hasChain && hasAmount && hasValidRecipient;
  }, [inputs]);

  const handleTransaction = async () => {
    if (
      !inputs?.amount ||
      !inputs?.recipient ||
      !inputs?.chain ||
      !inputs?.token
    ) {
      console.error("Missing required inputs");
      return;
    }
    setLoading(true);
    setTxError(null);
    try {
      if (inputs?.recipient !== connectedAddress) {
        // Transfer
        const transferTxn = await nexusSDK?.transfer({
          token: inputs?.token,
          amount: inputs?.amount,
          chainId: inputs?.chain,
          recipient: inputs?.recipient,
        });
        if (!transferTxn?.success) {
          throw new Error(transferTxn?.error || "Transaction rejected by user");
        }
        if (transferTxn?.success) {
          console.log("Transfer transaction successful");
          console.log(
            "Transfer transaction explorer",
            transferTxn?.explorerUrl
          );
        }
        return;
      }
      // Bridge
      const bridgeTxn = await nexusSDK?.bridge({
        token: inputs?.token,
        amount: inputs?.amount,
        chainId: inputs?.chain,
      });
      if (!bridgeTxn?.success) {
        throw new Error(bridgeTxn?.error || "Transaction rejected by user");
      }
      if (bridgeTxn?.success) {
        console.log("Bridge transaction successful");
        console.log("Bridge transaction explorer", bridgeTxn?.explorerUrl);
      }
    } catch (error) {
      console.error("Transaction failed:", (error as Error)?.message);
      if (!(error as Error)?.message?.includes("User rejected the request")) {
        setTxError((error as Error)?.message || "Transaction failed");
      }
      setIsDialogOpen(false);
    } finally {
      setLoading(false);
      setStartTxn(false);
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }
  };

  const filteredUnifiedBalance = useMemo(() => {
    return unifiedBalance?.filter((bal) => bal?.symbol === inputs?.token)[0];
  }, [unifiedBalance, inputs?.token]);

  const refreshIntent = async () => {
    setRefreshing(true);
    try {
      await intent?.refresh([]);
    } catch (error) {
      console.error("Transaction failed:", error);
    } finally {
      setRefreshing(false);
    }
  };

  const reset = () => {
    intent?.deny();
    setIntent(null);
    setInputs({
      chain:
        network === "testnet"
          ? SUPPORTED_CHAINS.SEPOLIA
          : SUPPORTED_CHAINS.ETHEREUM,
      token: "USDC",
      amount: undefined,
      recipient: connectedAddress,
    });
    setLoading(false);
    setStartTxn(false);
    setRefreshing(false);
  };

  const startTransaction = () => {
    setStartTxn(true);
    intent?.allow();
    setIsDialogOpen(true);
    setTxError(null);
  };

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (intent) {
      interval = setInterval(refreshIntent, 5000);
    }
    return () => {
      clearInterval(interval);
    };
  }, [intent]);

  useEffect(() => {
    if (startTxn) {
      timerRef.current = setInterval(() => {
        setTimer((prev) => prev + 0.1);
      }, 100);
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    };
  }, [startTxn]);

  useEffect(() => {
    if (intent) {
      intent.deny();
      setIntent(null);
    }
  }, [inputs]);

  useEffect(() => {
    if (intent || loading || !areInputsValid || txError) return;
    const timeout = setTimeout(() => {
      void handleTransaction();
    }, 700);
    return () => clearTimeout(timeout);
  }, [areInputsValid, intent, loading, txError]);

  return {
    inputs,
    setInputs,
    timer,
    setIsDialogOpen,
    setTxError,
    loading,
    refreshing,
    isDialogOpen,
    txError,
    handleTransaction,
    reset,
    filteredUnifiedBalance,
    startTransaction,
  };
};

export default useBridge;

