"use client";
import React, {
  type FC,
  memo,
  type RefObject,
  useEffect,
  useMemo,
  useState,
} from "react";
import { Button } from "../../ui/button";
import { Input } from "../../ui/input";
import { Label } from "../../ui/label";
import {
  type AllowanceHookSource,
  CHAIN_METADATA,
  type OnAllowanceHookData,
} from "@avail-project/nexus-core";
import { useNexus } from "../../nexus/NexusProvider";

interface AllowanceModalProps {
  allowance: RefObject<OnAllowanceHookData | null>;
  callback?: () => void;
  onCloseCallback?: () => void;
}

type AllowanceChoice = "min" | "max" | "custom";

interface AllowanceOptionProps {
  index: number;
  name: string;
  choice: AllowanceChoice;
  selectedChoice?: AllowanceChoice;
  onSelect: (index: number, choice: AllowanceChoice) => void;
  title: string;
  description?: string;
  children?: React.ReactNode;
  allowanceValue?: string;
}

const ALLOWANCE_CHOICES: Array<{
  choice: AllowanceChoice;
  title: string;
  description: string;
}> = [
  {
    choice: "min",
    title: "Minimum",
    description: "Grant the lowest allowance required for this action.",
  },
  {
    choice: "max",
    title: "Maximum",
    description: "Approve once and skip future approvals for this token.",
  },
  {
    choice: "custom",
    title: "Custom amount",
    description: "Specify an allowance that fits your threshold.",
  },
];

const AllowanceOption: FC<AllowanceOptionProps> = ({
  index,
  name,
  choice,
  selectedChoice,
  onSelect,
  title,
  description,
  children,
  allowanceValue,
}) => {
  const isActive = selectedChoice === choice;

  return (
    <Label className="block cursor-pointer">
      <input
        type="radio"
        name={name}
        value={choice}
        checked={isActive}
        onChange={() => onSelect(index, choice)}
        className="peer sr-only"
      />
      <div className="flex flex-col gap-3 rounded-xl border border-border/40 bg-background/40 px-4 py-3 transition peer-focus-visible:outline-none peer-focus-visible:ring-2 peer-focus-visible:ring-primary/50 peer-checked:border-primary peer-checked:bg-primary/10 sm:flex-row sm:items-center sm:justify-between">
        <div className="flex items-center justify-between w-full">
          <div>
            <p className="text-sm font-medium leading-tight">{title}</p>
            {description ? (
              <p className="text-xs text-muted-foreground">{description}</p>
            ) : null}
          </div>
          {allowanceValue && (
            <p className="text-sm font-medium leading-tight">
              {allowanceValue}
            </p>
          )}
        </div>

        {children}
      </div>
    </Label>
  );
};

const AllowanceModal: FC<AllowanceModalProps> = ({
  allowance,
  callback,
  onCloseCallback,
}) => {
  const { nexusSDK } = useNexus();
  const [selectedOption, setSelectedOption] = useState<AllowanceChoice[]>([]);
  const [customValues, setCustomValues] = useState<string[]>([]);

  const { sources, allow, deny } = allowance.current ?? {
    sources: [],
    allow: () => {},
    deny: () => {},
  };

  const defaultChoices = useMemo<AllowanceChoice[]>(
    () => Array.from({ length: sources.length }, () => "min"),
    [sources.length]
  );

  const isCustomValueValid = (
    value: string,
    minimumRaw: bigint,
    decimals: number
  ): boolean => {
    if (!value || value.trim() === "") return false;
    try {
      const parsedValue = nexusSDK?.utils?.parseUnits(value, decimals);
      if (parsedValue === undefined) return false;
      return parsedValue >= minimumRaw;
    } catch {
      return false;
    }
  };

  const hasValidationErrors = useMemo(() => {
    return sources.some((source, index) => {
      if (selectedOption[index] !== "custom") return false;
      const value = customValues[index];
      if (!value || value.trim() === "") return false;
      return !isCustomValueValid(
        value,
        source.allowance.minimumRaw,
        source.token.decimals
      );
    });
  }, [sources, selectedOption, customValues]);

  const onClose = () => {
    deny();
    allowance.current = null;
    onCloseCallback?.();
  };

  const onApprove = () => {
    const processed = sources.map((_, i) => {
      const opt = selectedOption[i];
      if (opt === "min" || opt === "max") return opt;
      const rawValue = customValues[i]?.trim();
      if (!rawValue) return "min";
      const parsed = Number(rawValue);
      if (!Number.isFinite(parsed) || parsed < 0) return "min";
      return rawValue;
    });
    try {
      allow(processed);
      allowance.current = null;
      callback?.();
    } catch (error) {
      console.error("AllowanceModal onApprove error", error);
      allowance.current = null;
      onCloseCallback?.();
    }
  };

  const handleChoiceChange = (index: number, value: AllowanceChoice) => {
    setSelectedOption((prev) => {
      const next = [...(prev.length ? prev : defaultChoices)];
      next[index] = value;
      return next;
    });
  };

  const formatAmount = (value: string | bigint, source: AllowanceHookSource) =>
    nexusSDK?.utils?.formatTokenBalance(value, {
      symbol: source.token.symbol,
      decimals: source.token.decimals,
    }) ?? "â€”";

  useEffect(() => {
    setSelectedOption(defaultChoices);
  }, [defaultChoices]);

  useEffect(() => {
    setCustomValues(Array.from({ length: sources.length }, () => ""));
  }, [sources.length]);

  return (
    <>
      <div className="space-y-1">
        <p className="text-lg font-semibold tracking-tight">
          Set Token Allowances
        </p>
        <p className="text-sm text-muted-foreground">
          Review every required token and choose the minimum, an unlimited max,
          or define a custom amount before approving.
        </p>
      </div>

      <div className="space-y-4 py-2 max-h-[60vh] overflow-y-auto pr-1">
        {sources?.map((source: AllowanceHookSource, index: number) => (
          <div
            key={`${source.token.symbol}-${index}`}
            className="rounded-2xl border border-border/40 bg-muted/10 p-4 shadow-sm transition hover:border-border"
          >
            <div className="flex flex-wrap items-center justify-between gap-3">
              <div className="flex items-center gap-3">
                <div className="flex h-12 w-12 items-center justify-center rounded-full bg-background shadow-inner">
                  <img
                    src={CHAIN_METADATA[source.chain.id]?.logo}
                    alt={source.chain.name}
                    width={24}
                    height={24}
                    className="rounded-full"
                  />
                </div>
                <div>
                  <p className="text-base font-semibold">
                    {source.token.symbol}
                  </p>
                  <p className="text-xs text-muted-foreground">
                    {source.chain.name}
                  </p>
                </div>
              </div>

              <div className="text-right">
                <p className="text-xs uppercase tracking-wide text-muted-foreground">
                  Current allowance
                </p>
                <p className="text-sm font-semibold">
                  {formatAmount(source.allowance.currentRaw, source)}
                </p>
              </div>
            </div>

            <div className="mt-4 space-y-2">
              {ALLOWANCE_CHOICES.map((choice) => {
                if (choice.choice === "custom") {
                  const customValue = customValues[index] ?? "";
                  const isCustomSelected = selectedOption[index] === "custom";
                  const showError =
                    isCustomSelected &&
                    customValue.trim() !== "" &&
                    !isCustomValueValid(
                      customValue,
                      source.allowance.minimumRaw,
                      source.token.decimals
                    );
                  return (
                    <AllowanceOption
                      key={choice.choice}
                      index={index}
                      name={`allowance-${index}`}
                      choice={choice.choice}
                      selectedChoice={selectedOption[index]}
                      onSelect={handleChoiceChange}
                      title={choice.title}
                      description={choice.description}
                    >
                      <div className="flex flex-col gap-1">
                        <Input
                          type="number"
                          step="any"
                          min="0"
                          inputMode="decimal"
                          placeholder="0.00"
                          value={isCustomSelected ? customValue : ""}
                          onChange={(e) => {
                            const next = [...customValues];
                            next[index] = e.target.value;
                            setCustomValues(next);
                          }}
                          maxLength={source.token.decimals}
                          className={`h-9 w-40 rounded-lg border bg-background/80 text-sm disabled:opacity-60 ${
                            showError ? "border-destructive" : ""
                          }`}
                          disabled={!isCustomSelected}
                        />
                        {showError && (
                          <p className="text-xs text-destructive">
                            Min: {source.allowance.minimum}
                          </p>
                        )}
                      </div>
                    </AllowanceOption>
                  );
                }
                return (
                  <AllowanceOption
                    key={choice.choice}
                    index={index}
                    name={`allowance-${index}`}
                    choice={choice.choice}
                    selectedChoice={selectedOption[index]}
                    onSelect={handleChoiceChange}
                    title={choice.title}
                    description={choice.description}
                    allowanceValue={
                      choice.choice === "min"
                        ? formatAmount(source.allowance.minimumRaw, source)
                        : "Unlimited"
                    }
                  />
                );
              })}
            </div>
          </div>
        ))}
      </div>

      <div className="mt-4 flex flex-col-reverse gap-3 sm:flex-row sm:items-center sm:justify-end">
        <Button variant="ghost" onClick={onClose} className="font-semibold">
          Deny
        </Button>
        <Button
          onClick={onApprove}
          className="w-full sm:w-auto font-semibold"
          disabled={hasValidationErrors}
        >
          Approve Selected
        </Button>
      </div>
    </>
  );
};

AllowanceModal.displayName = "AllowanceModal";

export default memo(AllowanceModal);

import { type FC, Fragment, useEffect, useMemo, useRef } from "react";
import { Input } from "../../ui/input";
import { Button } from "../../ui/button";
import { type UserAsset } from "@avail-project/nexus-core";
import { useNexus } from "../../nexus/NexusProvider";
import { type FastBridgeState } from "../hooks/useBridge";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";
import { SHORT_CHAIN_NAME } from "../../common";
import { LoaderCircle } from "lucide-react";

interface AmountInputProps {
  amount?: string;
  onChange: (value: string) => void;
  bridgableBalance?: UserAsset;
  onCommit?: (value: string) => void;
  disabled?: boolean;
  inputs: FastBridgeState;
  sourceChains?: number[];
  maxAmount?: string | number;
  maxAvailableAmount?: string;
}

const AmountInput: FC<AmountInputProps> = ({
  amount,
  onChange,
  bridgableBalance,
  onCommit,
  disabled,
  inputs,
  sourceChains,
  maxAmount,
  maxAvailableAmount,
}) => {
  const { nexusSDK, loading } = useNexus();
  const commitTimerRef = useRef<NodeJS.Timeout | null>(null);
  const normalizedMaxAmount = useMemo(() => {
    if (maxAmount === undefined || maxAmount === null) return undefined;
    const value = String(maxAmount).trim();
    if (!value || value === ".") return undefined;
    const parsed = Number.parseFloat(value);
    if (!Number.isFinite(parsed) || parsed <= 0) return undefined;
    return value;
  }, [maxAmount]);

  const applyMaxCap = (value: string) => {
    if (!normalizedMaxAmount || !nexusSDK || !inputs?.token || !inputs?.chain) {
      return value;
    }
    try {
      const valueRaw = nexusSDK.convertTokenReadableAmountToBigInt(
        value,
        inputs.token,
        inputs.chain,
      );
      const maxRaw = nexusSDK.convertTokenReadableAmountToBigInt(
        normalizedMaxAmount,
        inputs.token,
        inputs.chain,
      );
      return valueRaw > maxRaw ? normalizedMaxAmount : value;
    } catch {
      return value;
    }
  };

  const scheduleCommit = (val: string) => {
    if (!onCommit || disabled) return;
    if (commitTimerRef.current) clearTimeout(commitTimerRef.current);
    commitTimerRef.current = setTimeout(() => {
      onCommit(val);
    }, 800);
  };

  const onMaxClick = async () => {
    if (!nexusSDK || !inputs) return;
    const maxBalAvailable = await nexusSDK?.calculateMaxForBridge({
      token: inputs?.token,
      toChainId: inputs?.chain,
      recipient: inputs?.recipient,
      sourceChains,
    });
    if (!maxBalAvailable?.amount) return;
    const capped = applyMaxCap(maxBalAvailable.amount);
    onChange(capped);
    onCommit?.(capped);
  };

  useEffect(() => {
    return () => {
      if (commitTimerRef.current) {
        clearTimeout(commitTimerRef.current);
        commitTimerRef.current = null;
      }
    };
  }, []);

  return (
    <div className="flex flex-col gap-y-2 pb-2 w-full">
      <div className="w-full flex sm:flex-row flex-col border border-border rounded-lg gap-y-2">
        <Input
          type="text"
          inputMode="decimal"
          value={amount ?? ""}
          placeholder="Enter Amount"
          onChange={(e) => {
            let next = e.target.value.replaceAll(/[^0-9.]/g, "");
            const parts = next.split(".");
            if (parts.length > 2)
              next = parts[0] + "." + parts.slice(1).join("");
            if (next === ".") next = "0.";
            onChange(next);
            scheduleCommit(next);
          }}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              if (commitTimerRef.current) {
                clearTimeout(commitTimerRef.current);
                commitTimerRef.current = null;
              }
              onCommit?.(amount ?? "");
            }
          }}
          className="w-full border-none bg-transparent rounded-r-none focus-visible:ring-0 focus-visible:ring-offset-0 shadow-none py-0 px-3  h-12!"
          aria-invalid={Boolean(amount) && Number.isNaN(Number(amount))}
          disabled={disabled || loading}
        />
        <div className="flex items-center justify-end-safe gap-x-2 sm:gap-x-4 w-fit px-2 border-l border-border">
          {bridgableBalance && (
            <p className="text-base font-medium min-w-max">
              {nexusSDK?.utils?.formatTokenBalance(bridgableBalance?.balance, {
                symbol: bridgableBalance?.symbol,
                decimals: bridgableBalance?.decimals,
              })}
            </p>
          )}
          {loading && !bridgableBalance && (
            <LoaderCircle className="size-4 animate-spin" />
          )}
          <Button
            size={"sm"}
            variant={"ghost"}
            onClick={onMaxClick}
            className="px-0 font-medium"
            disabled={disabled}
          >
            MAX
          </Button>
        </div>
      </div>
      <Accordion type="single" collapsible className="w-full">
        <AccordionItem value="balance-breakdown">
          <AccordionTrigger
            className="w-fit justify-end items-center py-0 mt-2 gap-x-0.5 cursor-pointer text-sm font-normal"
            hideChevron={false}
          >
            View Balance Breakdown
          </AccordionTrigger>
          <AccordionContent className="pb-0 bg-muted rounded-lg mt-4">
            <div className="space-y-1 py-2">
              {bridgableBalance?.breakdown.map((chain) => {
                if (Number.parseFloat(chain.balance) === 0) return null;
                if (inputs?.chain === chain.chain.id) return null;
                return (
                  <Fragment key={chain.chain.id}>
                    <div className="flex items-center justify-between px-2 py-1 rounded-md">
                      <div className="flex items-center gap-2">
                        <div className="relative h-6 w-6">
                          <img
                            src={chain?.chain?.logo}
                            alt={chain.chain.name}
                            sizes="100%"
                            className="rounded-full"
                            loading="lazy"
                            decoding="async"
                            width="20"
                            height="20"
                          />
                        </div>
                        <span className="text-sm font-light sm:block hidden">
                          {SHORT_CHAIN_NAME[chain.chain.id]}
                        </span>
                      </div>
                      <p className="text-sm font-light text-right">
                        {nexusSDK?.utils?.formatTokenBalance(chain.balance, {
                          symbol: bridgableBalance?.symbol,
                          decimals: chain?.decimals,
                        })}
                      </p>
                    </div>
                  </Fragment>
                );
              })}
            </div>
          </AccordionContent>
        </AccordionItem>
      </Accordion>
    </div>
  );
};

export default AmountInput;

import { type FC, useMemo } from "react";
import { Label } from "../../ui/label";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../../ui/select";
import { type SUPPORTED_CHAINS_IDS } from "@avail-project/nexus-core";
import { cn } from "@/lib/utils";
import { useNexus } from "../../nexus/NexusProvider";

interface ChainSelectProps {
  selectedChain: number;
  disabled?: boolean;
  hidden?: boolean;
  className?: string;
  label?: string;
  handleSelect: (chainId: SUPPORTED_CHAINS_IDS) => void;
}

const ChainSelect: FC<ChainSelectProps> = ({
  selectedChain,
  disabled,
  hidden = false,
  className,
  label,
  handleSelect,
}) => {
  const { supportedChainsAndTokens } = useNexus();

  const selectedChainData = useMemo(() => {
    if (!supportedChainsAndTokens) return null;
    return supportedChainsAndTokens.find((c) => c.id === selectedChain);
  }, [selectedChain, supportedChainsAndTokens]);

  if (hidden) return null;
  return (
    <Select
      value={selectedChain?.toString() ?? ""}
      onValueChange={(value) => {
        if (!disabled) {
          handleSelect(Number.parseInt(value) as SUPPORTED_CHAINS_IDS);
        }
      }}
    >
      <div className="flex flex-col items-start gap-y-3 w-full">
        {label && <Label className="text-sm font-semibold">{label}</Label>}
        <SelectTrigger
          disabled={disabled}
          className="h-12! w-full text-base font-light"
        >
          <SelectValue>
            {selectedChainData && (
              <div
                className={cn("flex items-center gap-x-2 w-full", className)}
              >
                <img
                  src={selectedChainData?.logo}
                  alt={selectedChainData?.name}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                <p className="text-primary test-sm">
                  {selectedChainData?.name}
                </p>
              </div>
            )}
          </SelectValue>
        </SelectTrigger>
      </div>

      <SelectContent>
        <SelectGroup className="grid grid-cols-2 gap-2 max-h-[400px] overflow-y-scroll no-scrollbar">
          {supportedChainsAndTokens?.map((chain) => {
            return (
              <SelectItem key={chain.id} value={String(chain.id)}>
                <div className="flex items-center gap-x-2 my-1">
                  <img
                    src={chain.logo}
                    alt={chain?.name}
                    width={24}
                    height={24}
                    className="rounded-full"
                  />
                  <p className="text-primary test-sm">{chain.name}</p>
                </div>
              </SelectItem>
            );
          })}
        </SelectGroup>
      </SelectContent>
    </Select>
  );
};

export default ChainSelect;

import { type FC } from "react";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";
import {
  SUPPORTED_TOKENS,
  type ReadableIntent,
} from "@avail-project/nexus-core";
import { Skeleton } from "../../ui/skeleton";
import { useNexus } from "../../nexus/NexusProvider";
import { Tooltip, TooltipContent, TooltipTrigger } from "../../ui/tooltip";
import { MessageCircleQuestion } from "lucide-react";

interface FeeBreakdownProps {
  intent: ReadableIntent;
  tokenSymbol: SUPPORTED_TOKENS;
  isLoading?: boolean;
}

const FeeBreakdown: FC<FeeBreakdownProps> = ({
  intent,
  tokenSymbol,
  isLoading = false,
}) => {
  const { nexusSDK } = useNexus();

  const feeRows = [
    {
      key: "caGas",
      label: "Fast Bridge Gas Fees",
      value: intent?.fees?.caGas,
      description:
        "The gas fee required for executing the fast bridge transaction on the destination chain.",
    },
    {
      key: "gasSupplied",
      label: "Gas Supplied",
      value: intent?.fees?.gasSupplied,
      description:
        "The amount of gas tokens supplied to cover transaction costs on the destination chain.",
    },
    {
      key: "solver",
      label: "Solver Fees",
      value: intent?.fees?.solver,
      description:
        "Fees paid to the solver that executes the bridge transaction and ensures fast completion.",
    },
    {
      key: "protocol",
      label: "Protocol Fees",
      value: intent?.fees?.protocol,
      description:
        "Fees collected by the protocol for maintaining and operating the bridge infrastructure.",
    },
  ];

  return (
    <Accordion type="single" collapsible className="w-full">
      <AccordionItem value="breakdown">
        <div className="w-full flex items-start justify-between">
          <p className="font-light text-base">Total fees</p>

          <div className="flex flex-col items-end justify-end-safe gap-y-1">
            {isLoading ? (
              <Skeleton className="h-5 w-24" />
            ) : (
              <p className="font-light text-base min-w-max">
                {nexusSDK?.utils?.formatTokenBalance(intent.fees?.total, {
                  symbol: tokenSymbol,
                  decimals: intent?.token?.decimals,
                })}
              </p>
            )}
            <AccordionTrigger
              containerClassName="w-fit"
              className="p-0 items-center gap-x-1"
              hideChevron={false}
            >
              <p className="text-sm font-light">View Breakup</p>
            </AccordionTrigger>
          </div>
        </div>
        <AccordionContent>
          <div className="w-full flex flex-col items-center justify-between gap-y-3 bg-muted px-4 py-2 rounded-lg mt-2">
            {feeRows.map(({ key, label, value, description }) => {
              if (Number.parseFloat(value ?? "0") <= 0) return null;
              return (
                <Tooltip key={key}>
                  <div className="flex items-center w-full justify-between">
                    <div className="flex items-center gap-x-2">
                      <p className="text-sm font-light">{label}</p>
                      <TooltipTrigger asChild>
                        <MessageCircleQuestion className="size-4" />
                      </TooltipTrigger>
                    </div>
                    {isLoading ? (
                      <Skeleton className="h-4 w-20" />
                    ) : (
                      <p className="text-sm font-light">
                        {nexusSDK?.utils?.formatTokenBalance(value, {
                          symbol: tokenSymbol,
                          decimals: intent?.token?.decimals,
                        })}
                      </p>
                    )}
                  </div>
                  <TooltipContent className="max-w-sm text-balance">
                    <p className="text-sm font-light">{description}</p>
                  </TooltipContent>
                </Tooltip>
              );
            })}
          </div>
        </AccordionContent>
      </AccordionItem>
    </Accordion>
  );
};

export default FeeBreakdown;

"use client";
import { type FC, useState } from "react";
import { Input } from "../../ui/input";
import { Check, Edit } from "lucide-react";
import { Button } from "../../ui/button";
import { useNexus } from "../../nexus/NexusProvider";
import { type Address } from "viem";

interface RecipientAddressProps {
  address?: Address;
  onChange: (address: string) => void;
  disabled?: boolean;
}

const RecipientAddress: FC<RecipientAddressProps> = ({
  address,
  onChange,
  disabled,
}) => {
  const { nexusSDK } = useNexus();
  const [isEditing, setIsEditing] = useState(false);
  return (
    <div className="w-full">
      {isEditing ? (
        <div className="flex items-center w-full justify-between gap-x-4">
          <Input
            value={address}
            placeholder="Enter Recipient Address"
            onChange={(e) => onChange(e.target.value)}
            className="w-full"
          />
          <Button
            variant={"ghost"}
            size={"icon"}
            onClick={() => {
              setIsEditing(false);
            }}
          >
            <Check className="size-5" />
          </Button>
        </div>
      ) : (
        <div className="flex flex-col sm:flex-row items-start sm:items-center w-full justify-between">
          <p className="font-light text-base">Recipient Address</p>
          <div className="flex items-center gap-x-3 ">
            {address && (
              <p className="font-light text-base">
                {nexusSDK?.utils?.truncateAddress(address, 6, 6)}
              </p>
            )}

            <Button
              variant={"ghost"}
              size={"icon"}
              onClick={() => {
                setIsEditing(true);
              }}
              className="px-0 size-5"
              disabled={disabled}
            >
              <Edit className="size-5" />
            </Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RecipientAddress;

import {
  type ReadableIntent,
  type SUPPORTED_TOKENS,
  type UserAsset,
} from "@avail-project/nexus-core";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";
import { Skeleton } from "../../ui/skeleton";
import { useNexus } from "../../nexus/NexusProvider";
import { Checkbox } from "../../ui/checkbox";
import { cn } from "@/lib/utils";

type SourceCoverageState = "healthy" | "warning" | "error";

interface SourceBreakdownProps {
  intent?: ReadableIntent;
  tokenSymbol: SUPPORTED_TOKENS;
  isLoading?: boolean;
  availableSources: UserAsset["breakdown"];
  selectedSourceChains: number[];
  onToggleSourceChain: (chainId: number) => void;
  onSourceMenuOpenChange?: (open: boolean) => void;
  isSourceSelectionInsufficient?: boolean;
  sourceCoverageState?: SourceCoverageState;
  sourceCoveragePercent?: number;
  missingToProceed?: string;
  missingToSafety?: string;
  selectedTotal?: string;
  requiredTotal?: string;
  requiredSafetyTotal?: string;
}

const SourceBreakdown = ({
  intent,
  tokenSymbol,
  isLoading = false,
  availableSources,
  selectedSourceChains,
  onToggleSourceChain,
  onSourceMenuOpenChange,
  isSourceSelectionInsufficient = false,
  sourceCoverageState = "healthy",
  sourceCoveragePercent = 100,
  missingToProceed,
  missingToSafety,
  selectedTotal,
  requiredTotal,
  requiredSafetyTotal,
}: SourceBreakdownProps) => {
  const { nexusSDK } = useNexus();
  const normalizedCoverage = Math.max(0, Math.min(100, sourceCoveragePercent));
  const progressRadius = 16;
  const progressCircumference = 2 * Math.PI * progressRadius;
  const progressOffset =
    progressCircumference - (normalizedCoverage / 100) * progressCircumference;
  const showCoverageFeedback = Boolean(
    selectedTotal && requiredTotal && requiredSafetyTotal,
  );
  const shouldShowProceedMessage =
    sourceCoverageState === "error" &&
    Number.parseFloat(missingToProceed ?? "0") > 0;
  const shouldShowSafetyMessage =
    sourceCoverageState === "warning" &&
    Number.parseFloat(missingToSafety ?? "0") > 0;

  const coverageToneClass =
    sourceCoverageState === "error"
      ? "text-rose-500"
      : sourceCoverageState === "warning"
        ? "text-amber-500"
        : "text-emerald-500";

  const coverageSurfaceClass =
    sourceCoverageState === "error"
      ? " text-rose-950 dark:text-rose-200"
      : sourceCoverageState === "warning"
        ? " text-amber-950 dark:text-amber-200"
        : " text-emerald-950 dark:text-emerald-200";

  return (
    <Accordion
      type="single"
      collapsible
      className="w-full"
      onValueChange={(value) => onSourceMenuOpenChange?.(value === "sources")}
    >
      <AccordionItem value="sources">
        <div className="flex items-start justify-between gap-x-4 w-full">
          {isLoading ? (
            <>
              <div className="flex flex-col items-start gap-y-1 min-w-fit">
                <p className="text-base font-light">You Spend</p>
                <Skeleton className="h-4 w-44" />
              </div>
              <div className="flex flex-col items-end gap-y-1 min-w-fit">
                <Skeleton className="h-5 w-24" />
                <div className="w-fit">
                  <Skeleton className="h-4 w-24" />
                </div>
              </div>
            </>
          ) : (
            intent?.sources && (
              <>
                <div className="flex flex-col items-start gap-y-1 min-w-fit">
                  <p className="text-base font-light">You Spend</p>
                  <p className="text-sm font-light">
                    {`${tokenSymbol} on ${
                      intent?.sources?.length
                    } ${intent?.sources?.length > 1 ? "chains" : "chain"}`}
                  </p>
                </div>

                <div className="flex flex-col items-end gap-y-1 min-w-fit">
                  <p className="text-base font-light">
                    {nexusSDK?.utils?.formatTokenBalance(intent?.sourcesTotal, {
                      symbol: tokenSymbol,
                      decimals: intent?.token?.decimals,
                    })}
                  </p>
                  <AccordionTrigger
                    containerClassName="w-fit"
                    className="py-0 items-center gap-x-1"
                    hideChevron={false}
                  >
                    <p className="text-sm font-light">View Sources</p>
                  </AccordionTrigger>
                </div>
              </>
            )
          )}
        </div>
        {!isLoading && (
          <AccordionContent className="my-4 bg-muted pb-0 px-4 py-2 rounded-lg w-full">
            {showCoverageFeedback && (
              <div
                className={cn(
                  "mb-3 rounded-md py-2 text-sm",
                  coverageSurfaceClass,
                )}
              >
                <div className="flex items-center gap-3">
                  <div className="relative size-12 shrink-0">
                    <svg
                      className="-rotate-90 size-12"
                      viewBox="0 0 48 48"
                      aria-hidden="true"
                    >
                      <circle
                        cx="24"
                        cy="24"
                        r={progressRadius}
                        stroke="currentColor"
                        strokeWidth="4"
                        className="text-muted-foreground/30"
                        fill="none"
                      />
                      <circle
                        cx="24"
                        cy="24"
                        r={progressRadius}
                        stroke="currentColor"
                        strokeWidth="4"
                        strokeLinecap="round"
                        className={coverageToneClass}
                        fill="none"
                        strokeDasharray={progressCircumference}
                        strokeDashoffset={progressOffset}
                      />
                    </svg>
                    <span className="absolute inset-0 flex items-center justify-center text-[10px] font-medium p-1">
                      {Math.round(normalizedCoverage)}%
                    </span>
                  </div>

                  <div className="flex flex-col gap-y-0.5">
                    <p className="font-medium">
                      Selected{" "}
                      <span className="font-semibold">
                        {selectedTotal} {tokenSymbol}
                      </span>{" "}
                      of{" "}
                      <span className="font-semibold">
                        {requiredSafetyTotal} {tokenSymbol}
                      </span>{" "}
                    </p>
                    {shouldShowProceedMessage && (
                      <p>
                        Add{" "}
                        <span className="font-semibold">
                          {missingToProceed} {tokenSymbol}
                        </span>{" "}
                        more to make this transaction.
                      </p>
                    )}
                    {shouldShowSafetyMessage && (
                      <p>
                        Add{" "}
                        <span className="font-semibold">
                          {missingToSafety} {tokenSymbol}
                        </span>{" "}
                        more to reach the 130% safety buffer.
                      </p>
                    )}
                    {!isSourceSelectionInsufficient &&
                      sourceCoverageState === "healthy" && (
                        <p>Source coverage is healthy for execution.</p>
                      )}
                  </div>
                </div>
              </div>
            )}

            {availableSources.length === 0 ? (
              <p className="py-2 text-sm text-muted-foreground">
                No source balances available for this token.
              </p>
            ) : (
              <div className="flex flex-col items-center gap-y-3">
                {availableSources.map((source) => {
                  const chainId = source.chain.id;
                  const isSelected = selectedSourceChains.includes(chainId);
                  const isLastSelected = isSelected
                    ? selectedSourceChains.length === 1
                    : false;
                  const willUseAmount = intent?.sources?.find(
                    (s) => s.chainID === chainId,
                  )?.amount;

                  return (
                    <div
                      key={chainId}
                      className={cn(
                        "flex items-center justify-between w-full gap-x-2 select-none",
                        isLastSelected
                          ? "opacity-80 cursor-not-allowed"
                          : "cursor-pointer",
                      )}
                      onClick={() => {
                        if (isLastSelected) return;
                        onToggleSourceChain(chainId);
                      }}
                      role="button"
                      tabIndex={0}
                      onKeyDown={(e) => {
                        if (isLastSelected) return;
                        if (e.key === "Enter" || e.key === " ") {
                          e.preventDefault();
                          onToggleSourceChain(chainId);
                        }
                      }}
                    >
                      <div className="flex items-center gap-x-2">
                        <Checkbox
                          checked={isSelected}
                          disabled={isLastSelected}
                          onCheckedChange={() => {
                            if (isLastSelected) return;
                            onToggleSourceChain(chainId);
                          }}
                          onClick={(e) => e.stopPropagation()}
                          aria-label={`Select ${source.chain.name} as a source`}
                        />
                        <img
                          src={source.chain.logo}
                          alt={source.chain.name}
                          width={20}
                          height={20}
                          className="rounded-full"
                        />
                        <p className="text-base font-light">
                          {source.chain.name}
                        </p>
                      </div>

                      <div className="flex flex-col items-end gap-y-0.5 min-w-fit">
                        <p className="text-base font-light">
                          {nexusSDK?.utils?.formatTokenBalance(source.balance, {
                            symbol: tokenSymbol,
                            decimals: source.decimals,
                          })}
                        </p>
                        {willUseAmount && (
                          <p className="text-xs text-muted-foreground">
                            Will use:{" "}
                            {nexusSDK?.utils?.formatTokenBalance(
                              willUseAmount,
                              {
                                symbol: tokenSymbol,
                                decimals: intent?.token?.decimals,
                              },
                            )}
                          </p>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            )}

            {availableSources.length > 0 && (
              <div className="mt-3 text-xs text-muted-foreground space-y-1">
                <p>Select at least 1 chain.</p>
              </div>
            )}
          </AccordionContent>
        )}
      </AccordionItem>
    </Accordion>
  );
};

export default SourceBreakdown;

import {
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
} from "@avail-project/nexus-core";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../../ui/select";
import { Label } from "../../ui/label";
import { useNexus } from "../../nexus/NexusProvider";
import { useMemo } from "react";

interface TokenSelectProps {
  selectedToken?: SUPPORTED_TOKENS;
  selectedChain: SUPPORTED_CHAINS_IDS;
  handleTokenSelect: (token: SUPPORTED_TOKENS) => void;
  isTestnet?: boolean;
  disabled?: boolean;
  label?: string;
}

const TokenSelect = ({
  selectedToken,
  selectedChain,
  handleTokenSelect,
  isTestnet = false,
  disabled = false,
  label,
}: TokenSelectProps) => {
  const { supportedChainsAndTokens } = useNexus();
  const tokenData = useMemo(() => {
    return supportedChainsAndTokens
      ?.filter((chain) => chain.id === selectedChain)
      .flatMap((chain) => chain.tokens);
  }, [selectedChain, supportedChainsAndTokens]);

  const selectedTokenData = tokenData?.find((token) => {
    return token.symbol === selectedToken;
  });

  return (
    <Select
      value={selectedToken}
      onValueChange={(value) =>
        !disabled && handleTokenSelect(value as SUPPORTED_TOKENS)
      }
    >
      <div className="flex flex-col items-start gap-y-1">
        {label && <Label className="text-sm font-semibold">{label}</Label>}
        <SelectTrigger
          disabled={disabled}
          className="w-full h-12! text-base font-light"
        >
          <SelectValue placeholder="Select a token" className="w-full">
            {selectedChain && selectedTokenData && (
              <div className="flex items-center gap-x-2 w-full">
                <img
                  src={selectedTokenData?.logo}
                  alt={selectedTokenData?.symbol}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                {selectedToken}
              </div>
            )}
          </SelectValue>
        </SelectTrigger>
      </div>

      <SelectContent>
        <SelectGroup>
          {tokenData?.map((token) => (
            <SelectItem key={token.symbol} value={token.symbol}>
              <div className="flex items-center gap-x-2 my-1">
                <img
                  src={token.logo}
                  alt={token.symbol}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                <div className="flex flex-col">
                  <span>
                    {isTestnet ? `${token.symbol} (Testnet)` : token.symbol}
                  </span>
                </div>
              </div>
            </SelectItem>
          ))}
        </SelectGroup>
      </SelectContent>
    </Select>
  );
};

export default TokenSelect;

import { Check, Circle, LoaderPinwheel, SquareArrowOutUpRight } from "lucide-react";
import { type FC, memo, useMemo } from "react";
import {
  type BridgeStepType,
  type SwapStepType,
} from "@avail-project/nexus-core";
import { Button } from "../../ui/button";

type ProgressStep = BridgeStepType | SwapStepType;

interface TransactionProgressProps {
  timer: number;
  steps: Array<{ id: number; completed: boolean; step: ProgressStep }>;
  viewIntentUrl?: string;
  operationType?: string;
  completed?: boolean;
}

export const getOperationText = (type: string) => {
  switch (type) {
    case "bridge":
      return "Transaction";
    case "transfer":
      return "Transferring";
    case "bridgeAndExecute":
      return "Bridge & Execute";
    case "swap":
      return "Swapping";
    default:
      return "Processing";
  }
};

type DisplayStep = { id: string; label: string; completed: boolean };

const StepList: FC<{ steps: DisplayStep[]; currentIndex: number }> = memo(
  ({ steps, currentIndex }) => {
    return (
      <div className="w-full mt-6 space-y-6">
        {steps.map((s, idx) => {
          const isCompleted = !!s.completed;
          const isCurrent = currentIndex === -1 ? false : idx === currentIndex;

          let rightIcon = <Circle className="size-5 text-muted-foreground" />;
          if (isCompleted) {
            rightIcon = <Check className="size-5 text-green-600" />;
          } else if (isCurrent) {
            rightIcon = (
              <LoaderPinwheel className="size-5 animate-spin text-muted-foreground" />
            );
          }

          return (
            <div
              key={s.id}
              className="flex items-center justify-between w-full"
            >
              <div className="flex items-center gap-x-3">
                <span className="text-base font-semibold">{s.label}</span>
              </div>
              {rightIcon}
            </div>
          );
        })}
      </div>
    );
  }
);
StepList.displayName = "StepList";

const TransactionProgress: FC<TransactionProgressProps> = ({
  timer,
  steps,
  viewIntentUrl,
  operationType = "bridge",
  completed = false,
}) => {
  const totalSteps = Array.isArray(steps) ? steps.length : 0;
  const completedSteps = Array.isArray(steps)
    ? steps.reduce((acc, s) => acc + (s?.completed ? 1 : 0), 0)
    : 0;
  const rawPercent = totalSteps > 0 ? completedSteps / totalSteps : 0;
  const percent = completed ? 1 : rawPercent;
  const allCompleted = completed || percent >= 1;
  const opText = getOperationText(operationType);
  const headerText = allCompleted
    ? `${opText} Completed`
    : `${opText} In Progress...`;
  const ctaText = allCompleted ? `View Explorer` : "View Intent";

  const { effectiveSteps, currentIndex } = useMemo(() => {
    const milestones = [
      "Intent verified",
      "Collected on sources",
      "Filled on destination",
    ];
    const thresholds = milestones.map(
      (_, idx) => (idx + 1) / milestones.length
    );
    const displaySteps: DisplayStep[] = milestones.map((label, idx) => ({
      id: `M${idx}`,
      label,
      completed: idx === 0 ? timer > 0 : percent >= thresholds[idx],
    }));
    const current = displaySteps.findIndex((st) => !st.completed);
    return { effectiveSteps: displaySteps, currentIndex: current };
  }, [percent, timer, completed]);

  return (
    <div className="w-full flex flex-col items-center">
      <div className="flex flex-col items-center gap-y-3">
        {allCompleted ? (
          <Check className="size-6 text-green-600" />
        ) : (
          <LoaderPinwheel className="size-6 animate-spin" />
        )}
        <p>{headerText}</p>
        <div className="flex items-center justify-center w-full">
          <span className="text-2xl font-semibold font-nexus-primary text-nexus-black">
            {Math.floor(timer)}
          </span>
          <span className="text-base font-semibold font-nexus-primary text-nexus-black">
            .
          </span>
          <span className="text-base font-semibold font-nexus-primary text-nexus-muted-secondary">
            {String(Math.floor((timer % 1) * 1000)).padStart(3, "0")}s
          </span>
        </div>
      </div>

      <StepList steps={effectiveSteps} currentIndex={currentIndex} />

      {viewIntentUrl && (
        <Button asChild variant="outline" className="mt-8">
          <a href={viewIntentUrl} target="_blank" rel="noreferrer">
            {ctaText}
            <SquareArrowOutUpRight className="size-4" />
          </a>
        </Button>
      )}
    </div>
  );
};

export default TransactionProgress;

"use client";
import { type FC, useEffect, useState } from "react";
import { Card, CardContent } from "../ui/card";
import ChainSelect from "./components/chain-select";
import TokenSelect from "./components/token-select";
import { Button } from "../ui/button";
import { LoaderPinwheel, X } from "lucide-react";
import { useNexus } from "../nexus/NexusProvider";
import AmountInput from "./components/amount-input";
import FeeBreakdown from "./components/fee-breakdown";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../ui/dialog";
import TransactionProgress from "./components/transaction-progress";
import AllowanceModal from "./components/allowance-modal";
import useBridge from "./hooks/useBridge";
import SourceBreakdown from "./components/source-breakdown";
import {
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
} from "@avail-project/nexus-core";
import { type Address } from "viem";
import { Skeleton } from "../ui/skeleton";
import RecipientAddress from "./components/recipient-address";
import ViewHistory from "../view-history/view-history";

interface FastBridgeProps {
  connectedAddress: Address;
  maxAmount?: string | number;
  prefill?: {
    token: SUPPORTED_TOKENS;
    chainId: SUPPORTED_CHAINS_IDS;
    amount?: string;
    recipient?: Address;
  };
  onComplete?: () => void;
  onStart?: () => void;
  onError?: (message: string) => void;
}

const FastBridge: FC<FastBridgeProps> = ({
  connectedAddress,
  maxAmount,
  onComplete,
  onStart,
  onError,
  prefill,
}) => {
  const [isSourceMenuOpen, setIsSourceMenuOpen] = useState(false);
  const {
    nexusSDK,
    intent,
    bridgableBalance,
    allowance,
    network,
    fetchBridgableBalance,
  } = useNexus();

  const {
    inputs,
    setInputs,
    timer,
    loading,
    refreshing,
    isDialogOpen,
    txError,
    setTxError,
    handleTransaction,
    reset,
    filteredBridgableBalance,
    startTransaction,
    setIsDialogOpen,
    commitAmount,
    lastExplorerUrl,
    steps,
    status,
    availableSources,
    selectedSourceChains,
    toggleSourceChain,
    isSourceSelectionInsufficient,
    isSourceSelectionReadyForAccept,
    sourceCoverageState,
    sourceCoveragePercent,
    missingToProceed,
    missingToSafety,
    selectedTotal,
    requiredTotal,
    requiredSafetyTotal,
    maxAvailableAmount,
    isInputsValid,
  } = useBridge({
    prefill,
    network: network ?? "mainnet",
    connectedAddress,
    nexusSDK,
    intent,
    bridgableBalance,
    allowance,
    onComplete,
    onStart,
    onError,
    fetchBalance: fetchBridgableBalance,
    maxAmount,
    isSourceMenuOpen,
  });

  useEffect(() => {
    if (!intent.current?.intent) {
      setIsSourceMenuOpen(false);
    }
  }, [intent.current?.intent]);

  return (
    <Card className="w-full max-w-xl">
      <CardContent className="flex flex-col gap-y-4 w-full px-2 sm:px-6 relative">
        <ViewHistory className="absolute -top-2 right-3" />
        <ChainSelect
          selectedChain={inputs?.chain}
          handleSelect={(chain) =>
            setInputs({
              ...inputs,
              chain,
            })
          }
          label="To"
          disabled={!!prefill?.chainId}
        />
        <TokenSelect
          selectedChain={inputs?.chain}
          selectedToken={inputs?.token}
          handleTokenSelect={(token) => setInputs({ ...inputs, token })}
          disabled={!!prefill?.token}
        />
        <AmountInput
          amount={inputs?.amount}
          onChange={(amount) => setInputs({ ...inputs, amount })}
          bridgableBalance={filteredBridgableBalance}
          onCommit={() => void commitAmount()}
          disabled={refreshing || !!prefill?.amount}
          inputs={inputs}
          sourceChains={selectedSourceChains}
          maxAmount={maxAmount}
          maxAvailableAmount={maxAvailableAmount}
        />
        <RecipientAddress
          address={inputs?.recipient}
          onChange={(address) =>
            setInputs({ ...inputs, recipient: address as `0x${string}` })
          }
          disabled={!!prefill?.recipient}
        />
        {intent?.current?.intent && (
          <>
            <SourceBreakdown
              intent={intent?.current?.intent}
              tokenSymbol={filteredBridgableBalance?.symbol as SUPPORTED_TOKENS}
              isLoading={refreshing}
              availableSources={availableSources}
              selectedSourceChains={selectedSourceChains}
              onToggleSourceChain={toggleSourceChain}
              onSourceMenuOpenChange={setIsSourceMenuOpen}
              isSourceSelectionInsufficient={isSourceSelectionInsufficient}
              sourceCoverageState={sourceCoverageState}
              sourceCoveragePercent={sourceCoveragePercent}
              missingToProceed={missingToProceed}
              missingToSafety={missingToSafety}
              selectedTotal={selectedTotal}
              requiredTotal={requiredTotal}
              requiredSafetyTotal={requiredSafetyTotal}
            />

            <div className="w-full flex items-start justify-between gap-x-4">
              <p className="text-base font-light">You receive</p>
              <div className="flex flex-col gap-y-1 min-w-fit">
                {refreshing ? (
                  <Skeleton className="h-5 w-28" />
                ) : (
                  <p className="text-base font-light text-right">
                    {`${
                      connectedAddress === inputs?.recipient
                        ? intent?.current?.intent?.destination?.amount
                        : inputs.amount
                    } ${inputs?.token === "USDM" ? "USDM" : filteredBridgableBalance?.symbol}`}
                  </p>
                )}
                {refreshing ? (
                  <Skeleton className="h-4 w-36" />
                ) : (
                  <p className="text-sm font-light text-right">
                    on {intent?.current?.intent?.destination?.chainName}
                  </p>
                )}
              </div>
            </div>
            <FeeBreakdown
              intent={intent?.current?.intent}
              isLoading={refreshing}
              tokenSymbol={filteredBridgableBalance?.symbol as SUPPORTED_TOKENS}
            />
          </>
        )}

        {!intent.current && (
          <Button
            onClick={handleTransaction}
            disabled={!isInputsValid || loading}
          >
            {loading ? (
              <LoaderPinwheel className="animate-spin size-5" />
            ) : (
              "Bridge"
            )}
          </Button>
        )}

        <Dialog
          open={isDialogOpen}
          onOpenChange={(open) => {
            if (loading) return;
            setIsDialogOpen(open);
          }}
        >
          {intent.current && !isDialogOpen && (
            <div className="w-full flex items-center gap-x-2 justify-between">
              <Button variant={"destructive"} onClick={reset} className="w-1/2">
                Deny
              </Button>
              <DialogTrigger asChild>
                <Button
                  onClick={startTransaction}
                  className="w-1/2"
                  disabled={refreshing || !isSourceSelectionReadyForAccept}
                >
                  {refreshing ? "Refreshing..." : "Accept"}
                </Button>
              </DialogTrigger>
            </div>
          )}

          <DialogContent>
            <DialogHeader className="sr-only">
              <DialogTitle>Transaction Progress</DialogTitle>
            </DialogHeader>
            {allowance.current ? (
              <AllowanceModal
                allowance={allowance}
                callback={startTransaction}
                onCloseCallback={reset}
              />
            ) : (
              <TransactionProgress
                timer={timer}
                steps={steps}
                viewIntentUrl={lastExplorerUrl}
                operationType={"bridge"}
                completed={status === "success"}
              />
            )}
          </DialogContent>
        </Dialog>

        {txError && (
          <div className="rounded-md border border-destructive bg-destructive/80 px-3 py-2 text-sm text-destructive-foreground flex items-start justify-between gap-x-3 mt-3 w-full">
            <span className="flex-1 w-full truncate">{txError}</span>
            <Button
              type="button"
              size={"icon"}
              variant={"ghost"}
              onClick={() => {
                reset();
                setTxError(null);
              }}
              className="text-destructive-foreground/80 hover:text-destructive-foreground focus:outline-none"
              aria-label="Dismiss error"
            >
              <X className="size-4" />
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default FastBridge;

import {
  type BridgeStepType,
  NEXUS_EVENTS,
  type NexusNetwork,
  NexusSDK,
  type OnAllowanceHookData,
  type OnIntentHookData,
  SUPPORTED_CHAINS,
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
  type UserAsset,
} from "@avail-project/nexus-core";
import {
  useEffect,
  useMemo,
  useCallback,
  useRef,
  useState,
  useReducer,
  type RefObject,
} from "react";
import { type Address, isAddress } from "viem";
import {
  useStopwatch,
  usePolling,
  useNexusError,
  useTransactionSteps,
  type TransactionStatus,
} from "../../common";
import { notifyIntentHistoryRefresh } from "../../view-history/history-events";

export interface FastBridgeState {
  chain: SUPPORTED_CHAINS_IDS;
  token: SUPPORTED_TOKENS;
  amount?: string;
  recipient?: `0x${string}`;
}

interface UseBridgeProps {
  network: NexusNetwork;
  connectedAddress: Address;
  nexusSDK: NexusSDK | null;
  intent: RefObject<OnIntentHookData | null>;
  allowance: RefObject<OnAllowanceHookData | null>;
  bridgableBalance: UserAsset[] | null;
  prefill?: {
    token: string;
    chainId: number;
    amount?: string;
    recipient?: Address;
  };
  onComplete?: () => void;
  onStart?: () => void;
  onError?: (message: string) => void;
  fetchBalance: () => Promise<void>;
  maxAmount?: string | number;
  isSourceMenuOpen?: boolean;
}

type BridgeState = {
  inputs: FastBridgeState;
  status: TransactionStatus;
};

type Action =
  | { type: "setInputs"; payload: Partial<FastBridgeState> }
  | { type: "resetInputs" }
  | { type: "setStatus"; payload: TransactionStatus };

const buildInitialInputs = (
  network: NexusNetwork,
  connectedAddress: Address,
  prefill?: {
    token: string;
    chainId: number;
    amount?: string;
    recipient?: Address;
  },
): FastBridgeState => {
  return {
    chain:
      (prefill?.chainId as SUPPORTED_CHAINS_IDS) ??
      (network === "testnet"
        ? SUPPORTED_CHAINS.SEPOLIA
        : SUPPORTED_CHAINS.ETHEREUM),
    token: (prefill?.token as SUPPORTED_TOKENS) ?? "USDC",
    amount: prefill?.amount ?? undefined,
    recipient: (prefill?.recipient as `0x${string}`) ?? connectedAddress,
  };
};

const MAX_AMOUNT_REGEX = /^\d*\.?\d+$/;

const normalizeMaxAmount = (
  maxAmount?: string | number,
): string | undefined => {
  if (maxAmount === undefined || maxAmount === null) return undefined;
  const value = String(maxAmount).trim();
  if (!value || value === "." || !MAX_AMOUNT_REGEX.test(value))
    return undefined;
  const parsed = Number.parseFloat(value);
  if (!Number.isFinite(parsed) || parsed <= 0) return undefined;
  return value;
};

const clampAmountToMax = ({
  amount,
  maxAmount,
  nexusSDK,
  token,
  chainId,
}: {
  amount: string;
  maxAmount?: string;
  nexusSDK: NexusSDK;
  token: SUPPORTED_TOKENS;
  chainId: SUPPORTED_CHAINS_IDS;
}): string => {
  if (!maxAmount) return amount;
  try {
    const amountRaw = nexusSDK.convertTokenReadableAmountToBigInt(
      amount,
      token,
      chainId,
    );
    const maxRaw = nexusSDK.convertTokenReadableAmountToBigInt(
      maxAmount,
      token,
      chainId,
    );
    return amountRaw > maxRaw ? maxAmount : amount;
  } catch {
    return amount;
  }
};

type SourceCoverageState = "healthy" | "warning" | "error";

const SOURCE_SAFETY_MULTIPLIER_NUMERATOR = BigInt(130);
const SOURCE_SAFETY_MULTIPLIER_DENOMINATOR = BigInt(100);

const formatAmountForDisplay = (
  amount: bigint,
  decimals: number | undefined,
  nexusSDK: NexusSDK,
): string => {
  if (typeof decimals !== "number") return amount.toString();
  const formatted = nexusSDK.utils.formatUnits(amount, decimals);
  if (!formatted.includes(".")) return formatted;
  const [whole, fraction] = formatted.split(".");
  const trimmedFraction = fraction.slice(0, 6).replace(/0+$/, "");
  if (!trimmedFraction && whole === "0" && amount > BigInt(0)) {
    return "0.000001";
  }
  return trimmedFraction ? `${whole}.${trimmedFraction}` : whole;
};

const useBridge = ({
  network,
  connectedAddress,
  nexusSDK,
  intent,
  bridgableBalance,
  prefill,
  onComplete,
  onStart,
  onError,
  fetchBalance,
  allowance,
  maxAmount,
  isSourceMenuOpen = false,
}: UseBridgeProps) => {
  const handleNexusError = useNexusError();
  const initialState: BridgeState = {
    inputs: buildInitialInputs(network, connectedAddress, prefill),
    status: "idle",
  };
  function reducer(state: BridgeState, action: Action): BridgeState {
    switch (action.type) {
      case "setInputs":
        return { ...state, inputs: { ...state.inputs, ...action.payload } };
      case "resetInputs":
        return {
          ...state,
          inputs: buildInitialInputs(network, connectedAddress, prefill),
        };
      case "setStatus":
        return { ...state, status: action.payload };
      default:
        return state;
    }
  }
  const [state, dispatch] = useReducer(reducer, initialState);
  const inputs = state.inputs;
  const setInputs = (next: FastBridgeState | Partial<FastBridgeState>) => {
    dispatch({ type: "setInputs", payload: next as Partial<FastBridgeState> });
  };

  const loading = state.status === "executing";
  const [refreshing, setRefreshing] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [txError, setTxError] = useState<string | null>(null);
  const [lastExplorerUrl, setLastExplorerUrl] = useState<string>("");
  const commitLockRef = useRef<boolean>(false);
  const runIdRef = useRef(0);
  const previousConnectedAddressRef = useRef<Address>(connectedAddress);
  const maxAmountRequestIdRef = useRef(0);
  const [selectedSourceChains, setSelectedSourceChains] = useState<
    number[] | null
  >(null);
  const [selectedSourcesMaxAmount, setSelectedSourcesMaxAmount] = useState<
    string | null
  >(null);
  const [appliedSourceSelectionKey, setAppliedSourceSelectionKey] =
    useState("ALL");
  const {
    steps,
    onStepsList,
    onStepComplete,
    reset: resetSteps,
  } = useTransactionSteps<BridgeStepType>();
  const configuredMaxAmount = useMemo(
    () => normalizeMaxAmount(maxAmount),
    [maxAmount],
  );

  const areInputsValid = useMemo(() => {
    const hasToken = inputs?.token !== undefined && inputs?.token !== null;
    const hasChain = inputs?.chain !== undefined && inputs?.chain !== null;
    const hasAmount = Boolean(inputs?.amount) && Number(inputs?.amount) > 0;
    const hasValidrecipient =
      Boolean(inputs?.recipient) && isAddress(inputs?.recipient as string);
    return hasToken && hasChain && hasAmount && hasValidrecipient;
  }, [inputs]);

  const handleTransaction = async () => {
    if (commitLockRef.current) return;
    commitLockRef.current = true;
    const currentRunId = ++runIdRef.current;
    let didEnterExecutingState = false;
    const cleanupSupersededExecution = () => {
      if (!didEnterExecutingState) return;
      setRefreshing(false);
      setIsDialogOpen(false);
      setLastExplorerUrl("");
      stopwatch.stop();
      stopwatch.reset();
      resetSteps();
      dispatch({ type: "setStatus", payload: "idle" });
    };
    try {
      if (
        !inputs?.amount ||
        !inputs?.recipient ||
        !inputs?.chain ||
        !inputs?.token
      ) {
        console.error("Missing required inputs");
        return;
      }
      if (!nexusSDK) {
        const message = "Nexus SDK not initialized";
        setTxError(message);
        onError?.(message);
        return;
      }
      if (allAvailableSourceChainIds.length === 0) {
        const message =
          "No eligible source chains available for the selected token and destination.";
        setTxError(message);
        onError?.(message);
        dispatch({ type: "setStatus", payload: "error" });
        return;
      }

      const parsedAmount = Number(inputs.amount);
      if (!Number.isFinite(parsedAmount) || parsedAmount <= 0) {
        const message = "Enter a valid amount greater than 0.";
        setTxError(message);
        onError?.(message);
        dispatch({ type: "setStatus", payload: "error" });
        return;
      }

      const formattedAmount = nexusSDK.convertTokenReadableAmountToBigInt(
        inputs.amount,
        inputs.token,
        inputs.chain,
      );

      if (configuredMaxAmount) {
        const configuredMaxRaw = nexusSDK.convertTokenReadableAmountToBigInt(
          configuredMaxAmount,
          inputs.token,
          inputs.chain,
        );
        if (formattedAmount > configuredMaxRaw) {
          const message = `Amount exceeds maximum limit of ${configuredMaxAmount} ${inputs.token}.`;
          setTxError(message);
          onError?.(message);
          dispatch({ type: "setStatus", payload: "error" });
          return;
        }
      }

      const maxForCurrentSelection = await getMaxForCurrentSelection();
      if (currentRunId !== runIdRef.current) return;
      if (!maxForCurrentSelection) {
        const message =
          "Unable to determine max bridge amount for selected sources. Please try again.";
        setTxError(message);
        onError?.(message);
        dispatch({ type: "setStatus", payload: "error" });
        return;
      }
      const maxForSelectionRaw = nexusSDK.convertTokenReadableAmountToBigInt(
        maxForCurrentSelection,
        inputs.token,
        inputs.chain,
      );
      if (formattedAmount > maxForSelectionRaw) {
        const message = `Selected sources can provide up to ${maxForCurrentSelection} ${inputs.token}. Reduce amount or enable more sources.`;
        setTxError(message);
        onError?.(message);
        dispatch({ type: "setStatus", payload: "error" });
        return;
      }

      dispatch({ type: "setStatus", payload: "executing" });
      didEnterExecutingState = true;
      setTxError(null);
      onStart?.();
      setLastExplorerUrl("");
      setAppliedSourceSelectionKey(sourceSelectionKey);

      const bridgeTxn = await nexusSDK.bridge(
        {
          token: inputs.token,
          amount: formattedAmount,
          toChainId: inputs.chain,
          recipient: inputs.recipient ?? connectedAddress,
          sourceChains: sourceChainsForSdk,
        },
        {
          onEvent: (event) => {
            if (currentRunId !== runIdRef.current) return;
            if (event.name === NEXUS_EVENTS.STEPS_LIST) {
              const list = Array.isArray(event.args) ? event.args : [];
              onStepsList(list);
            }
            if (event.name === NEXUS_EVENTS.STEP_COMPLETE) {
              if (event.args.type === "INTENT_HASH_SIGNED") {
                stopwatch.start();
              }
              onStepComplete(event.args);
            }
          },
        },
      );
      if (currentRunId !== runIdRef.current) {
        cleanupSupersededExecution();
        return;
      }
      if (!bridgeTxn) {
        throw new Error("Transaction rejected by user");
      }
      setLastExplorerUrl(bridgeTxn.explorerUrl);
      await onSuccess();
    } catch (error) {
      if (currentRunId !== runIdRef.current) {
        cleanupSupersededExecution();
        return;
      }
      const { message, code, context, details } = handleNexusError(error);
      console.error("Fast bridge transaction failed:", {
        code,
        message,
        context,
        details,
      });
      intent.current?.deny();
      intent.current = null;
      allowance.current = null;
      setTxError(message);
      onError?.(message);
      setIsDialogOpen(false);
      // Reset sources selection on failures/rejections so a new attempt starts
      // from a clean "all sources" default.
      setSelectedSourceChains(null);
      setRefreshing(false);
      stopwatch.stop();
      stopwatch.reset();
      resetSteps();
      void fetchBalance();
      dispatch({ type: "setStatus", payload: "error" });
    } finally {
      commitLockRef.current = false;
    }
  };

  const onSuccess = async () => {
    // Close dialog and stop timer on success
    stopwatch.stop();
    dispatch({ type: "setStatus", payload: "success" });
    onComplete?.();
    intent.current = null;
    allowance.current = null;
    dispatch({ type: "resetInputs" });
    setRefreshing(false);
    setSelectedSourceChains(null);
    setAppliedSourceSelectionKey("ALL");
    await fetchBalance();
    notifyIntentHistoryRefresh();
  };

  const filteredBridgableBalance = useMemo(() => {
    return bridgableBalance?.find((bal) =>
      inputs?.token === "USDM"
        ? bal?.symbol === "USDC"
        : bal?.symbol === inputs?.token,
    );
  }, [bridgableBalance, inputs?.token]);

  const availableSources = useMemo(() => {
    const breakdown = filteredBridgableBalance?.breakdown ?? [];
    const destinationChainId = inputs?.chain;
    const nonZero = breakdown.filter((b) => {
      if (Number.parseFloat(b.balance ?? "0") <= 0) return false;
      if (typeof destinationChainId === "number") {
        return b.chain.id !== destinationChainId;
      }
      return true;
    });
    const decimals = filteredBridgableBalance?.decimals;
    if (!nexusSDK || typeof decimals !== "number") {
      return nonZero.sort(
        (a, b) => Number.parseFloat(b.balance) - Number.parseFloat(a.balance),
      );
    }
    return nonZero.sort((a, b) => {
      try {
        const aRaw = nexusSDK.utils.parseUnits(a.balance ?? "0", decimals);
        const bRaw = nexusSDK.utils.parseUnits(b.balance ?? "0", decimals);
        if (aRaw === bRaw) return 0;
        return aRaw > bRaw ? -1 : 1;
      } catch {
        return Number.parseFloat(b.balance) - Number.parseFloat(a.balance);
      }
    });
  }, [
    inputs?.chain,
    filteredBridgableBalance?.breakdown,
    filteredBridgableBalance?.decimals,
    nexusSDK,
  ]);

  const allAvailableSourceChainIds = useMemo(
    () => availableSources.map((s) => s.chain.id),
    [availableSources],
  );

  const effectiveSelectedSourceChains = useMemo(() => {
    if (selectedSourceChains && selectedSourceChains.length > 0) {
      const availableSet = new Set(allAvailableSourceChainIds);
      const filteredSelection = selectedSourceChains.filter((id) =>
        availableSet.has(id),
      );
      if (filteredSelection.length > 0) {
        return filteredSelection;
      }
    }
    return allAvailableSourceChainIds;
  }, [selectedSourceChains, allAvailableSourceChainIds]);

  const sourceChainsForSdk =
    effectiveSelectedSourceChains.length > 0
      ? effectiveSelectedSourceChains
      : undefined;

  const sourceSelectionKey = useMemo(() => {
    if (allAvailableSourceChainIds.length === 0) return "NONE";
    if (!selectedSourceChains || selectedSourceChains.length === 0) {
      return "ALL";
    }
    return [...effectiveSelectedSourceChains].sort((a, b) => a - b).join("|");
  }, [
    allAvailableSourceChainIds.length,
    effectiveSelectedSourceChains,
    selectedSourceChains,
  ]);
  const hasPendingSourceSelectionChanges =
    sourceSelectionKey !== appliedSourceSelectionKey;

  const getMaxForCurrentSelection = useCallback(async () => {
    if (!nexusSDK || !inputs?.token || !inputs?.chain) return undefined;
    const maxBalAvailable = await nexusSDK.calculateMaxForBridge({
      token: inputs.token,
      toChainId: inputs.chain,
      recipient: inputs.recipient,
      sourceChains: sourceChainsForSdk,
    });
    if (!maxBalAvailable?.amount) return "0";
    return clampAmountToMax({
      amount: maxBalAvailable.amount,
      maxAmount: configuredMaxAmount,
      nexusSDK,
      token: inputs.token,
      chainId: inputs.chain,
    });
  }, [
    configuredMaxAmount,
    inputs?.chain,
    inputs?.recipient,
    inputs?.token,
    nexusSDK,
    sourceChainsForSdk,
  ]);

  const toggleSourceChain = useCallback(
    (chainId: number) => {
      setSelectedSourceChains((prev) => {
        if (allAvailableSourceChainIds.length === 0) return prev;
        const current =
          prev && prev.length > 0 ? prev : allAvailableSourceChainIds;
        const next = current.includes(chainId)
          ? current.filter((id) => id !== chainId)
          : [...current, chainId];
        if (next.length === 0) {
          // Always require at least one selected source chain.
          return current;
        }
        const isAllSelected =
          next.length === allAvailableSourceChainIds.length &&
          allAvailableSourceChainIds.every((id) => next.includes(id));
        return isAllSelected ? null : next;
      });
    },
    [allAvailableSourceChainIds],
  );

  const sourceSelection = useMemo(() => {
    const amount = inputs?.amount?.trim() ?? "";
    console.log("BNB", filteredBridgableBalance);
    const decimals =
      inputs?.token === "USDM" || inputs?.chain === SUPPORTED_CHAINS.BNB
        ? 18
        : filteredBridgableBalance?.decimals;
    const selectedChainSet = new Set(effectiveSelectedSourceChains);
    const selectedTotalRaw =
      !nexusSDK || typeof decimals !== "number"
        ? BigInt(0)
        : availableSources.reduce((sum, source) => {
            if (!selectedChainSet.has(source.chain.id)) return sum;
            try {
              return (
                sum + nexusSDK.utils.parseUnits(source.balance ?? "0", decimals)
              );
            } catch {
              return sum;
            }
          }, BigInt(0));
    const selectedTotal =
      !nexusSDK || typeof decimals !== "number"
        ? "0"
        : formatAmountForDisplay(selectedTotalRaw, decimals, nexusSDK);
    const baseSelection = {
      selectedTotal,
      requiredTotal: amount || "0",
      requiredSafetyTotal: amount || "0",
      missingToProceed: "0",
      missingToSafety: "0",
      coverageState: "healthy" as SourceCoverageState,
      coverageToSafetyPercent: 100,
      isBelowRequired: false,
      isBelowSafetyBuffer: false,
    };

    if (!nexusSDK || !inputs?.token || !inputs?.chain || !amount) {
      return baseSelection;
    }

    try {
      const requiredRaw = nexusSDK.convertTokenReadableAmountToBigInt(
        amount,
        inputs.token,
        inputs.chain,
      );
      if (requiredRaw <= BigInt(0)) {
        return baseSelection;
      }

      const requiredSafetyRaw =
        (requiredRaw * SOURCE_SAFETY_MULTIPLIER_NUMERATOR +
          (SOURCE_SAFETY_MULTIPLIER_DENOMINATOR - BigInt(1))) /
        SOURCE_SAFETY_MULTIPLIER_DENOMINATOR;

      const missingToProceedRaw =
        selectedTotalRaw >= requiredRaw
          ? BigInt(0)
          : requiredRaw - selectedTotalRaw;
      const missingToSafetyRaw =
        selectedTotalRaw >= requiredSafetyRaw
          ? BigInt(0)
          : requiredSafetyRaw - selectedTotalRaw;

      const coverageState: SourceCoverageState =
        selectedTotalRaw < requiredRaw
          ? "error"
          : selectedTotalRaw < requiredSafetyRaw
            ? "warning"
            : "healthy";

      const coverageBasisPoints =
        requiredSafetyRaw === BigInt(0)
          ? 10_000
          : selectedTotalRaw >= requiredSafetyRaw
            ? 10_000
            : Number((selectedTotalRaw * BigInt(10_000)) / requiredSafetyRaw);

      return {
        selectedTotal,
        requiredTotal: amount,
        requiredSafetyTotal: formatAmountForDisplay(
          requiredSafetyRaw,
          decimals,
          nexusSDK,
        ),
        missingToProceed: formatAmountForDisplay(
          missingToProceedRaw,
          decimals,
          nexusSDK,
        ),
        missingToSafety: formatAmountForDisplay(
          missingToSafetyRaw,
          decimals,
          nexusSDK,
        ),
        coverageState,
        coverageToSafetyPercent: coverageBasisPoints / 100,
        isBelowRequired: coverageState === "error",
        isBelowSafetyBuffer: coverageState !== "healthy",
      };
    } catch {
      return baseSelection;
    }
  }, [
    filteredBridgableBalance?.decimals,
    nexusSDK,
    inputs?.chain,
    inputs?.amount,
    inputs?.token,
    availableSources,
    effectiveSelectedSourceChains,
  ]);

  const refreshIntent = async (options?: { reportError?: boolean }) => {
    if (!intent.current) return false;
    const activeRunId = runIdRef.current;
    setRefreshing(true);
    try {
      const updated = await intent.current.refresh(sourceChainsForSdk);
      if (activeRunId !== runIdRef.current) return false;
      if (updated) {
        intent.current.intent = updated;
      }
      setAppliedSourceSelectionKey(sourceSelectionKey);
      return true;
    } catch (error) {
      if (activeRunId !== runIdRef.current) return false;
      console.error("Transaction failed:", error);
      if (options?.reportError) {
        const message = "Unable to refresh source selection. Please try again.";
        setTxError(message);
        onError?.(message);
      }
      return false;
    } finally {
      if (activeRunId !== runIdRef.current) return;
      setRefreshing(false);
    }
  };

  const reset = () => {
    runIdRef.current += 1;
    intent.current?.deny();
    intent.current = null;
    allowance.current = null;
    dispatch({ type: "resetInputs" });
    dispatch({ type: "setStatus", payload: "idle" });
    setRefreshing(false);
    setSelectedSourceChains(null);
    setAppliedSourceSelectionKey("ALL");
    setLastExplorerUrl("");
    stopwatch.stop();
    stopwatch.reset();
    resetSteps();
  };

  const startTransaction = () => {
    if (!intent.current) return;
    if (allAvailableSourceChainIds.length === 0) {
      const message =
        "No eligible source chains available for the selected token and destination.";
      setTxError(message);
      onError?.(message);
      return;
    }
    if (sourceSelection.isBelowRequired && inputs?.token) {
      const message = `Selected sources are not enough. Add ${sourceSelection.missingToProceed} ${inputs.token} more to make this transaction.`;
      setTxError(message);
      onError?.(message);
      return;
    }
    if (sourceSelection.coverageState === "warning" && inputs?.token) {
      const message = `Add ${sourceSelection.missingToSafety} ${inputs.token} more in selected sources to reach the 130% safety buffer.`;
      setTxError(message);
      onError?.(message);
      return;
    }
    void (async () => {
      // Ensure the intent reflects the latest selected sources before allowing.
      const refreshed = await refreshIntent({ reportError: true });
      if (!refreshed || !intent.current) return;
      intent.current?.allow();
      setIsDialogOpen(true);
      setTxError(null);
    })();
  };

  const commitAmount = async () => {
    if (intent.current || loading || txError || !areInputsValid) return;
    await handleTransaction();
  };

  usePolling(
    Boolean(intent.current) &&
      !isDialogOpen &&
      !isSourceMenuOpen &&
      !hasPendingSourceSelectionChanges,
    async () => {
      await refreshIntent();
    },
    15000,
  );

  const stopwatch = useStopwatch({ intervalMs: 100 });

  useEffect(() => {
    if (!nexusSDK || !inputs?.token || !inputs?.chain) {
      setSelectedSourcesMaxAmount(null);
      return;
    }
    if (allAvailableSourceChainIds.length === 0) {
      setSelectedSourcesMaxAmount("0");
      return;
    }
    const requestId = ++maxAmountRequestIdRef.current;
    void (async () => {
      try {
        const maxForCurrentSelection = await getMaxForCurrentSelection();
        if (requestId !== maxAmountRequestIdRef.current) return;
        setSelectedSourcesMaxAmount(maxForCurrentSelection ?? "0");
      } catch (error) {
        if (requestId !== maxAmountRequestIdRef.current) return;
        console.error("Unable to calculate max for selected sources:", error);
        setSelectedSourcesMaxAmount("0");
      }
    })();
  }, [
    allAvailableSourceChainIds.length,
    getMaxForCurrentSelection,
    inputs?.chain,
    inputs?.token,
    nexusSDK,
  ]);

  useEffect(() => {
    const previousConnectedAddress = previousConnectedAddressRef.current;
    if (connectedAddress === previousConnectedAddress) return;
    previousConnectedAddressRef.current = connectedAddress;
    if (prefill?.recipient) return;
    if (!inputs?.recipient || inputs.recipient === previousConnectedAddress) {
      dispatch({ type: "setInputs", payload: { recipient: connectedAddress } });
    }
  }, [connectedAddress, inputs?.recipient, prefill?.recipient]);

  useEffect(() => {
    runIdRef.current += 1;
    if (intent.current) {
      intent.current.deny();
      intent.current = null;
    }
    setRefreshing(false);
    setAppliedSourceSelectionKey("ALL");
  }, [inputs]);

  useEffect(() => {
    setSelectedSourceChains(null);
  }, [inputs?.token]);

  useEffect(() => {
    if (!isDialogOpen) {
      stopwatch.stop();
      stopwatch.reset();
    }
  }, [isDialogOpen, stopwatch]);

  useEffect(() => {
    if (txError) {
      setTxError(null);
    }
  }, [inputs]);

  return {
    inputs,
    setInputs,
    timer: stopwatch.seconds,
    setIsDialogOpen,
    setTxError,
    loading,
    refreshing,
    isDialogOpen,
    txError,
    handleTransaction,
    reset,
    filteredBridgableBalance,
    startTransaction,
    commitAmount,
    lastExplorerUrl,
    steps,
    status: state.status,
    availableSources,
    selectedSourceChains: effectiveSelectedSourceChains,
    toggleSourceChain,
    isSourceSelectionInsufficient: sourceSelection.isBelowRequired,
    isSourceSelectionBelowSafetyBuffer: sourceSelection.isBelowSafetyBuffer,
    isSourceSelectionReadyForAccept:
      sourceSelection.coverageState === "healthy",
    sourceCoverageState: sourceSelection.coverageState,
    sourceCoveragePercent: sourceSelection.coverageToSafetyPercent,
    missingToProceed: sourceSelection.missingToProceed,
    missingToSafety: sourceSelection.missingToSafety,
    selectedTotal: sourceSelection.selectedTotal,
    requiredTotal: sourceSelection.requiredTotal,
    requiredSafetyTotal: sourceSelection.requiredSafetyTotal,
    maxAvailableAmount: selectedSourcesMaxAmount ?? undefined,
    isInputsValid: areInputsValid,
  };
};

