"use client";
import React, { type FC } from "react";
import { Card, CardContent } from "../ui/card";
import ChainSelect from "./components/chain-select";
import TokenSelect from "./components/token-select";
import { Button } from "../ui/button";
import { LoaderPinwheel, X } from "lucide-react";
import { useNexus } from "../nexus/NexusProvider";
import AmountInput from "./components/amount-input";
import FeeBreakdown from "./components/fee-breakdown";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../ui/dialog";
import TransactionProgress from "./components/transaction-progress";
import SourceBreakdown from "./components/source-breakdown";
import {
  SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
} from "@avail-project/nexus-core";
import { type Address } from "viem";
import { Skeleton } from "../ui/skeleton";
import RecipientAddress from "./components/recipient-address";
import useTransfer from "./hooks/useTransfer";
import AllowanceModal from "./components/allowance-modal";

interface FastTransferProps {
  prefill?: {
    token: SUPPORTED_TOKENS;
    chainId: SUPPORTED_CHAINS_IDS;
    amount?: string;
    recipient?: Address;
  };
  onComplete?: () => void;
  onStart?: () => void;
  onError?: (message: string) => void;
}

const FastTransfer: FC<FastTransferProps> = ({
  onComplete,
  onStart,
  onError,
  prefill,
}) => {
  const { nexusSDK, intent, unifiedBalance, allowance, network } = useNexus();

  const {
    inputs,
    setInputs,
    timer,
    loading,
    refreshing,
    isDialogOpen,
    txError,
    setTxError,
    handleTransaction,
    reset,
    filteredUnifiedBalance,
    startTransaction,
    setIsDialogOpen,
    commitAmount,
    lastExplorerUrl,
    steps,
    status,
  } = useTransfer({
    prefill,
    network: network ?? "mainnet",
    nexusSDK,
    intent,
    unifiedBalance,
    onComplete,
    onStart,
    onError,
    allowance,
  });
  return (
    <Card className="w-full max-w-xl">
      <CardContent className="flex flex-col gap-y-4 w-full px-2 sm:px-6">
        <ChainSelect
          selectedChain={inputs?.chain}
          handleSelect={(chain) =>
            setInputs({
              ...inputs,
              chain,
            })
          }
          label="To"
          disabled={!!prefill?.chainId}
        />
        <TokenSelect
          selectedChain={inputs?.chain}
          selectedToken={inputs?.token}
          handleTokenSelect={(token) => setInputs({ ...inputs, token })}
          disabled={!!prefill?.token}
        />
        <AmountInput
          amount={inputs?.amount}
          onChange={(amount) => setInputs({ ...inputs, amount })}
          unifiedBalance={filteredUnifiedBalance}
          onCommit={() => void commitAmount()}
          disabled={refreshing || !!prefill?.amount}
          inputs={inputs}
        />
        <RecipientAddress
          address={inputs?.recipient}
          onChange={(address) =>
            setInputs({ ...inputs, recipient: address as `0x${string}` })
          }
          disabled={!!prefill?.recipient}
        />
        {intent?.current?.intent && (
          <>
            <SourceBreakdown
              intent={intent?.current?.intent}
              tokenSymbol={filteredUnifiedBalance?.symbol as SUPPORTED_TOKENS}
              isLoading={refreshing}
              chain={inputs?.chain}
              unifiedBalance={filteredUnifiedBalance}
              requiredAmount={inputs?.amount}
            />
            <div className="w-full flex items-start justify-between gap-x-4">
              <p className="text-base font-semibold">Receipient Receives</p>
              <div className="flex flex-col gap-y-1 min-w-fit">
                {refreshing ? (
                  <Skeleton className="h-5 w-28" />
                ) : (
                  <p className="text-base font-semibold text-right">
                    {inputs?.amount} {filteredUnifiedBalance?.symbol}
                  </p>
                )}
                {refreshing ? (
                  <Skeleton className="h-4 w-36" />
                ) : (
                  <p className="text-sm font-medium text-right">
                    on {intent?.current?.intent?.destination?.chainName}
                  </p>
                )}
              </div>
            </div>
            <FeeBreakdown
              intent={intent?.current?.intent}
              isLoading={refreshing}
            />
          </>
        )}

        {!intent.current && (
          <Button
            onClick={handleTransaction}
            disabled={
              !inputs?.amount ||
              !inputs?.recipient ||
              !inputs?.chain ||
              !inputs?.token ||
              loading
            }
          >
            {loading ? (
              <LoaderPinwheel className="animate-spin size-5" />
            ) : (
              "Transfer to recipient"
            )}
          </Button>
        )}

        <Dialog
          open={isDialogOpen}
          onOpenChange={(open) => {
            if (loading) return;
            setIsDialogOpen(open);
          }}
        >
          {intent.current && !isDialogOpen && (
            <div className="w-full flex items-center gap-x-2 justify-between">
              <Button variant={"destructive"} onClick={reset} className="w-1/2">
                Deny
              </Button>
              <DialogTrigger asChild>
                <Button
                  onClick={startTransaction}
                  className="w-1/2"
                  disabled={refreshing}
                >
                  {refreshing ? "Refreshing..." : "Accept"}
                </Button>
              </DialogTrigger>
            </div>
          )}

          <DialogContent>
            <DialogHeader className="sr-only">
              <DialogTitle>Transaction Progress</DialogTitle>
            </DialogHeader>
            {allowance.current ? (
              <AllowanceModal
                allowance={allowance}
                callback={startTransaction}
                onCloseCallback={reset}
              />
            ) : (
              <TransactionProgress
                timer={timer}
                steps={steps}
                viewIntentUrl={lastExplorerUrl}
                operationType={"transfer"}
                completed={status === "success"}
              />
            )}
          </DialogContent>
        </Dialog>

        {txError && (
          <div className="rounded-md border border-destructive bg-destructive/80 px-3 py-2 text-sm text-destructive-foreground flex items-start justify-between gap-x-3 mt-3 w-full max-w-md">
            <span className="flex-1 w-full truncate">{txError}</span>
            <Button
              type="button"
              size={"icon"}
              variant={"ghost"}
              onClick={() => {
                reset();
                setTxError(null);
              }}
              className="text-destructive-foreground/80 hover:text-destructive-foreground focus:outline-none"
              aria-label="Dismiss error"
            >
              <X className="size-4" />
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default FastTransfer;

import { formatUnits, parseUnits } from "viem";

export const SHORT_CHAIN_NAME: Record<number, string> = {
  1: "Ethereum",
  8453: "Base",
  42161: "Arbitrum",
  10: "Optimism",
  137: "Polygon",
  43114: "Avalanche",
  534352: "Scroll",
  50104: "Sophon",
  8217: "Kaia",
  56: "BNB",
  999: "HyperEVM",
  728126428: "Tron",
  11155111: "Sepolia",
  84532: "Base Sepolia",
  421614: "Arbitrum Sepolia",
  11155420: "Optimism Sepolia",
  80002: "Polygon Amoy",
  10143: "Monad Testnet",
  2494104990: "Tron Shasta",
  567: "Validium Testnet",
} as const;

const DEFAULT_SAFETY_MARGIN = 0.01; // 1%

/**
 * Compute an amount string for fraction buttons (25%, 50%, 75%, 100%).
 *
 * @param balanceStr - user's balance as a human decimal string (e.g. "12.345") OR as base-unit integer string if `balanceIsBaseUnits` true
 * @param fraction - fraction e.g. 0.25, 0.5, 0.75, 1
 * @param decimals - token decimals (6 for USDC/USDT, 18 for ETH)
 * @param safetyMargin - 0.01 for 1% default
 * @param balanceIsBaseUnits - if true, balanceStr is already base units integer string (wei / smallest unit)
 * @returns decimal string clipped to token decimals (rounded down)
 */
export function computeAmountFromFraction(
  balanceStr: string,
  fraction: number,
  decimals: number,
  safetyMargin = DEFAULT_SAFETY_MARGIN,
  balanceIsBaseUnits = false
): string {
  if (!balanceStr) return "0";

  // parse balance into base units (BigInt)
  const balanceUnits: bigint = balanceIsBaseUnits
    ? BigInt(balanceStr)
    : parseUnits(balanceStr, decimals);

  if (balanceUnits === BigInt(0)) return "0";

  // Use an integer precision multiplier to avoid FP issues
  const PREC = 1_000_000; // 1e6 precision for fraction & safety margin
  const safetyMul = BigInt(Math.max(0, Math.floor((1 - safetyMargin) * PREC))); // (1 - safetyMargin) * PREC
  const fractionMul = BigInt(Math.max(0, Math.floor(fraction * PREC))); // fraction * PREC

  // Apply safety margin: floor(balance * (1 - safetyMargin))
  const maxAfterSafety = (balanceUnits * safetyMul) / BigInt(PREC);

  // Apply fraction and floor: floor(maxAfterSafety * fraction)
  let desiredUnits = (maxAfterSafety * fractionMul) / BigInt(PREC);

  // Extra clamp just in case
  if (desiredUnits > balanceUnits) desiredUnits = balanceUnits;
  if (desiredUnits < BigInt(0)) desiredUnits = BigInt(0);

  // format back to human readable decimal string with token decimals (formatUnits truncates/keeps decimals)
  // formatUnits will produce exactly decimals digits if fractional part exists; we'll strip trailing zeros.
  const raw = formatUnits(desiredUnits, decimals);
  // strip trailing zeros and possible trailing dot
  return raw
    .replace(/(\.\d*?[1-9])0+$/u, "$1")
    .replace(/\.0+$/u, "")
    .replace(/^\.$/u, "0");
}

import { useCallback, useRef } from "react";

/**
 * Returns a stable function identity that always calls the latest implementation.
 * Useful when passing callbacks to memoized children without re-creating handlers.
 */
export function useStableCallback<T extends (...args: any[]) => any>(fn: T): T {
  const fnRef = useRef<T>(fn);
  fnRef.current = fn;

  // eslint-disable-next-line react-hooks/exhaustive-deps
  const stable = useCallback(
    ((...args: any[]) => {
      return fnRef.current(...args);
    }) as T,
    []
  );

  return stable;
}

import { NexusError } from "@avail-project/nexus-core";

function handler(err: unknown) {
  if (err instanceof NexusError) {
    return {
      code: err?.code,
      message: err?.message,
      context: err?.data?.context,
      details: err?.data?.details,
    };
  } else {
    console.error("Unexpected error:", err);
    return {
      code: "unexpected_error",
      message: "Oops! Something went wrong. Please try again.",
      context: undefined,
      details: undefined,
    };
  }
}
export function useNexusError() {
  return handler;
}

import { useEffect, useRef } from "react";
import { useStableCallback } from "./useStableCallback";

interface UseIntervalOptions {
  enabled?: boolean;
  immediate?: boolean;
}

/**
 * Declarative setInterval with pause/resume and latest-callback semantics.
 * Pass delay=null to pause.
 */
export function useInterval(
  callback: () => void,
  delay: number | null,
  options: UseIntervalOptions = {}
) {
  const { enabled = true, immediate = false } = options;
  const savedCallback = useStableCallback(callback);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (!enabled || delay == null) return;
    if (immediate) {
      savedCallback();
    }
    intervalRef.current = setInterval(savedCallback, delay);
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, [delay, enabled, immediate, savedCallback]);
}

import { useEffect, useRef, useState } from "react";

interface UseStopwatchOptions {
  running?: boolean;
  intervalMs?: number;
}

/**
 * Simple stopwatch that increments elapsed seconds while running.
 * Designed to replace scattered timer effects.
 */
export function useStopwatch(options: UseStopwatchOptions = {}) {
  const { running = false, intervalMs = 100 } = options;
  const [elapsedSeconds, setElapsedSeconds] = useState(0);
  const timerRef = useRef<ReturnType<typeof setInterval> | null>(null);

  const reset = () => {
    setElapsedSeconds(0);
  };

  const stop = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const start = () => {
    if (timerRef.current) return;
    timerRef.current = setInterval(() => {
      // 1s == 1000ms; we add fractional seconds per tick
      setElapsedSeconds((prev) => prev + intervalMs / 1000);
    }, intervalMs);
  };

  useEffect(() => {
    if (running) {
      start();
    } else {
      stop();
    }
    return stop;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [running, intervalMs]);

  return {
    seconds: elapsedSeconds,
    start,
    stop,
    reset,
    running: Boolean(timerRef.current),
  };
}

import { useRef } from "react";
import { useInterval } from "./useInterval";
import { useStableCallback } from "./useStableCallback";

/**
 * Declarative polling with in-flight protection (no overlap).
 * When enabled becomes true, an immediate run is executed,
 * followed by interval-based runs.
 */
export function usePolling(
  enabled: boolean,
  fn: () => Promise<void> | void,
  intervalMs: number
) {
  const inFlightRef = useRef(false);
  const wrapped = useStableCallback(async () => {
    if (inFlightRef.current) return;
    try {
      inFlightRef.current = true;
      await fn();
    } catch (error) {
      console.error(error);
    } finally {
      inFlightRef.current = false;
    }
  });

  useInterval(wrapped, enabled ? intervalMs : null, {
    enabled,
    immediate: enabled,
  });
}

import { useEffect, useMemo, useRef } from "react";
import { useStableCallback } from "./useStableCallback";

type AnyFn = (...args: any[]) => any;

export interface Debounced<T extends AnyFn> {
  (...args: Parameters<T>): void;
  cancel: () => void;
  flush: () => void;
}

/**
 * Returns a debounced function that delays invoking `fn` until after `delay`
 * milliseconds have elapsed since the last call.
 */
export function useDebouncedCallback<T extends AnyFn>(
  fn: T,
  delay: number
): Debounced<T> {
  const latest = useStableCallback(fn);
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const lastArgsRef = useRef<Parameters<T> | null>(null);

  const cancel = () => {
    if (timerRef.current) {
      clearTimeout(timerRef.current);
      timerRef.current = null;
    }
  };

  const flush = () => {
    if (timerRef.current && lastArgsRef.current) {
      clearTimeout(timerRef.current);
      timerRef.current = null;
      // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
      latest(...lastArgsRef.current);
      lastArgsRef.current = null;
    }
  };

  // cancel when delay changes/unmounts
  useEffect(() => cancel, [delay]);

  return useMemo(() => {
    const debounced = ((...args: Parameters<T>) => {
      lastArgsRef.current = args;
      cancel();
      timerRef.current = setTimeout(() => {
        // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
        latest(...lastArgsRef.current!);
        lastArgsRef.current = null;
        timerRef.current = null;
      }, delay);
    }) as Debounced<T>;
    debounced.cancel = cancel;
    debounced.flush = flush;
    return debounced;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [delay, latest]);
}

import { useEffect, useState } from "react";
import { useDebouncedCallback } from "./useDebouncedCallback";

/**
 * Derives a debounced value from an input value and delay.
 */
export function useDebouncedValue<T>(value: T, delay: number): T {
  const [debounced, setDebounced] = useState<T>(value);
  const setter = useDebouncedCallback((v: T) => setDebounced(v), delay);

  useEffect(() => {
    setter(value);
    return setter.cancel;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [value, delay]);

  return debounced;
}

export type TransactionStatus =
  | "idle"
  | "preview"
  | "awaiting-approval"
  | "executing"
  | "success"
  | "error";

export type GenericStep<TStep> = {
  id: number;
  completed: boolean;
  step: TStep;
};

/**
 * Normalizes a step to a stable key. Prefers typeID, then type, otherwise JSON.
 */
export function getStepKey(step: any): string {
  if (!step) return "";
  if (typeof step.typeID === "string" && step.typeID.length > 0) {
    return step.typeID;
  }
  if (typeof step.type === "string" && step.type.length > 0) {
    return step.type;
  }
  try {
    return JSON.stringify(step);
  } catch {
    return String(step);
  }
}

import type { SwapStepType } from "@avail-project/nexus-core";
import type { GenericStep } from "./types";
import { getStepKey } from "./types";

/**
 * Predefined expected steps for swaps to seed UI before events arrive.
 * Kept here to avoid duplication across exact-in and exact-out hooks.
 */
export const SWAP_EXPECTED_STEPS: SwapStepType[] = [
  { type: "SWAP_START", typeID: "SWAP_START" } as SwapStepType,
  { type: "DETERMINING_SWAP", typeID: "DETERMINING_SWAP" } as SwapStepType,
  {
    type: "CREATE_PERMIT_FOR_SOURCE_SWAP",
    typeID:
      "CREATE_PERMIT_FOR_SOURCE_SWAP" as unknown as SwapStepType["typeID"],
  } as SwapStepType,
  {
    type: "SOURCE_SWAP_BATCH_TX",
    typeID: "SOURCE_SWAP_BATCH_TX",
  } as SwapStepType,
  {
    type: "SOURCE_SWAP_HASH",
    typeID: "SOURCE_SWAP_HASH" as unknown as SwapStepType["typeID"],
  } as SwapStepType,
  { type: "RFF_ID", typeID: "RFF_ID" } as SwapStepType,
  {
    type: "DESTINATION_SWAP_BATCH_TX",
    typeID: "DESTINATION_SWAP_BATCH_TX",
  } as SwapStepType,
  {
    type: "DESTINATION_SWAP_HASH",
    typeID: "DESTINATION_SWAP_HASH" as unknown as SwapStepType["typeID"],
  } as SwapStepType,
  { type: "SWAP_COMPLETE", typeID: "SWAP_COMPLETE" } as SwapStepType,
];

export function seedSteps<T>(expected: T[]): Array<GenericStep<T>> {
  return expected.map((st, index) => ({
    id: index,
    completed: false,
    step: st,
  }));
}

export function computeAllCompleted<T>(steps: Array<GenericStep<T>>): boolean {
  return steps.length > 0 && steps.every((s) => s.completed);
}

/**
 * Replace the current list of steps with a new list, preserving completion
 * for any steps that were already marked completed (matched by key).
 */
export function mergeStepsList<T>(
  prev: Array<GenericStep<T>>,
  list: T[]
): Array<GenericStep<T>> {
  const completedKeys = new Set<string>();
  for (const prevStep of prev) {
    if (prevStep.completed) {
      completedKeys.add(getStepKey(prevStep.step));
    }
  }
  const next: Array<GenericStep<T>> = [];
  for (let index = 0; index < list.length; index++) {
    const step = list[index];
    const key = getStepKey(step);
    next.push({
      id: index,
      completed: completedKeys.has(key),
      step,
    });
  }
  return next;
}

/**
 * Mark a step complete in-place; if the step doesn't yet exist, append it.
 */
export function mergeStepComplete<T>(
  prev: Array<GenericStep<T>>,
  step: T
): Array<GenericStep<T>> {
  const key = getStepKey(step);
  const updated: Array<GenericStep<T>> = [];
  let found = false;
  for (const s of prev) {
    if (getStepKey(s.step) === key) {
      updated.push({ ...s, completed: true, step: { ...s.step, ...step } });
      found = true;
    } else {
      updated.push(s);
    }
  }
  if (!found) {
    updated.push({
      id: updated.length,
      completed: true,
      step,
    });
  }
  return updated;
}

import { useMemo, useRef, useState } from "react";
import type { GenericStep } from "./types";
import { getStepKey } from "./types";
import {
  computeAllCompleted,
  mergeStepComplete,
  mergeStepsList,
  seedSteps,
} from "./steps";

interface UseTransactionStepsOptions<T> {
  expected?: T[];
}

/**
 * Manages transaction steps with utilities to seed from expected steps,
 * replace the list on "steps list" events, and mark individual steps complete.
 */
export function useTransactionSteps<
  T extends { typeID?: string; type?: string }
>(options: UseTransactionStepsOptions<T> = {}) {
  const { expected } = options;
  const [steps, setSteps] = useState<Array<GenericStep<T>>>(() =>
    expected ? seedSteps(expected) : []
  );
  const lastSignatureRef = useRef<string>("");

  const onStepsList = (list: T[]) => {
    const signature = list.map((step) => getStepKey(step)).join("|");
    if (lastSignatureRef.current === signature) {
      setSteps((prev) => mergeStepsList(prev, list));
      return;
    }
    lastSignatureRef.current = signature;
    setSteps((prev) => mergeStepsList(prev, list));
  };

  const onStepComplete = (step: T) => {
    setSteps((prev) => mergeStepComplete(prev, step));
  };

  const seed = (expectedSteps: T[]) => {
    setSteps(seedSteps(expectedSteps));
  };

  const reset = () => {
    setSteps(expected ? seedSteps(expected) : []);
    lastSignatureRef.current = "";
  };

  const allCompleted = useMemo(() => computeAllCompleted(steps), [steps]);

  return {
    steps,
    allCompleted,
    onStepsList,
    onStepComplete,
    seed,
    reset,
  };
}

"use client";
import React, {
  type FC,
  memo,
  RefObject,
  useEffect,
  useMemo,
  useState,
} from "react";
import { Button } from "../../ui/button";
import { Input } from "../../ui/input";
import { Label } from "../../ui/label";
import {
  type AllowanceHookSource,
  CHAIN_METADATA,
  type OnAllowanceHookData,
} from "@avail-project/nexus-core";
import { useNexus } from "../../nexus/NexusProvider";

interface AllowanceModalProps {
  allowance: RefObject<OnAllowanceHookData | null>;
  callback?: () => void;
  onCloseCallback?: () => void;
}

type AllowanceChoice = "min" | "max" | "custom";

interface AllowanceOptionProps {
  index: number;
  name: string;
  choice: AllowanceChoice;
  selectedChoice?: AllowanceChoice;
  onSelect: (index: number, choice: AllowanceChoice) => void;
  title: string;
  description?: string;
  children?: React.ReactNode;
  allowanceValue?: string;
}

const ALLOWANCE_CHOICES: Array<{
  choice: AllowanceChoice;
  title: string;
  description: string;
}> = [
  {
    choice: "min",
    title: "Minimum",
    description: "Grant the lowest allowance required for this action.",
  },
  {
    choice: "max",
    title: "Maximum",
    description: "Approve once and skip future approvals for this token.",
  },
  {
    choice: "custom",
    title: "Custom amount",
    description: "Specify an allowance that fits your threshold.",
  },
];

const AllowanceOption: FC<AllowanceOptionProps> = ({
  index,
  name,
  choice,
  selectedChoice,
  onSelect,
  title,
  description,
  children,
  allowanceValue,
}) => {
  const isActive = selectedChoice === choice;

  return (
    <Label className="block cursor-pointer">
      <input
        type="radio"
        name={name}
        value={choice}
        checked={isActive}
        onChange={() => onSelect(index, choice)}
        className="peer sr-only"
      />
      <div className="flex flex-col gap-3 rounded-xl border border-border/40 bg-background/40 px-4 py-3 transition peer-focus-visible:outline-none peer-focus-visible:ring-2 peer-focus-visible:ring-primary/50 peer-checked:border-primary peer-checked:bg-primary/10 sm:flex-row sm:items-center sm:justify-between">
        <div className="flex items-center justify-between w-full">
          <div>
            <p className="text-sm font-medium leading-tight">{title}</p>
            {description ? (
              <p className="text-xs text-muted-foreground">{description}</p>
            ) : null}
          </div>
          {allowanceValue && (
            <p className="text-sm font-medium leading-tight">
              {allowanceValue}
            </p>
          )}
        </div>

        {children}
      </div>
    </Label>
  );
};

const AllowanceModal: FC<AllowanceModalProps> = ({
  allowance,
  callback,
  onCloseCallback,
}) => {
  const { nexusSDK } = useNexus();
  const [selectedOption, setSelectedOption] = useState<AllowanceChoice[]>([]);
  const [customValues, setCustomValues] = useState<string[]>([]);

  const { sources, allow, deny } = allowance.current ?? {
    sources: [],
    allow: () => {},
    deny: () => {},
  };

  const defaultChoices = useMemo<AllowanceChoice[]>(
    () => Array.from({ length: sources.length }, () => "min"),
    [sources.length]
  );

  const onClose = () => {
    deny();
    allowance.current = null;
    onCloseCallback?.();
  };

  const onApprove = () => {
    const processed = sources.map((_, i) => {
      const opt = selectedOption[i];
      if (opt === "min" || opt === "max") return opt;
      const rawValue = customValues[i]?.trim();
      if (!rawValue) return "min";
      const parsed = Number(rawValue);
      if (!Number.isFinite(parsed) || parsed < 0) return "min";
      return rawValue;
    });
    try {
      allow(processed);
      allowance.current = null;
      callback?.();
    } catch (error) {
      console.error("AllowanceModal onApprove error", error);
      allowance.current = null;
      onCloseCallback?.();
    }
  };

  const handleChoiceChange = (index: number, value: AllowanceChoice) => {
    setSelectedOption((prev) => {
      const next = [...(prev.length ? prev : defaultChoices)];
      next[index] = value;
      return next;
    });
  };

  const formatAmount = (value: string | bigint, source: AllowanceHookSource) =>
    nexusSDK?.utils?.formatTokenBalance(value, {
      symbol: source.token.symbol,
      decimals: source.token.decimals,
    }) ?? "â€”";

  useEffect(() => {
    setSelectedOption(defaultChoices);
  }, [defaultChoices]);

  useEffect(() => {
    setCustomValues(Array.from({ length: sources.length }, () => ""));
  }, [sources.length]);

  return (
    <>
      <div className="space-y-1">
        <p className="text-lg font-semibold tracking-tight">
          Set Token Allowances
        </p>
        <p className="text-sm text-muted-foreground">
          Review every required token and choose the minimum, an unlimited max,
          or define a custom amount before approving.
        </p>
      </div>

      <div className="space-y-4 py-2 max-h-[60vh] overflow-y-auto pr-1">
        {sources?.map((source: AllowanceHookSource, index: number) => (
          <div
            key={`${source.token.symbol}-${index}`}
            className="rounded-2xl border border-border/40 bg-muted/10 p-4 shadow-sm transition hover:border-border"
          >
            <div className="flex flex-wrap items-center justify-between gap-3">
              <div className="flex items-center gap-3">
                <div className="flex h-12 w-12 items-center justify-center rounded-full bg-background shadow-inner">
                  <img
                    src={CHAIN_METADATA[source.chain.id]?.logo}
                    alt={source.chain.name}
                    width={24}
                    height={24}
                    className="rounded-full"
                  />
                </div>
                <div>
                  <p className="text-base font-semibold">
                    {source.token.symbol}
                  </p>
                  <p className="text-xs text-muted-foreground">
                    {source.chain.name}
                  </p>
                </div>
              </div>

              <div className="text-right">
                <p className="text-xs uppercase tracking-wide text-muted-foreground">
                  Current allowance
                </p>
                <p className="text-sm font-semibold">
                  {formatAmount(source.allowance.currentRaw, source)}
                </p>
              </div>
            </div>

            <div className="mt-4 space-y-2">
              {ALLOWANCE_CHOICES.map((choice) => {
                if (choice.choice === "custom") {
                  return (
                    <AllowanceOption
                      key={choice.choice}
                      index={index}
                      name={`allowance-${index}`}
                      choice={choice.choice}
                      selectedChoice={selectedOption[index]}
                      onSelect={handleChoiceChange}
                      title={choice.title}
                      description={choice.description}
                    >
                      <Input
                        type="number"
                        step="any"
                        min="0"
                        inputMode="decimal"
                        placeholder="0.00"
                        value={
                          selectedOption[index] === "custom"
                            ? customValues[index]
                            : ""
                        }
                        onChange={(e) => {
                          const next = [...customValues];
                          next[index] = e.target.value;
                          setCustomValues(next);
                        }}
                        maxLength={source.token.decimals}
                        className="h-9 w-40 rounded-lg border bg-background/80 text-sm disabled:opacity-60"
                        disabled={selectedOption[index] !== "custom"}
                      />
                    </AllowanceOption>
                  );
                }
                return (
                  <AllowanceOption
                    key={choice.choice}
                    index={index}
                    name={`allowance-${index}`}
                    choice={choice.choice}
                    selectedChoice={selectedOption[index]}
                    onSelect={handleChoiceChange}
                    title={choice.title}
                    description={choice.description}
                    allowanceValue={
                      choice.choice === "min"
                        ? formatAmount(source.allowance.minimumRaw, source)
                        : "Unlimited"
                    }
                  />
                );
              })}
            </div>
          </div>
        ))}
      </div>

      <div className="mt-4 flex flex-col-reverse gap-3 sm:flex-row sm:items-center sm:justify-end">
        <Button variant="ghost" onClick={onClose} className="font-semibold">
          Deny
        </Button>
        <Button onClick={onApprove} className="w-full sm:w-auto font-semibold">
          Approve Selected
        </Button>
      </div>
    </>
  );
};

AllowanceModal.displayName = "AllowanceModal";

export default memo(AllowanceModal);

import React, { type FC, Fragment, useEffect, useRef } from "react";
import { Input } from "../../ui/input";
import { Button } from "../../ui/button";
import { type UserAsset } from "@avail-project/nexus-core";
import { useNexus } from "../../nexus/NexusProvider";
import { type FastTransferState } from "../hooks/useTransfer";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";
import { SHORT_CHAIN_NAME } from "../../common";
import { LoaderCircle } from "lucide-react";

interface AmountInputProps {
  amount?: string;
  onChange: (value: string) => void;
  unifiedBalance?: UserAsset;
  onCommit?: (value: string) => void;
  disabled?: boolean;
  inputs: FastTransferState;
}

const AmountInput: FC<AmountInputProps> = ({
  amount,
  onChange,
  unifiedBalance,
  onCommit,
  disabled,
  inputs,
}) => {
  const { nexusSDK, loading } = useNexus();
  const commitTimerRef = useRef<NodeJS.Timeout | null>(null);

  const scheduleCommit = (val: string) => {
    if (!onCommit || disabled) return;
    if (commitTimerRef.current) clearTimeout(commitTimerRef.current);
    commitTimerRef.current = setTimeout(() => {
      onCommit(val);
    }, 800);
  };

  const onMaxClick = async () => {
    if (!nexusSDK || !inputs) return;
    const maxBalAvailable = await nexusSDK?.calculateMaxForBridge({
      token: inputs?.token,
      toChainId: inputs?.chain,
      recipient: inputs?.recipient,
    });
    if (!maxBalAvailable) return;
    onChange(maxBalAvailable.amount);
    onCommit?.(maxBalAvailable.amount);
  };

  useEffect(() => {
    return () => {
      if (commitTimerRef.current) {
        clearTimeout(commitTimerRef.current);
        commitTimerRef.current = null;
      }
    };
  }, []);

  return (
    <div className="flex flex-col gap-y-2 pb-2 w-full">
      <div className="w-full flex sm:flex-row flex-col border border-border rounded-lg gap-y-2">
        <Input
          type="text"
          inputMode="decimal"
          value={amount ?? ""}
          placeholder="Enter Amount"
          onChange={(e) => {
            let next = e.target.value.replaceAll(/[^0-9.]/g, "");
            const parts = next.split(".");
            if (parts.length > 2)
              next = parts[0] + "." + parts.slice(1).join("");
            if (next === ".") next = "0.";
            onChange(next);
            scheduleCommit(next);
          }}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              if (commitTimerRef.current) {
                clearTimeout(commitTimerRef.current);
                commitTimerRef.current = null;
              }
              onCommit?.(amount ?? "");
            }
          }}
          className="w-full border-none bg-transparent rounded-r-none focus-visible:ring-0 focus-visible:ring-offset-0 shadow-none py-0 px-3"
          aria-invalid={Boolean(amount) && Number.isNaN(Number(amount))}
          disabled={disabled || loading}
        />
        <div className="flex items-center justify-end-safe gap-x-2 sm:gap-x-4 w-fit px-2 border-l border-border">
          {unifiedBalance && (
            <p className="text-base font-semibold min-w-max">
              {nexusSDK?.utils?.formatTokenBalance(unifiedBalance?.balance, {
                symbol: unifiedBalance?.symbol,
                decimals: unifiedBalance?.decimals,
              })}
            </p>
          )}
          {loading && !unifiedBalance && (
            <LoaderCircle className="size-4 animate-spin" />
          )}
          <Button
            size={"sm"}
            variant={"ghost"}
            onClick={onMaxClick}
            className="px-0"
            disabled={disabled}
          >
            Max
          </Button>
        </div>
      </div>
      <Accordion type="single" collapsible className="w-full">
        <AccordionItem value="balance-breakdown">
          <AccordionTrigger
            className="w-fit justify-end items-center py-0 gap-x-0.5 cursor-pointer"
            hideChevron={false}
          >
            View Assets
          </AccordionTrigger>
          <AccordionContent className="pb-0">
            <div className="space-y-3 py-2">
              {unifiedBalance?.breakdown.map((chain, index) => {
                if (Number.parseFloat(chain.balance) === 0) return null;
                return (
                  <Fragment key={chain.chain.id}>
                    <div className="flex items-center justify-between px-2 py-1 rounded-md">
                      <div className="flex items-center gap-2">
                        <div className="relative h-6 w-6">
                          <img
                            src={chain?.chain?.logo}
                            alt={chain.chain.name}
                            sizes="100%"
                            className="rounded-full"
                            loading="lazy"
                            decoding="async"
                            width="24"
                            height="24"
                          />
                        </div>
                        <span className="text-sm sm:block hidden">
                          {SHORT_CHAIN_NAME[chain.chain.id]}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-sm font-medium">
                          {nexusSDK?.utils?.formatTokenBalance(chain.balance, {
                            symbol: unifiedBalance?.symbol,
                            decimals: unifiedBalance?.decimals,
                          })}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          ${chain.balanceInFiat.toFixed(2)}
                        </p>
                      </div>
                    </div>
                  </Fragment>
                );
              })}
            </div>
          </AccordionContent>
        </AccordionItem>
      </Accordion>
    </div>
  );
};

export default AmountInput;

import {
  CHAIN_METADATA,
  type ReadableIntent,
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
  type UserAsset,
} from "@avail-project/nexus-core";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../../ui/accordion";
import { Skeleton } from "../../ui/skeleton";
import { useMemo } from "react";
import { useNexus } from "../../nexus/NexusProvider";

interface SourceBreakdownProps {
  intent?: ReadableIntent;
  tokenSymbol: SUPPORTED_TOKENS;
  isLoading?: boolean;
  chain?: SUPPORTED_CHAINS_IDS;
  unifiedBalance?: UserAsset;
  requiredAmount?: string;
}

const SourceBreakdown = ({
  intent,
  tokenSymbol,
  isLoading = false,
  chain,
  unifiedBalance,
  requiredAmount,
}: SourceBreakdownProps) => {
  const { nexusSDK } = useNexus();

  const fundsOnDestination = useMemo(() => {
    if (!unifiedBalance || !chain) return 0;
    return Number.parseFloat(
      unifiedBalance?.breakdown?.find((b) => b.chain?.id === chain)?.balance ??
        "0"
    );
  }, [unifiedBalance, chain]);

  const spendOnSources = useMemo(() => {
    if (!intent || (intent?.sources?.length ?? 0) < 2)
      return `1 asset on 1 chain`;
    return `${intent?.sources?.length} Assets on ${intent?.sources?.length} chains`;
  }, [intent]);

  const amountSpend = useMemo(() => {
    const base = Number(requiredAmount ?? "0");
    if (!intent) return base;
    const fees = Number.parseFloat(intent?.fees?.total ?? "0");
    return base + fees;
  }, [requiredAmount, intent]);

  const displaySources = useMemo(() => {
    if (!intent) {
      return [
        {
          chainID: chain as number,
          chainLogo: chain ? CHAIN_METADATA[chain]?.logo : undefined,
          chainName: chain
            ? CHAIN_METADATA[chain]?.name ?? "Destination"
            : "Destination",
          amount: requiredAmount ?? "0",
          contractAddress: "" as `0x${string}`,
        },
      ];
    }
    const baseSources = intent?.sources ?? [];
    const requiredAmountNumber = Number(requiredAmount ?? "0");
    const destUsed = Math.max(
      Math.min(requiredAmountNumber, fundsOnDestination),
      0
    );
    if (destUsed <= 0 || !chain) {
      return baseSources;
    }
    const allSources = intent?.allSources ?? [];
    const destDetails = allSources?.find?.((s) => s?.chainID === chain);
    const hasDest = baseSources?.some?.((s) => s?.chainID === chain);
    const destSource = {
      chainID: chain,
      chainLogo: destDetails?.chainLogo,
      chainName: destDetails?.chainName ?? "Destination",
      amount: destUsed.toString(),
      contractAddress: (destDetails?.contractAddress ?? "") as `0x${string}`,
    };
    if (hasDest) {
      return baseSources.map((s) =>
        s?.chainID === chain ? { ...s, amount: destSource.amount } : s
      );
    }
    return [...baseSources, destSource];
  }, [intent, requiredAmount, fundsOnDestination, chain]);

  return (
    <Accordion type="single" collapsible className="w-full">
      <AccordionItem value="sources">
        <div className="flex items-start justify-between gap-x-4 w-full">
          {isLoading ? (
            <>
              <div className="flex flex-col items-start gap-y-1 min-w-fit">
                <p className="text-base font-semibold">You Spend</p>
                <Skeleton className="h-4 w-44" />
              </div>
              <div className="flex flex-col items-end gap-y-1 min-w-fit">
                <Skeleton className="h-5 w-24" />
                <div className="w-fit">
                  <Skeleton className="h-4 w-24" />
                </div>
              </div>
            </>
          ) : (
            displaySources && (
              <>
                <div className="flex flex-col items-start gap-y-1 min-w-fit">
                  <p className="text-base font-semibold">You Spend</p>
                  <p className="text-sm font-medium">{spendOnSources}</p>
                </div>

                <div className="flex flex-col items-end gap-y-1 min-w-fit">
                  <p className="text-base font-semibold">
                    {nexusSDK?.utils?.formatTokenBalance(amountSpend, {
                      symbol: tokenSymbol,
                      decimals: intent?.token?.decimals,
                    })}
                  </p>

                  <AccordionTrigger
                    containerClassName="w-fit"
                    className="py-0 items-center gap-x-1"
                    hideChevron={false}
                  >
                    <p className="text-sm font-medium">View Sources</p>
                  </AccordionTrigger>
                </div>
              </>
            )
          )}
        </div>
        {!isLoading && displaySources?.length > 0 && (
          <AccordionContent className="my-4 bg-muted pb-0 px-4 py-2 rounded-lg w-full">
            <div className="flex flex-col items-center gap-y-3">
              {displaySources?.map((source) => (
                <div
                  key={source.chainID}
                  className="flex items-center justify-between w-full gap-x-2"
                >
                  <div className="flex items-center gap-x-2">
                    <img
                      src={source?.chainLogo}
                      alt={source?.chainName}
                      width={20}
                      height={20}
                      className="rounded-full"
                    />
                    <p className="text-sm font-semibold">{source.chainName}</p>
                  </div>

                  <p className="text-sm font-semibold">
                    {nexusSDK?.utils?.formatTokenBalance(source.amount, {
                      symbol: tokenSymbol,
                      decimals: intent?.token?.decimals,
                    })}
                  </p>
                </div>
              ))}
            </div>
          </AccordionContent>
        )}
      </AccordionItem>
    </Accordion>
  );
};

export default SourceBreakdown;

