          <p className="font-semibold">Recipient Address</p>
          <div className="flex items-center gap-x-3 ">
            {address && (
              <p className="font-semibold">
                {nexusSDK?.utils?.truncateAddress(address, 6, 6)}
              </p>
            )}

            <Button
              variant={"ghost"}
              size={"icon"}
              onClick={() => {
                setIsEditing(true);
              }}
              className="px-0 size-6"
              disabled={disabled}
            >
              <Edit className="size-6" />
            </Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RecipientAddress;

import {
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
} from "@avail-project/nexus-core";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../../ui/select";
import { Label } from "../../ui/label";
import { useNexus } from "../../nexus/NexusProvider";
import { useMemo } from "react";

interface TokenSelectProps {
  selectedToken?: SUPPORTED_TOKENS;
  selectedChain: SUPPORTED_CHAINS_IDS;
  handleTokenSelect: (token: SUPPORTED_TOKENS) => void;
  isTestnet?: boolean;
  disabled?: boolean;
  label?: string;
}

const TokenSelect = ({
  selectedToken,
  selectedChain,
  handleTokenSelect,
  isTestnet = false,
  disabled = false,
  label,
}: TokenSelectProps) => {
  const { supportedChainsAndTokens } = useNexus();
  const tokenData = useMemo(() => {
    return supportedChainsAndTokens
      ?.filter((chain) => chain.id === selectedChain)
      .flatMap((chain) => chain.tokens);
  }, [selectedChain, supportedChainsAndTokens]);

  const selectedTokenData = tokenData?.find((token) => {
    return token.symbol === selectedToken;
  });

  return (
    <Select
      value={selectedToken}
      onValueChange={(value) =>
        !disabled && handleTokenSelect(value as SUPPORTED_TOKENS)
      }
    >
      <div className="flex flex-col items-start gap-y-1">
        {label && <Label className="text-sm font-semibold">{label}</Label>}
        <SelectTrigger disabled={disabled} className="w-full">
          <SelectValue placeholder="Select a token" className="w-full">
            {selectedChain && selectedTokenData && (
              <div className="flex items-center gap-x-2 w-full">
                <img
                  src={selectedTokenData?.logo}
                  alt={selectedTokenData?.symbol}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                {selectedToken}
              </div>
            )}
          </SelectValue>
        </SelectTrigger>
      </div>

      <SelectContent>
        <SelectGroup>
          {tokenData?.map((token) => (
            <SelectItem key={token.symbol} value={token.symbol}>
              <div className="flex items-center gap-x-2 my-1">
                <img
                  src={token.logo}
                  alt={token.symbol}
                  width={24}
                  height={24}
                  className="rounded-full"
                />
                <div className="flex flex-col">
                  <span>
                    {isTestnet ? `${token.symbol} (Testnet)` : token.symbol}
                  </span>
                </div>
              </div>
            </SelectItem>
          ))}
        </SelectGroup>
      </SelectContent>
    </Select>
  );
};

export default TokenSelect;

import { Check, Circle, LoaderPinwheel } from "lucide-react";
import React, { type FC, memo, useMemo } from "react";
import {
  type BridgeStepType,
  type SwapStepType,
} from "@avail-project/nexus-core";

type ProgressStep = BridgeStepType | SwapStepType;

interface TransactionProgressProps {
  timer: number;
  steps: Array<{ id: number; completed: boolean; step: ProgressStep }>;
  viewIntentUrl?: string;
  operationType?: string;
  completed?: boolean;
}

export const getOperationText = (type: string) => {
  switch (type) {
    case "transfer":
      return "Transferring";
    case "swap":
      return "Swapping";
    default:
      return "Processing";
  }
};

type DisplayStep = { id: string; label: string; completed: boolean };

const StepList: FC<{ steps: DisplayStep[]; currentIndex: number }> = memo(
  ({ steps, currentIndex }) => {
    return (
      <div className="w-full mt-6 space-y-6">
        {steps.map((s, idx) => {
          const isCompleted = !!s.completed;
          const isCurrent = currentIndex === -1 ? false : idx === currentIndex;

          let rightIcon = <Circle className="size-5 text-muted-foreground" />;
          if (isCompleted) {
            rightIcon = <Check className="size-5 text-green-600" />;
          } else if (isCurrent) {
            rightIcon = (
              <LoaderPinwheel className="size-5 animate-spin text-muted-foreground" />
            );
          }

          return (
            <div
              key={s.id}
              className="flex items-center justify-between w-full"
            >
              <div className="flex items-center gap-x-3">
                <span className="text-base font-semibold">{s.label}</span>
              </div>
              {rightIcon}
            </div>
          );
        })}
      </div>
    );
  }
);

const TransactionProgress: FC<TransactionProgressProps> = ({
  timer,
  steps,
  viewIntentUrl,
  operationType = "transfer",
  completed = false,
}) => {
  const totalSteps = Array.isArray(steps) ? steps.length : 0;
  const completedSteps = Array.isArray(steps)
    ? steps.reduce((acc, s) => acc + (s?.completed ? 1 : 0), 0)
    : 0;
  const rawPercent = totalSteps > 0 ? completedSteps / totalSteps : 0;
  const percent = completed ? 1 : rawPercent;
  const allCompleted = completed || percent >= 1;
  const opText = getOperationText(operationType);
  const headerText = allCompleted
    ? `${opText} Completed`
    : `${opText} In Progress...`;
  const ctaText = allCompleted ? `View Explorer` : "View Intent";

  const { effectiveSteps, currentIndex } = useMemo(() => {
    const milestones = [
      "Intent verified",
      "Collected on sources",
      "Filled on destination",
    ];
    const thresholds = milestones.map(
      (_, idx) => (idx + 1) / milestones.length
    );
    const displaySteps: DisplayStep[] = milestones.map((label, idx) => ({
      id: `M${idx}`,
      label,
      completed: percent >= thresholds[idx],
    }));
    const current = displaySteps.findIndex((st) => !st.completed);
    return { effectiveSteps: displaySteps, currentIndex: current };
  }, [percent, opText, completed]);

  return (
    <div className="w-full flex flex-col items-center">
      <div className="flex flex-col items-center gap-y-3">
        {allCompleted ? (
          <Check className="size-6 text-green-600" />
        ) : (
          <LoaderPinwheel className="size-6 animate-spin" />
        )}
        <p>{headerText}</p>
        <div className="flex items-center justify-center w-full">
          <span className="text-2xl font-semibold font-nexus-primary text-nexus-black">
            {Math.floor(timer)}
          </span>
          <span className="text-base font-semibold font-nexus-primary text-nexus-black">
            .
          </span>
          <span className="text-base font-semibold font-nexus-primary text-nexus-muted-secondary">
            {String(Math.floor((timer % 1) * 1000)).padStart(3, "0")}s
          </span>
        </div>
      </div>

      <StepList steps={effectiveSteps} currentIndex={currentIndex} />

      {viewIntentUrl && (
        <a
          href={viewIntentUrl}
          target="_blank"
          rel="noreferrer"
          className="mt-8 underline font-semibold"
        >
          {ctaText}
        </a>
      )}
    </div>
  );
};

export default TransactionProgress;

import {
  type BridgeStepType,
  NEXUS_EVENTS,
  type NexusNetwork,
  NexusSDK,
  type OnAllowanceHookData,
  type OnIntentHookData,
  SUPPORTED_CHAINS,
  type SUPPORTED_CHAINS_IDS,
  type SUPPORTED_TOKENS,
  TOKEN_METADATA,
  type UserAsset,
} from "@avail-project/nexus-core";
import {
  useEffect,
  useMemo,
  useRef,
  useState,
  useReducer,
  RefObject,
} from "react";
import { type Address, isAddress } from "viem";
import { useNexus } from "../../nexus/NexusProvider";
import {
  useStopwatch,
  usePolling,
  useTransactionSteps,
  type TransactionStatus,
  useNexusError,
} from "../../common";

export interface FastTransferState {
  chain: SUPPORTED_CHAINS_IDS;
  token: SUPPORTED_TOKENS;
  amount?: string;
  recipient: `0x${string}`;
}

interface UseTransferProps {
  network: NexusNetwork;
  nexusSDK: NexusSDK | null;
  intent: RefObject<OnIntentHookData | null>;
  allowance: RefObject<OnAllowanceHookData | null>;
  unifiedBalance: UserAsset[] | null;
  prefill?: {
    token: string;
    chainId: number;
    amount?: string;
    recipient?: Address;
  };
  onComplete?: () => void;
  onStart?: () => void;
  onError?: (message: string) => void;
}

interface TransferState {
  inputs: FastTransferState;
  status: TransactionStatus;
}
type Action =
  | { type: "setInputs"; payload: Partial<FastTransferState> }
  | { type: "resetInputs" }
  | { type: "setStatus"; payload: TransactionStatus };

const buildInitialInputs = (
  network: NexusNetwork,
  prefill?: {
    token: string;
    chainId: number;
    amount?: string;
    recipient?: Address;
  }
): FastTransferState => {
  return {
    chain:
      (prefill?.chainId as SUPPORTED_CHAINS_IDS) ??
      (network === "testnet"
        ? SUPPORTED_CHAINS.SEPOLIA
        : SUPPORTED_CHAINS.ETHEREUM),
    token: (prefill?.token as SUPPORTED_TOKENS) ?? "USDC",
    amount: prefill?.amount ?? undefined,
    recipient: (prefill?.recipient as `0x${string}`) ?? undefined,
  };
};

const useTransfer = ({
  network,
  nexusSDK,
  intent,
  unifiedBalance,
  prefill,
  onComplete,
  onStart,
  onError,
  allowance,
}: UseTransferProps) => {
  const { fetchUnifiedBalance } = useNexus();
  const handleNexusError = useNexusError();
  const initialState: TransferState = {
    inputs: buildInitialInputs(network, prefill),
    status: "idle",
  };
  function reducer(state: TransferState, action: Action): TransferState {
    switch (action.type) {
      case "setInputs":
        return { ...state, inputs: { ...state.inputs, ...action.payload } };
      case "resetInputs":
        return {
          ...state,
          inputs: buildInitialInputs(network, prefill),
        };
      case "setStatus":
        return { ...state, status: action.payload };
      default:
        return state;
    }
  }
  const [state, dispatch] = useReducer(reducer, initialState);
  const inputs = state.inputs;
  const setInputs = (next: FastTransferState | Partial<FastTransferState>) => {
    dispatch({
      type: "setInputs",
      payload: next as Partial<FastTransferState>,
    });
  };

  const loading = state.status === "executing";
  const [refreshing, setRefreshing] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [txError, setTxError] = useState<string | null>(null);
  const [lastExplorerUrl, setLastExplorerUrl] = useState<string>("");
  const commitLockRef = useRef<boolean>(false);
  const {
    steps,
    onStepsList,
    onStepComplete,
    reset: resetSteps,
  } = useTransactionSteps<BridgeStepType>();

  const areInputsValid = useMemo(() => {
    const hasToken = inputs?.token !== undefined && inputs?.token !== null;
    const hasChain = inputs?.chain !== undefined && inputs?.chain !== null;
    const hasAmount = Boolean(inputs?.amount) && Number(inputs?.amount) > 0;
    const hasValidrecipient =
      Boolean(inputs?.recipient) && isAddress(inputs?.recipient as string);
    return hasToken && hasChain && hasAmount && hasValidrecipient;
  }, [inputs]);

  const handleTransaction = async () => {
    if (
      !inputs?.amount ||
      !inputs?.recipient ||
      !inputs?.chain ||
      !inputs?.token
    ) {
      console.error("Missing required inputs");
      return;
    }
    dispatch({ type: "setStatus", payload: "executing" });
    setTxError(null);
    onStart?.();
    try {
      if (!nexusSDK) {
        throw new Error("Nexus SDK not initialized");
      }
      const transferTxn = await nexusSDK.bridgeAndTransfer(
        {
          token: inputs?.token,
          amount: nexusSDK.utils.parseUnits(
            inputs?.amount,
            TOKEN_METADATA[inputs?.token].decimals
          ),
          toChainId: inputs?.chain,
          recipient: inputs?.recipient,
        },
        {
          onEvent: (event) => {
            if (event.name === NEXUS_EVENTS.STEPS_LIST) {
              const list = Array.isArray(event.args) ? event.args : [];
              onStepsList(list);
            }
            if (event.name === NEXUS_EVENTS.STEP_COMPLETE) {
              onStepComplete(event.args);
            }
          },
        }
      );
      if (!transferTxn) {
        throw new Error("Transaction rejected by user");
      }
      if (transferTxn) {
        setLastExplorerUrl(transferTxn.explorerUrl);
        await onSuccess();
      }
    } catch (error) {
      const { message } = handleNexusError(error);
      setTxError(message);
      onError?.(message);
      setIsDialogOpen(false);
      dispatch({ type: "setStatus", payload: "error" });
    }
  };

  const onSuccess = async () => {
    // Close dialog and stop timer on success
    stopwatch.stop();
    dispatch({ type: "setStatus", payload: "success" });
    onComplete?.();
    intent.current = null;
    allowance.current = null;
    dispatch({ type: "resetInputs" });
    setRefreshing(false);
    await fetchUnifiedBalance();
  };

  const filteredUnifiedBalance = useMemo(() => {
    return unifiedBalance?.find((bal) => bal?.symbol === inputs?.token);
  }, [unifiedBalance, inputs?.token]);

  const refreshIntent = async () => {
    setRefreshing(true);
    try {
      await intent.current?.refresh([]);
    } catch (error) {
      console.error("Transaction failed:", error);
    } finally {
      setRefreshing(false);
    }
  };

  const reset = () => {
    intent.current?.deny();
    intent.current = null;
    allowance.current = null;
    dispatch({ type: "resetInputs" });
    dispatch({ type: "setStatus", payload: "idle" });
    setRefreshing(false);
    stopwatch.stop();
    stopwatch.reset();
    resetSteps();
  };

  const startTransaction = () => {
    // Reset timer for a fresh run
    intent.current?.allow();
    setIsDialogOpen(true);
    setTxError(null);
  };

  const commitAmount = async () => {
    if (commitLockRef.current) return;
    if (intent.current || loading || txError || !areInputsValid) return;
    commitLockRef.current = true;
    try {
      await handleTransaction();
    } finally {
      commitLockRef.current = false;
    }
  };

  usePolling(Boolean(intent.current) && !isDialogOpen, refreshIntent, 15000);

  const stopwatch = useStopwatch({ running: isDialogOpen, intervalMs: 100 });

  useEffect(() => {
    if (intent.current) {
      intent.current.deny();
      intent.current = null;
    }
  }, [inputs]);

  useEffect(() => {
    if (!isDialogOpen) {
      stopwatch.stop();
      stopwatch.reset();
    }
  }, [isDialogOpen, stopwatch]);

  useEffect(() => {
    if (txError) {
      setTxError(null);
    }
  }, [inputs]);

  return {
    inputs,
    setInputs,
    timer: stopwatch.seconds,
    setIsDialogOpen,
    setTxError,
    loading,
    refreshing,
    isDialogOpen,
    txError,
    handleTransaction,
    reset,
    filteredUnifiedBalance,
    startTransaction,
    commitAmount,
    lastExplorerUrl,
    steps,
    status: state.status,
  };
};

export default useTransfer;
