import { type FC, useRef } from "react";

interface AmountInputProps {
  amount?: string;
  onChange?: (value: string) => void;
  onFocus?: () => void;
  disabled?: boolean;
}

const AmountInput: FC<AmountInputProps> = ({
  amount,
  onChange,
  onFocus,
  disabled,
}) => {
  const inputRef = useRef<HTMLInputElement>(null);
  const mirrorRef = useRef<HTMLDivElement>(null);

  return (
    <div className="relative flex items-start gap-2 text-4xl font-medium transition-all duration-150 ease-out w-full">
      <div
        ref={mirrorRef}
        className="absolute invisible pointer-events-none text-4xl font-medium whitespace-pre"
        style={{
          fontVariantNumeric: "proportional-nums",
        }}
      >
        {amount || "0"}
      </div>

      <input
        ref={inputRef}
        type="text"
        inputMode="decimal"
        placeholder="0"
        value={amount}
        onChange={(e) => {
          onChange?.(e.target.value);
        }}
        onFocus={onFocus}
        maxLength={18}
        autoFocus
        className="bg-transparent w-full text-foreground text-4xl font-medium outline-none transition-all duration-150 placeholder-muted-foreground proportional-nums disabled:opacity-50"
        disabled={disabled}
      />
      <div className="absolute -inset-1 -z-10 blur-sm pointer-events-none opacity-0" />
    </div>
  );
};

export default AmountInput;

"use client";
import { type FC, useMemo, useState } from "react";
import { Button } from "../../ui/button";
import {
  type SUPPORTED_CHAINS_IDS,
  CHAIN_METADATA,
  type UserAsset,
} from "@avail-project/nexus-core";
import { DESTINATION_SWAP_TOKENS } from "../config/destination";
import { DialogClose } from "../../ui/dialog";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
} from "../../ui/select";
import { Link2, Search, X } from "lucide-react";
import { SHORT_CHAIN_NAME, usdFormatter } from "../../common";
import { TokenIcon } from "./token-icon";
import { useNexus } from "../../nexus/NexusProvider";
import { type DestinationTokenInfo } from "../hooks/useSwaps";

interface DestinationAssetSelectProps {
  swapBalance: UserAsset[] | null;
  onSelect: (
    chainId: SUPPORTED_CHAINS_IDS,
    token: DestinationTokenInfo
  ) => void;
}

const DestinationAssetSelect: FC<DestinationAssetSelectProps> = ({
  swapBalance,
  onSelect,
}) => {
  const { nexusSDK } = useNexus();
  const [tempChain, setTempChain] = useState<number | null>(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Get all tokens from all chains with their chain info
  const allTokens: DestinationTokenInfo[] = useMemo(() => {
    const tokens: DestinationTokenInfo[] = [];
    for (const [chainId, chainTokens] of DESTINATION_SWAP_TOKENS.entries()) {
      for (const token of chainTokens) {
        tokens.push({
          ...token,
          chainId,
        });
      }
    }
    return tokens.map((token) => {
      const balance = swapBalance
        ?.find((t) => t.symbol === token.symbol)
        ?.breakdown?.find((chain) => chain.chain?.id === token.chainId);
      return {
        ...token,
        balance: nexusSDK?.utils?.formatTokenBalance(balance?.balance ?? "0", {
          symbol: token.symbol,
          decimals: balance?.decimals ?? 0,
        }),
        balanceInFiat: usdFormatter.format(balance?.balanceInFiat ?? 0),
      };
    });
  }, [swapBalance]);

  // Only show chains that have tokens
  const chainsWithTokens = useMemo(() => {
    return Array.from(DESTINATION_SWAP_TOKENS.keys());
  }, []);

  // Filter tokens by selected chain and search query
  const displayedTokens: DestinationTokenInfo[] = useMemo(() => {
    let filtered = allTokens;

    // Filter by chain
    if (tempChain) {
      filtered = filtered.filter((t) => t.chainId === tempChain);
    }

    // Filter by search query
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase().trim();
      filtered = filtered.filter(
        (t) =>
          t.symbol.toLowerCase().includes(query) ||
          t.name.toLowerCase().includes(query) ||
          t.tokenAddress.toLowerCase().includes(query)
      );
    }

    return filtered;
  }, [tempChain, allTokens, searchQuery]);

  const handlePick = (tok: DestinationTokenInfo) => {
    const chainId = tempChain ?? tok.chainId;
    if (!chainId) return;
    onSelect(chainId as SUPPORTED_CHAINS_IDS, tok);
  };

  return (
    <div className="w-full">
      <div className="w-full flex flex-col gap-y-3">
        <Select
          value={tempChain ? CHAIN_METADATA[tempChain].name : ""}
          onValueChange={(value) => {
            const matchedChain = chainsWithTokens.find(
              (chain) => String(chain) === value
            );
            if (matchedChain) {
              setTempChain(matchedChain);
            }
          }}
        >
          <div className="flex bg-input/30 w-full px-2 py-1.5">
            <div className="flex items-center gap-x-2 w-full justify-between">
              <Search className="size-5 opacity-65" />
              <input
                placeholder="Search tokens..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="bg-transparent w-full text-foreground text-base font-medium outline-none transition-all duration-150 placeholder-muted-foreground proportional-nums disabled:opacity-80"
              />
              {searchQuery && (
                <button
                  type="button"
                  onClick={() => setSearchQuery("")}
                  className="p-0.5 hover:bg-muted rounded-full transition-colors"
                >
                  <X className="size-4 opacity-65" />
                </button>
              )}
            </div>
            <SelectTrigger className="rounded-full border-none cursor-pointer bg-transparent!">
              {tempChain ? (
                <img
                  src={CHAIN_METADATA[tempChain].logo}
                  alt={CHAIN_METADATA[tempChain].name}
                  width={24}
                  height={24}
                  className="rounded-full size-6"
                />
              ) : (
                <div className="size-8 rounded-full flex items-center justify-center border border-border">
                  <Link2 className="size-4" />
                </div>
              )}
            </SelectTrigger>
          </div>
          <SelectContent>
            <SelectGroup>
              {chainsWithTokens.map((c) => (
                <SelectItem key={c} value={String(c)}>
                  <div className="flex items-center justify-between gap-x-2">
                    <img
                      src={CHAIN_METADATA[c].logo}
                      alt={CHAIN_METADATA[c].name}
                      width={20}
                      height={20}
                      className="rounded-full size-5"
                    />
                    <span className="text-sm">{CHAIN_METADATA[c].name}</span>
                  </div>
                </SelectItem>
              ))}
            </SelectGroup>
          </SelectContent>
        </Select>
        <p className="text-sm">
          {tempChain
            ? `Tokens on ${SHORT_CHAIN_NAME[tempChain]}`
            : "All Tokens"}
        </p>
        <div className="rounded-md px-2 max-h-80 overflow-y-auto no-scrollbar">
          <div className="flex flex-col items-center sm:items-start gap-y-4 w-full no-scrollbar">
            {displayedTokens.length > 0 ? (
              displayedTokens.map((t) => (
                <DialogClose asChild key={`${t.tokenAddress}-${t.chainId}`}>
                  <Button
                    variant={"ghost"}
                    onClick={() => handlePick(t)}
                    className="flex items-center justify-between gap-x-2 p-2 rounded w-full h-max"
                  >
                    <div className="flex items-center gap-x-2">
                      {t.symbol ? (
                        <div className="relative">
                          <TokenIcon
                            symbol={t.symbol}
                            tokenLogo={t.logo}
                            chainLogo={CHAIN_METADATA[t.chainId ?? 1]?.logo}
                            className="border border-border rounded-full"
                          />
                        </div>
                      ) : null}
                    </div>
                    <div className="flex flex-col items-end">
                      <p className="text-base text-foreground">{t.balance}</p>
                      <p className="text-sm text-muted-foreground">
                        {t.balanceInFiat}
                      </p>
                    </div>
                  </Button>
                </DialogClose>
              ))
            ) : (
              <p className="text-xs text-muted-foreground">No Tokens Found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DestinationAssetSelect;

import React, { type RefObject } from "react";
import { Label } from "../../ui/label";
import { cn } from "@/lib/utils";
import {
  CHAIN_METADATA,
  type OnSwapIntentHookData,
  type SUPPORTED_CHAINS_IDS,
  type UserAsset,
} from "@avail-project/nexus-core";
import {
  type SwapInputs,
  type SwapMode,
  type TransactionStatus,
} from "../hooks/useSwaps";
import { Button } from "../../ui/button";
import { TokenIcon } from "./token-icon";
import AmountInput from "./amount-input";
import { usdFormatter } from "../../common";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../../ui/dialog";
import { ChevronDown } from "lucide-react";
import DestinationAssetSelect from "./destination-asset-select";

interface DestinationContainerProps {
  destinationHovered: boolean;
  inputs: SwapInputs;
  swapIntent: RefObject<OnSwapIntentHookData | null>;
  destinationBalance?: UserAsset["breakdown"][0];
  swapBalance: UserAsset[] | null;
  availableStables: UserAsset[];
  swapMode: SwapMode;
  status: TransactionStatus;
  setInputs: (inputs: Partial<SwapInputs>) => void;
  setSwapMode: (mode: SwapMode) => void;
  getFiatValue: (amount: number, token: string) => number;
  formatBalance: (
    balance?: string | number,
    symbol?: string,
    decimals?: number
  ) => string | undefined;
}

const DestinationContainer: React.FC<DestinationContainerProps> = ({
  destinationHovered,
  inputs,
  swapIntent,
  destinationBalance,
  swapBalance,
  availableStables,
  swapMode,
  status,
  setInputs,
  setSwapMode,
  getFiatValue,
  formatBalance,
}) => {
  // In exactOut mode, show user's input; in exactIn mode, show calculated destination
  const displayedAmount =
    swapMode === "exactOut"
      ? inputs.toAmount ?? ""
      : formatBalance(
          swapIntent?.current?.intent?.destination?.amount,
          swapIntent?.current?.intent?.destination?.token?.symbol,
          swapIntent?.current?.intent?.destination?.token?.decimals
        ) ?? "";

  return (
    <div className="bg-background rounded-xl flex flex-col items-center w-full gap-y-4">
      <div className="w-full flex items-center justify-between">
        <Label className="text-lg font-medium text-foreground">Buy</Label>
        {(!inputs?.toToken || !inputs?.toChainID) && (
          <div
            className={cn(
              "flex transition-all duration-150 ease-out w-full justify-end gap-x-2",
              destinationHovered
                ? "opacity-100 translate-y-0"
                : "opacity-0 -translate-y-1"
            )}
          >
            {availableStables.map((token) => (
              <Button
                key={token?.symbol}
                size={"icon-sm"}
                variant={"secondary"}
                onClick={() => {
                  if (!token) return;
                  setInputs({
                    ...inputs,
                    toToken: {
                      tokenAddress: token.breakdown[0].contractAddress,
                      decimals: token.decimals,
                      logo: token.icon ?? "",
                      name: token.symbol,
                      symbol: token.symbol,
                    },
                    toChainID: token.breakdown[0].chain
                      .id as SUPPORTED_CHAINS_IDS,
                  });
                }}
                className="bg-transparent rounded-full hover:-translate-y-1 hover:object-scale-down"
              >
                <TokenIcon
                  symbol={token?.symbol}
                  tokenLogo={token?.icon}
                  chainLogo={token.breakdown[0].chain.logo}
                  size="sm"
                />
              </Button>
            ))}
          </div>
        )}
      </div>
      <div className="flex items-center justify-between gap-x-4 w-full">
        <AmountInput
          amount={displayedAmount}
          onChange={(val) => {
            setSwapMode("exactOut");
            setInputs({ toAmount: val, fromAmount: undefined });
          }}
          disabled={status === "simulating" || status === "swapping"}
        />
        <Dialog>
          <DialogTrigger asChild>
            <div className="flex items-center gap-x-3 bg-card/50 hover:bg-card-foreground/10 border border-border min-w-max rounded-full p-1 cursor-pointer transition-colors">
              <TokenIcon
                symbol={inputs?.toToken?.symbol}
                tokenLogo={inputs?.toToken?.logo}
                chainLogo={
                  inputs?.toChainID
                    ? CHAIN_METADATA[inputs?.toChainID]?.logo
                    : undefined
                }
                size="lg"
              />
              <span className="font-medium">{inputs?.toToken?.symbol}</span>
              <ChevronDown size={16} className="mr-1" />
            </div>
          </DialogTrigger>
          <DialogContent className="max-w-md!">
            <DialogHeader>
              <DialogTitle>Select Destination</DialogTitle>
            </DialogHeader>
            <DestinationAssetSelect
              swapBalance={swapBalance}
              onSelect={(toChainID, toToken) =>
                setInputs({ ...inputs, toChainID, toToken })
              }
            />
          </DialogContent>
        </Dialog>
      </div>
      <div className="flex items-center justify-between gap-x-4 w-full">
        {swapIntent?.current?.intent?.destination?.amount && inputs?.toToken ? (
          <span className="text-sm text-accent-foreground">
            {usdFormatter.format(
              getFiatValue(
                Number.parseFloat(
                  swapIntent?.current?.intent?.destination?.amount
                ),
                inputs.toToken?.symbol
              )
            )}
          </span>
        ) : (
          <span className="h-5" />
        )}
        {inputs?.toToken ? (
          <span className="text-sm text-muted-foreground">
            {formatBalance(
              destinationBalance?.balance,
              inputs?.toToken?.symbol,
              destinationBalance?.decimals
            ) ?? ""}
          </span>
        ) : (
          <span className="h-5" />
        )}
      </div>
    </div>
  );
};

export default DestinationContainer;

"use client";
import { type FC, useMemo, useState } from "react";
import { Button } from "../../ui/button";
import { useNexus } from "../../nexus/NexusProvider";
import {
  type UserAsset,
  type SUPPORTED_CHAINS_IDS,
  CHAIN_METADATA,
} from "@avail-project/nexus-core";
import { TOKEN_IMAGES } from "../config/destination";
import { Link2, Loader2, Search, X } from "lucide-react";
import { DialogClose } from "../../ui/dialog";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
} from "../../ui/select";
import { TokenIcon } from "./token-icon";
import { SHORT_CHAIN_NAME } from "../../common";
import { type SourceTokenInfo } from "../hooks/useSwaps";

interface SourceAssetSelectProps {
  onSelect: (chainId: SUPPORTED_CHAINS_IDS, token: SourceTokenInfo) => void;
  swapBalance: UserAsset[] | null;
}

const SourceAssetSelect: FC<SourceAssetSelectProps> = ({
  onSelect,
  swapBalance,
}) => {
  const { swapSupportedChainsAndTokens, nexusSDK } = useNexus();
  const [tempChain, setTempChain] = useState<{
    id: number;
    logo: string;
    name: string;
  } | null>(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Get all tokens from swapBalance with their chain info
  const allTokens: SourceTokenInfo[] = useMemo(() => {
    if (!swapBalance) return [];
    const tokens: SourceTokenInfo[] = [];

    for (const asset of swapBalance) {
      if (!asset?.breakdown?.length) continue;
      for (const breakdown of asset.breakdown) {
        if (Number.parseFloat(breakdown.balance) <= 0) continue;

        tokens.push({
          contractAddress: breakdown.contractAddress,
          decimals: breakdown.decimals ?? asset.decimals,
          logo: TOKEN_IMAGES[asset.symbol] ?? "",
          name: asset.symbol,
          symbol: asset.symbol,
          balance: nexusSDK?.utils?.formatTokenBalance(breakdown?.balance, {
            symbol: asset.symbol,
            decimals: asset.decimals,
          }),
          balanceInFiat: `$${breakdown.balanceInFiat}`,
          chainId: breakdown.chain?.id,
        });
      }
    }

    // Dedupe by contractAddress + chainId
    const unique = new Map<string, SourceTokenInfo>();
    for (const t of tokens) {
      const key = `${t.contractAddress.toLowerCase()}-${t.chainId}`;
      unique.set(key, t);
    }
    return Array.from(unique.values());
  }, [swapBalance, nexusSDK]);

  // Only show chains that have tokens with balance
  const chainsWithTokens = useMemo(() => {
    if (!swapSupportedChainsAndTokens || !allTokens.length) return [];
    const chainIdsWithTokens = new Set(allTokens.map((t) => t.chainId));
    return swapSupportedChainsAndTokens.filter((c) =>
      chainIdsWithTokens.has(c.id)
    );
  }, [swapSupportedChainsAndTokens, allTokens]);

  // Filter tokens by selected chain and search query
  const displayedTokens: SourceTokenInfo[] = useMemo(() => {
    let filtered = allTokens;

    // Filter by chain
    if (tempChain) {
      filtered = filtered.filter((t) => t.chainId === tempChain.id);
    }

    // Filter by search query
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase().trim();
      filtered = filtered.filter(
        (t) =>
          t.symbol.toLowerCase().includes(query) ||
          t.name.toLowerCase().includes(query) ||
          t.contractAddress.toLowerCase().includes(query)
      );
    }

    return filtered;
  }, [tempChain, allTokens, searchQuery]);

  const handlePick = (tok: SourceTokenInfo) => {
    const chainId = tempChain?.id ?? tok.chainId;
    if (!chainId) return;
    onSelect(chainId as SUPPORTED_CHAINS_IDS, tok);
  };

  if (!swapBalance)
    return (
      <div className="flex flex-col items-center justify-center gap-y-3">
        <p className="text-sm text-muted-foreground">
          Fetching swappable assets
        </p>
        <Loader2 className="animate-spin size-5" />
      </div>
    );

  return (
    <div className="w-full flex flex-col gap-y-3">
      <Select
        value={tempChain?.name}
        onValueChange={(value) => {
          const matchedChain = chainsWithTokens.find(
            (chain) => chain.name === value
          );
          if (matchedChain) {
            setTempChain(matchedChain);
          }
        }}
      >
        <div className="flex bg-input/30 w-full px-2 py-1.5">
          <div className="flex items-center gap-x-2 w-full justify-between">
            <Search className="size-5 opacity-65" />
            <input
              placeholder="Search tokens..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="bg-transparent w-full text-foreground text-base font-medium outline-none transition-all duration-150 placeholder-muted-foreground proportional-nums disabled:opacity-80"
            />
            {searchQuery && (
              <button
                type="button"
                onClick={() => setSearchQuery("")}
                className="p-0.5 hover:bg-muted rounded-full transition-colors"
              >
                <X className="size-4 opacity-65" />
              </button>
            )}
          </div>
          <SelectTrigger className="rounded-full border-none cursor-pointer bg-transparent!">
            {tempChain ? (
              <img
                src={tempChain?.logo}
                alt={tempChain?.name}
                width={24}
                height={24}
                className="rounded-full size-6"
              />
            ) : (
              <div className="size-8 rounded-full flex items-center justify-center border border-border">
                <Link2 className="size-4" />
              </div>
            )}
          </SelectTrigger>
        </div>
        <SelectContent>
          <SelectGroup>
            {chainsWithTokens.map((c) => (
              <SelectItem key={c.id} value={c.name}>
                <div className="flex items-center justify-between gap-x-2">
                  <img
                    src={c.logo}
                    alt={c.name}
                    width={20}
                    height={20}
                    className="rounded-full size-5"
                  />
                  <span className="text-sm">{c.name}</span>
                </div>
              </SelectItem>
            ))}
          </SelectGroup>
        </SelectContent>
      </Select>
      <p className="text-sm">
        {tempChain?.id
          ? `Tokens on ${SHORT_CHAIN_NAME[tempChain.id]}`
          : "All Tokens"}
      </p>
      <div className="rounded-md max-h-80 overflow-y-auto no-scrollbar">
        <div className="flex flex-col items-center sm:items-start gap-y-4 w-full no-scrollbar">
          {displayedTokens.length > 0 ? (
            displayedTokens.map((t) => (
              <DialogClose asChild key={`${t.contractAddress}-${t.chainId}`}>
                <Button
                  variant={"ghost"}
                  onClick={() => handlePick(t)}
                  className="flex items-center justify-between gap-x-2 p-2 rounded w-full h-max"
                >
                  <div className="flex  items-center gap-x-4">
                    {t.symbol ? (
                      <div className="relative">
                        <TokenIcon
                          symbol={t.symbol}
                          tokenLogo={t.logo}
                          chainLogo={CHAIN_METADATA[t.chainId ?? 1]?.logo}
                          className="border border-border rounded-full"
                        />
                      </div>
                    ) : null}
                  </div>
                  <div className="flex flex-col items-end">
                    <p className="text-base text-foreground">{t.balance}</p>
                    <p className="text-sm text-muted-foreground">
                      {t.balanceInFiat}
                    </p>
                  </div>
                </Button>
              </DialogClose>
            ))
          ) : (
            <p className="text-xs text-muted-foreground">No Tokens Found</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default SourceAssetSelect;

import React, { type RefObject } from "react";
import { Label } from "../../ui/label";
import { cn } from "@/lib/utils";
import { Button } from "../../ui/button";
import {
  type TransactionStatus,
  type SwapInputs,
  type SwapMode,
} from "../hooks/useSwaps";
import { computeAmountFromFraction, usdFormatter } from "../../common";
import {
  CHAIN_METADATA,
  type UserAsset,
  type OnSwapIntentHookData,
} from "@avail-project/nexus-core";
import AmountInput from "./amount-input";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../../ui/dialog";
import { TokenIcon } from "./token-icon";
import { ChevronDown } from "lucide-react";
import SourceAssetSelect from "./source-asset-select";

const RANGE_OPTIONS = [
  {
    label: "25%",
    value: 0.25,
  },
  {
    label: "50%",
    value: 0.5,
  },
  {
    label: "75%",
    value: 0.75,
  },
  {
    label: "MAX",
    value: 1,
  },
];

const SAFETY_MARGIN = 0.05;

interface SourceContainerProps {
  status: TransactionStatus;
  sourceHovered: boolean;
  inputs: SwapInputs;
  availableBalance?: UserAsset["breakdown"][0];
  swapBalance: UserAsset[] | null;
  swapMode: SwapMode;
  swapIntent: RefObject<OnSwapIntentHookData | null>;
  setInputs: (inputs: Partial<SwapInputs>) => void;
  setSwapMode: (mode: SwapMode) => void;
  setTxError: (error: string | null) => void;
  getFiatValue: (amount: number, token: string) => number;
  formatBalance: (
    balance?: string | number,
    symbol?: string,
    decimals?: number
  ) => string | undefined;
}

const SourceContainer: React.FC<SourceContainerProps> = ({
  status,
  sourceHovered,
  inputs,
  availableBalance,
  swapBalance,
  swapMode,
  swapIntent,
  setInputs,
  setSwapMode,
  setTxError,
  getFiatValue,
  formatBalance,
}) => {
  const isExactOut = swapMode === "exactOut";

  // In exactIn mode, show user's input; in exactOut mode, show calculated source from intent
  const displayedAmount =
    swapMode === "exactIn"
      ? inputs.fromAmount ?? ""
      : formatBalance(
          swapIntent?.current?.intent?.sources?.[0]?.amount,
          swapIntent?.current?.intent?.sources?.[0]?.token?.symbol,
          swapIntent?.current?.intent?.sources?.[0]?.token?.decimals
        ) ?? "";

  const isDisabled =
    isExactOut || status === "simulating" || status === "swapping";

  // Render exact-out read-only view
  if (isExactOut) {
    return (
      <div className="bg-background rounded-xl flex flex-col items-center w-full gap-y-4 h-[134px]">
        <div className="w-full flex items-center justify-between">
          <Label className="text-lg font-medium text-foreground">Sell</Label>
        </div>
        <div className="flex items-center justify-center w-full py-4">
          <p className="text-sm text-muted-foreground text-center">
            Enter destination token, chain and amount.
            <br />
            We&apos;ll calculate the best sources for you.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-background rounded-xl flex flex-col items-center w-full gap-y-4">
      <div className="w-full flex items-center justify-between">
        <Label className="text-lg font-medium text-foreground">Sell</Label>
        <div
          className={cn(
            "flex transition-all duration-150 ease-out w-full justify-end gap-x-2",
            sourceHovered
              ? "opacity-100 translate-y-0"
              : "opacity-0 -translate-y-1"
          )}
        >
          {RANGE_OPTIONS.map((option) => (
            <Button
              key={option.label}
              size={"icon-sm"}
              variant={"secondary"}
              disabled={!inputs.fromChainID || !inputs.fromToken}
              onClick={() => {
                if (!inputs.fromToken) return 0;
                setSwapMode("exactIn");
                const amount = computeAmountFromFraction(
                  availableBalance?.balance ?? "0",
                  option.value,
                  inputs?.fromToken?.decimals,
                  SAFETY_MARGIN
                );
                setInputs({ fromAmount: amount, toAmount: undefined });
              }}
              className="px-5 py-1.5 rounded-full hover:-translate-y-1 hover:object-scale-down"
            >
              <p className="text-xs font-medium">{option.label}</p>
            </Button>
          ))}
        </div>
      </div>
      <div className="flex items-center justify-between gap-x-4 w-full">
        <AmountInput
          amount={displayedAmount}
          onChange={(val) => {
            if (availableBalance?.balance) {
              const parsedAvailableBalance = Number.parseFloat(
                availableBalance?.balance
              );
              const parsedVal = Number.parseFloat(val);
              if (parsedVal > parsedAvailableBalance) {
                setTxError("Insufficient Balance");
                return;
              }
            }
            setSwapMode("exactIn");
            setInputs({ fromAmount: val, toAmount: undefined });
          }}
          disabled={isDisabled}
        />

        <Dialog>
          <DialogTrigger asChild>
            <div
              className={cn(
                "flex items-center gap-x-3 bg-card/50 hover:bg-card-foreground/10 border border-border min-w-max rounded-full p-1 cursor-pointer transition-colors",
                isDisabled ? "pointer-events-none select-none opacity-50" : ""
              )}
            >
              <TokenIcon
                symbol={inputs?.fromToken?.symbol}
                tokenLogo={inputs?.fromToken?.logo}
                chainLogo={
                  inputs?.fromChainID
                    ? CHAIN_METADATA[inputs?.fromChainID]?.logo
                    : undefined
                }
                size="lg"
              />
              <span className="font-medium">{inputs?.fromToken?.symbol}</span>
              <ChevronDown size={16} className="mr-1" />
            </div>
          </DialogTrigger>
          <DialogContent className="max-w-md!">
            <DialogHeader>
              <DialogTitle>Select a Token</DialogTitle>
            </DialogHeader>
            <SourceAssetSelect
              onSelect={(fromChainID, fromToken) =>
                setInputs({ ...inputs, fromChainID, fromToken })
              }
              swapBalance={swapBalance}
            />
          </DialogContent>
        </Dialog>
      </div>
      <div className="flex items-center justify-between gap-x-4 w-full">
        {inputs.fromAmount && inputs?.fromToken ? (
          <span className="text-sm text-accent-foreground">
            {usdFormatter.format(
              getFiatValue(
                Number.parseFloat(inputs.fromAmount),
                inputs.fromToken?.symbol
              )
            )}
          </span>
        ) : (
          <span className="h-5" />
        )}

        <span className="text-sm text-muted-foreground">
          {formatBalance(
            availableBalance?.balance ?? "0",
            inputs?.fromToken?.symbol,
            availableBalance?.decimals
          )}
        </span>
      </div>
    </div>
  );
};

