"use client";
import React from "react";
import SwapExactIn from "./exact-in/exact-in";

interface SwapsProps {
  exactIn?: boolean;
  onComplete?: (amount?: string) => void;
  prefill?: {
    fromChainID?: number;
    fromToken?: string;
    fromAmount?: string;
    toChainID?: number;
    toToken?: string;
  };
}

const Swaps = ({ exactIn = true, onComplete, prefill }: SwapsProps) => {
  if (exactIn) return <SwapExactIn onComplete={onComplete} prefill={prefill} />;
  return (
    <div className="text-sm text-muted-foreground">Exact Out coming soon</div>
  );
};

export default Swaps;

"use client";
import React, { FC } from "react";
import { Card, CardContent } from "../../ui/card";
import { Button } from "../../ui/button";
import AmountInput from "./components/amount-input";
import SourceAssetSelect from "./components/source-asset-select";
import DestinationAssetSelect from "./components/destination-asset-select";
import { useNexus } from "../../nexus/NexusProvider";
import useExactIn from "./hooks/useExactIn";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "../../ui/dialog";
import TransactionProgress from "./components/transaction-progress";
import SwapSourceBreakdown from "./components/swap-source-breakdown";
import { Input } from "../../ui/input";
import { Label } from "../../ui/label";
import { LoaderPinwheel } from "lucide-react";

interface SwapExactInProps {
  onComplete?: (amount?: string) => void;
  prefill?: {
    fromChainID?: number;
    fromToken?: string;
    fromAmount?: string;
    toChainID?: number;
    toToken?: string;
  };
}

const SwapExactIn: FC<SwapExactInProps> = ({ onComplete, prefill }) => {
  const { nexusSDK, swapIntent, setSwapIntent } = useNexus();
  const {
    inputs,
    setInputs,
    loading,
    isDialogOpen,
    setIsDialogOpen,
    txError,
    timer,
    steps,
    sourceExplorerUrl,
    destinationExplorerUrl,
    handleSwap,
    reset,
  } = useExactIn({ nexusSDK, onComplete, prefill });

  return (
    <Card className="w-full max-w-xl">
      <CardContent className="flex flex-col gap-y-4 w-full">
        <div className="flex items-center justify-between">
          <p className="text-base font-semibold">Swap</p>
          <span className="text-xs text-muted-foreground">Exact In</span>
        </div>

        <SourceAssetSelect
          selectedChain={inputs.fromChainID}
          selectedToken={inputs.fromToken}
          onSelect={(fromChainID, fromToken) =>
            setInputs({ ...inputs, fromChainID, fromToken })
          }
          disabled={Boolean(prefill?.fromChainID && prefill?.fromToken)}
        />
        <div className="flex flex-col gap-y-2">
          <label className="text-sm font-medium" htmlFor="swap-amount">
            Amount
          </label>
          <AmountInput
            amount={inputs.fromAmount}
            onChange={(val) => setInputs({ ...inputs, fromAmount: val })}
            symbol={inputs.fromToken?.symbol}
            disabled={Boolean(prefill?.fromAmount)}
          />
        </div>

        <DestinationAssetSelect
          selectedChain={inputs.toChainID}
          selectedToken={inputs.toToken}
          onSelect={(toChainID, toToken) =>
            setInputs({ ...inputs, toChainID, toToken })
          }
          disabled={Boolean(prefill?.toChainID && prefill?.toToken)}
        />

        {!swapIntent && (
          <Button onClick={handleSwap} disabled={loading}>
            {loading ? (
              <LoaderPinwheel className="animate-spin size-5" />
            ) : (
              "Swap"
            )}
          </Button>
        )}

        {swapIntent && (
          <>
            <div className="flex flex-col gap-y-2">
              <Label className="text-sm font-medium" htmlFor="swap-receive">
                You receive (estimated)
              </Label>
              <Input
                id="swap-receive"
                disabled
                className="w-full border rounded px-3 py-2 text-sm bg-muted cursor-not-allowed"
                value={`${swapIntent.intent.destination.amount}`}
                placeholder="—"
                readOnly
              />
            </div>
            <div className="w-full flex items-center gap-x-2 justify-between">
              <Button
                variant={"destructive"}
                onClick={() => {
                  swapIntent.deny();
                  setSwapIntent(null);
                  reset();
                }}
                className="w-1/2"
              >
                Deny
              </Button>
              <Button
                onClick={() => {
                  swapIntent.allow();
                  setIsDialogOpen(true);
                }}
                className="w-1/2"
              >
                Accept
              </Button>
            </div>
            <SwapSourceBreakdown intent={swapIntent.intent} />
          </>
        )}

        <Dialog
          open={isDialogOpen}
          onOpenChange={(o) => {
            if (loading) return;
            if (!o) reset();
            setIsDialogOpen(o);
          }}
        >
          <DialogContent>
            <DialogHeader className="sr-only">
              <DialogTitle>Swap Progress</DialogTitle>
            </DialogHeader>
            <TransactionProgress
              timer={timer}
              steps={steps}
              sourceExplorerUrl={sourceExplorerUrl}
              destinationExplorerUrl={destinationExplorerUrl}
            />
          </DialogContent>
        </Dialog>

        {txError && (
          <div className="rounded-md border border-destructive bg-destructive/80 px-3 py-2 text-sm text-destructive-foreground flex items-start justify-between gap-x-3 mt-3 w-full max-w-lg">
            <span className="flex-1 max-w-md truncate">{txError}</span>
            <Button
              type="button"
              size={"icon"}
              variant={"ghost"}
              onClick={() => {
                reset();
              }}
              className="text-destructive-foreground/80 hover:text-destructive-foreground focus:outline-none"
              aria-label="Dismiss error"
            >
              ×
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default SwapExactIn;

"use client";
import React, { FC, useMemo, useState } from "react";
import { Button } from "../../../ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../../../ui/dialog";
import { useNexus } from "../../../nexus/NexusProvider";
import {
  CHAIN_METADATA,
  type SUPPORTED_CHAINS_IDS,
} from "@avail-project/nexus-core";
import { TOKEN_IMAGES } from "../config/destination";

type SourceTokenInfo = {
  contractAddress: `0x${string}`;
  decimals: number;
  logo: string;
  name: string;
  symbol: string;
};

interface SourceAssetSelectProps {
  selectedChain?: SUPPORTED_CHAINS_IDS;
  selectedToken?: SourceTokenInfo;
  onSelect: (chainId: SUPPORTED_CHAINS_IDS, token: SourceTokenInfo) => void;
  disabled?: boolean;
  label?: string;
}

const SourceAssetSelect: FC<SourceAssetSelectProps> = ({
  selectedChain,
  selectedToken,
  onSelect,
  disabled,
  label = "From",
}) => {
  const { swapSupportedChainsAndTokens, unifiedBalance } = useNexus();
  const [open, setOpen] = useState(false);
  const [tempChain, setTempChain] = useState<number | null>(null);
  // const [tokensForTempChain, setTokensForTempChain] = useState<
  //   | {
  //       amount: string;
  //       chainID: number;
  //       decimals: number;
  //       symbol: string;
  //       tokenAddress: `0x${string}`;
  //       value: number;
  //       logo: string;
  //     }[]
  //   | undefined
  // >(undefined);

  const chains = swapSupportedChainsAndTokens ?? [];

  // const fetchTokens = useCallback(async () => {
  //   const balances = await nexusSDK?.getBalancesForSwap();
  //   console.log("swap balances HELLO", balances);
  //   setTokensForTempChain(balances);
  // }, [tempChain]);

  // useEffect(() => {
  //   if (!tempChain) return;
  //   fetchTokens();
  // }, [tempChain]);

  const tokensForTempChain: SourceTokenInfo[] = useMemo(() => {
    if (!tempChain) return [] as SourceTokenInfo[];
    const balances = unifiedBalance ?? [];
    const tokens: SourceTokenInfo[] = [];

    for (const asset of balances) {
      if (!asset?.breakdown?.length) continue;
      const breakdownForChain = asset.breakdown.find(
        (b) => b.chain?.id === tempChain && Number.parseFloat(b.balance) > 0
      );
      if (!breakdownForChain) continue;

      tokens.push({
        contractAddress: breakdownForChain.contractAddress,
        decimals: breakdownForChain.decimals ?? asset.decimals,
        logo: TOKEN_IMAGES[asset.symbol] ?? "",
        name: asset.symbol,
        symbol: asset.symbol,
      });
    }

    const unique = new Map<string, SourceTokenInfo>();
    for (const t of tokens) {
      unique.set(t.contractAddress.toLowerCase(), t);
    }
    return Array.from(unique.values());
  }, [tempChain, unifiedBalance]);

  const handlePick = (tok: SourceTokenInfo) => {
    if (!tempChain) return;
    onSelect(tempChain as SUPPORTED_CHAINS_IDS, tok);
    setOpen(false);
  };

  return (
    <div className="w-full">
      <p className="text-sm font-semibold mb-1">{label}</p>
      <Dialog open={open} onOpenChange={(o) => !disabled && setOpen(o)}>
        <DialogTrigger asChild>
          <Button
            variant="outline"
            className="w-full justify-between"
            disabled={disabled}
          >
            {selectedChain && selectedToken ? (
              <div className="flex items-center gap-x-3">
                <div className="relative">
                  <img
                    src={TOKEN_IMAGES[selectedToken?.symbol]}
                    alt={selectedToken.symbol}
                    width={24}
                    height={24}
                    className="rounded-full"
                  />
                  <img
                    src={CHAIN_METADATA[selectedChain].logo}
                    alt={CHAIN_METADATA[selectedChain].name}
                    width={16}
                    height={16}
                    className="rounded-full absolute bottom-0 right-0 translate-x-1/3 translate-y-1/6"
                  />
                </div>

                <span className="text-sm font-medium">
                  {selectedToken.symbol} on {CHAIN_METADATA[selectedChain].name}
                </span>
              </div>
            ) : (
              <span className="text-sm text-muted-foreground">
                Select chain and token
              </span>
            )}
          </Button>
        </DialogTrigger>
        <DialogContent className="max-w-3xl">
          <DialogHeader>
            <DialogTitle>Select source asset</DialogTitle>
          </DialogHeader>
          <div className="grid grid-cols-2 gap-4">
            <div className="border rounded-md p-2 max-h-80 overflow-y-auto">
              <p className="text-xs font-medium mb-2">Chains</p>
              <div className="flex flex-col items-start gap-y-1 w-full">
                {chains.map((c: any) => (
                  <Button
                    key={c.id}
                    variant={"ghost"}
                    onClick={() => setTempChain(c.id)}
                    className={`flex items-center justify-start w-full gap-x-2 p-2 rounded hover:bg-muted ${
                      tempChain === c.id ? "bg-muted" : ""
                    }`}
                  >
                    <img
                      src={c.logo}
                      alt={c.name}
                      width={18}
                      height={18}
                      className="rounded-full"
                    />
                    <span className="text-sm">{c.name}</span>
                  </Button>
                ))}
              </div>
            </div>
            <div className="border rounded-md p-2 max-h-80 overflow-y-auto">
              <p className="text-xs font-medium mb-2">Tokens</p>
              <div className="flex flex-col items-start gap-y-1 w-full">
                {tempChain && tokensForTempChain ? (
                  tokensForTempChain?.map((t) => (
                    <Button
                      key={t.symbol}
                      variant={"ghost"}
                      onClick={() => handlePick(t)}
                      className="flex items-center justify-start w-full gap-x-2 p-2 rounded hover:bg-muted"
                    >
                      {t.symbol ? (
                        <img
                          src={TOKEN_IMAGES[t.symbol]}
                          alt={t.symbol}
                          width={18}
                          height={18}
                          className="rounded-full"
                        />
                      ) : null}
                      <span className="text-sm">{t.symbol}</span>
                    </Button>
                  ))
                ) : (
                  <p className="text-xs text-muted-foreground">
                    Select a chain
                  </p>
                )}
              </div>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default SourceAssetSelect;

"use client";
import React, { FC, useMemo, useState } from "react";
import { Button } from "../../../ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../../../ui/dialog";
import {
  type SUPPORTED_CHAINS_IDS,
  CHAIN_METADATA,
} from "@avail-project/nexus-core";
import { DESTINATION_SWAP_TOKENS, TOKEN_IMAGES } from "../config/destination";

type DestinationTokenInfo = {
  tokenAddress: `0x${string}`;
  decimals: number;
  logo: string;
  name: string;
  symbol: string;
};

interface DestinationAssetSelectProps {
  selectedChain?: SUPPORTED_CHAINS_IDS;
  selectedToken?: DestinationTokenInfo;
  onSelect: (
    chainId: SUPPORTED_CHAINS_IDS,
    token: DestinationTokenInfo
  ) => void;
  disabled?: boolean;
  label?: string;
}

const DestinationAssetSelect: FC<DestinationAssetSelectProps> = ({
  selectedChain,
  selectedToken,
  onSelect,
  disabled,
  label = "To",
}) => {
  const [open, setOpen] = useState(false);
  const [tempChain, setTempChain] = useState<number | null>(null);

  const chains = useMemo(() => Array.from(DESTINATION_SWAP_TOKENS.keys()), []);
  const tokensForTempChain: DestinationTokenInfo[] = useMemo(() => {
    if (!tempChain) return [] as DestinationTokenInfo[];
    return DESTINATION_SWAP_TOKENS.get(tempChain) ?? [];
  }, [tempChain]);

  const handlePick = (tok: DestinationTokenInfo) => {
    if (!tempChain) return;
    onSelect(tempChain as SUPPORTED_CHAINS_IDS, tok);
    setOpen(false);
  };

  return (
    <div className="w-full">
      <p className="text-sm font-semibold mb-1">{label}</p>
      <Dialog open={open} onOpenChange={(o) => !disabled && setOpen(o)}>
        <DialogTrigger asChild>
          <Button
            variant="outline"
            className="w-full justify-between"
            disabled={disabled}
          >
            {selectedChain && selectedToken ? (
              <div className="flex items-center gap-x-3">
                <div className="relative">
                  <img
                    src={TOKEN_IMAGES[selectedToken?.symbol]}
                    alt={selectedToken.symbol}
                    width={24}
                    height={24}
                    className="rounded-full"
                  />
                  <img
                    src={CHAIN_METADATA[selectedChain].logo}
                    alt={CHAIN_METADATA[selectedChain].name}
                    width={16}
                    height={16}
                    className="rounded-full absolute bottom-0 right-0 translate-x-1/3 translate-y-1/6"
                  />
                </div>

                <span className="text-sm font-medium">
                  {selectedToken.symbol} on {CHAIN_METADATA[selectedChain].name}
                </span>
              </div>
            ) : (
              <span className="text-sm text-muted-foreground">
                Select chain and token
              </span>
            )}
          </Button>
        </DialogTrigger>
        <DialogContent className="max-w-3xl">
          <DialogHeader>
            <DialogTitle>Select destination asset</DialogTitle>
          </DialogHeader>
          <div className="grid grid-cols-2 gap-4">
            <div className="border rounded-md p-2 max-h-80 overflow-y-auto">
              <p className="text-xs font-medium mb-2">Chains</p>
              <div className="flex flex-col gap-y-1">
                {chains.map((id) => {
                  const meta = CHAIN_METADATA[id as SUPPORTED_CHAINS_IDS];
                  return (
                    <Button
                      key={id}
                      type="button"
                      variant="ghost"
                      onClick={() => setTempChain(id)}
                      className={`flex items-center justify-start gap-x-2 p-2 rounded hover:bg-muted w-full ${
                        tempChain === id ? "bg-muted" : ""
                      }`}
                    >
                      <img
                        src={meta.logo}
                        alt={meta.name}
                        width={18}
                        height={18}
                        className="rounded-full"
                      />
                      <span className="text-sm">{meta.name}</span>
                    </Button>
                  );
                })}
              </div>
            </div>
            <div className="border rounded-md p-2 max-h-80 overflow-y-auto">
              <p className="text-xs font-medium mb-2">Tokens</p>
              <div className="flex flex-col gap-y-1">
                {tempChain ? (
                  tokensForTempChain.map((t) => (
                    <Button
                      key={t.symbol}
                      type="button"
                      variant="ghost"
                      onClick={() => handlePick(t)}
                      className="flex items-center justify-start gap-x-2 p-2 rounded hover:bg-muted w-full"
                    >
                      {t.logo ? (
                        <img
                          src={t.logo}
                          alt={t.symbol}
                          width={18}
                          height={18}
                          className="rounded-full"
                        />
                      ) : null}
                      <span className="text-sm">{t.symbol}</span>
                    </Button>
                  ))
                ) : (
                  <p className="text-xs text-muted-foreground">
                    Select a chain
                  </p>
                )}
              </div>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default DestinationAssetSelect;

import React, { FC, useEffect, useMemo, useRef } from "react";
import { Input } from "../../../ui/input";
import { useNexus } from "@/registry/nexus-elements/nexus/NexusProvider";

interface AmountInputProps {
  amount?: string;
  onChange: (value: string) => void;
  onCommit?: (value: string) => void;
  disabled?: boolean;
  symbol?: string;
}

const AmountInput: FC<AmountInputProps> = ({
  amount,
  onChange,
  onCommit,
  disabled,
  symbol,
}) => {
  const { unifiedBalance } = useNexus();
  const commitTimerRef = useRef<NodeJS.Timeout | null>(null);

  const scheduleCommit = (val: string) => {
    if (!onCommit || disabled) return;
    if (commitTimerRef.current) clearTimeout(commitTimerRef.current);
    commitTimerRef.current = setTimeout(() => {
      onCommit(val);
    }, 600);
  };

  const tokenBalance = useMemo(() => {
    const token = unifiedBalance?.find((u) => u.symbol === symbol);
    if (!token) return "";
    return Number.parseFloat(token.balance).toFixed(6);
  }, [unifiedBalance, symbol]);

  useEffect(() => {
    return () => {
      if (commitTimerRef.current) {
        clearTimeout(commitTimerRef.current);
        commitTimerRef.current = null;
      }
    };
  }, []);

  return (
    <div className="w-full flex border border-border rounded-lg gap-y-2">
      <Input
        type="text"
        inputMode="decimal"
        value={amount ?? ""}
        placeholder={`Enter Amount ${symbol ?? ""}`}
        onChange={(e) => {
          let next = e.target.value.replaceAll(/[^0-9.]/g, "");
          const parts = next.split(".");
          if (parts.length > 2) next = parts[0] + "." + parts.slice(1).join("");
          if (next === ".") next = "0.";
          onChange(next);
          scheduleCommit(next);
        }}
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            if (commitTimerRef.current) {
              clearTimeout(commitTimerRef.current);
              commitTimerRef.current = null;
            }
            onCommit?.(amount ?? "");
          }
        }}
        className="w-full border-none focus-visible:ring-0 focus-visible:ring-offset-0 shadow-none py-0 px-3"
        aria-invalid={Boolean(amount) && Number.isNaN(Number(amount))}
        disabled={disabled}
      />
      {symbol && (
        <div className="flex items-center justify-end-safe gap-x-4 w-max px-2 border-l border-border">
          <p className="text-sm font-semibold w-max">{tokenBalance}</p>
          <p className="text-sm font-semibold">{symbol}</p>
        </div>
      )}
    </div>
  );
};

export default AmountInput;

import React from "react";
import { type OnSwapIntentHook } from "@avail-project/nexus-core";

type SwapIntent = Parameters<OnSwapIntentHook>[0]["intent"];

interface SwapSourceBreakdownProps {
  intent: SwapIntent;
  isLoading?: boolean;
}

const SwapSourceBreakdown: React.FC<SwapSourceBreakdownProps> = ({
  intent,
  isLoading,
}) => {
  if (!intent) return null;
  return (
    <div className="w-full border rounded-md p-3">
      <p className="text-sm font-semibold mb-2">Route</p>
      <div className="flex flex-col gap-y-2">
        <div className="flex items-start justify-between gap-x-4">
          <p className="text-sm font-medium">Destination</p>
          <div className="text-right">
            <p className="text-sm font-semibold">
              {intent.destination.amount} {intent.destination.token.symbol}
            </p>
            <p className="text-xs text-muted-foreground">
              on {intent.destination.chain.name}
            </p>
          </div>
        </div>
        <div className="flex flex-col gap-y-1">
          <p className="text-sm font-medium">Sources</p>
          {intent.sources.map((s) => (
            <div key={s.chain.id} className="flex items-center justify-between">
              <span className="text-xs text-muted-foreground">
                {s.chain.name}
              </span>
              <span className="text-sm font-medium">
                {s.amount} {s.token.symbol}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SwapSourceBreakdown;

import { useEffect, useMemo, useRef, useState } from "react";
import {
  NexusSDK,
  SUPPORTED_CHAINS,
  type SUPPORTED_CHAINS_IDS,
  type ExactInSwapInput,
  type SwapResult,
  NEXUS_EVENTS,
  type SwapStepType,
  parseUnits,
} from "@avail-project/nexus-core";
import { type Address } from "viem";
import { useNexus } from "../../../nexus/NexusProvider";
import { DESTINATION_SWAP_TOKENS, TOKEN_IMAGES } from "../config/destination";

type SourceTokenInfo = {
  contractAddress: `0x${string}`;
  decimals: number;
  logo: string;
  name: string;
  symbol: string;
};
type DestinationTokenInfo = {
  tokenAddress: `0x${string}`;
  decimals: number;
  logo: string;
  name: string;
  symbol: string;
};

interface SwapInputs {
  fromChainID: SUPPORTED_CHAINS_IDS;
  fromToken?: SourceTokenInfo;
  fromAmount?: string;
  toChainID: SUPPORTED_CHAINS_IDS;
  toToken?: DestinationTokenInfo;
}

interface UseExactInProps {
  nexusSDK: NexusSDK | null;
  address?: Address;
  onComplete?: (amount?: string) => void;
  prefill?: {
    fromChainID?: number;
    fromToken?: string;
    fromAmount?: string;
    toChainID?: number;
    toToken?: string;
  };
}

const EXPECTED_SWAP_STEPS: SwapStepType[] = [
  { type: "SWAP_START", typeID: "SWAP_START" } as SwapStepType,
  { type: "DETERMINING_SWAP", typeID: "DETERMINING_SWAP" } as SwapStepType,
  {
    type: "CREATE_PERMIT_FOR_SOURCE_SWAP",
    typeID:
      "CREATE_PERMIT_FOR_SOURCE_SWAP" as unknown as SwapStepType["typeID"],
  } as SwapStepType,
  {
    type: "SOURCE_SWAP_BATCH_TX",
    typeID: "SOURCE_SWAP_BATCH_TX",
  } as SwapStepType,
  {
    type: "SOURCE_SWAP_HASH",
    typeID: "SOURCE_SWAP_HASH" as unknown as SwapStepType["typeID"],
  } as SwapStepType,
  { type: "RFF_ID", typeID: "RFF_ID" } as SwapStepType,
  {
    type: "DESTINATION_SWAP_BATCH_TX",
    typeID: "DESTINATION_SWAP_BATCH_TX",
  } as SwapStepType,
  {
    type: "DESTINATION_SWAP_HASH",
    typeID: "DESTINATION_SWAP_HASH" as unknown as SwapStepType["typeID"],
  } as SwapStepType,
  { type: "SWAP_COMPLETE", typeID: "SWAP_COMPLETE" } as SwapStepType,
];

const useExactIn = ({ nexusSDK, onComplete, prefill }: UseExactInProps) => {
  const {
    handleNexusError,
    swapIntent,
    setSwapIntent,
    fetchUnifiedBalance,
    unifiedBalance,
  } = useNexus();

  const [inputs, setInputs] = useState<SwapInputs>({
    fromChainID:
      (prefill?.fromChainID as SUPPORTED_CHAINS_IDS) ?? SUPPORTED_CHAINS.BASE,
    toChainID:
      (prefill?.toChainID as SUPPORTED_CHAINS_IDS) ?? SUPPORTED_CHAINS.OPTIMISM,
    fromAmount: prefill?.fromAmount ?? undefined,
  });
  const [timer, setTimer] = useState(0);
  const [loading, setLoading] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [txError, setTxError] = useState<string | null>(null);
  const [explorerUrl, setExplorerUrl] = useState<string>("");
  const [sourceExplorerUrl, setSourceExplorerUrl] = useState<string>("");
  const [destinationExplorerUrl, setDestinationExplorerUrl] =
    useState<string>("");
  const [steps, setSteps] = useState<
    Array<{ id: number; completed: boolean; step: SwapStepType }>
  >([]);

  const timerRef = useRef<NodeJS.Timeout | null>(null);

  const areInputsValid = useMemo(() => {
    return (
      inputs.fromChainID !== undefined &&
      inputs.toChainID !== undefined &&
      inputs.fromToken &&
      inputs.toToken &&
      inputs.fromAmount &&
      Number(inputs.fromAmount) > 0
    );
  }, [inputs]);

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const startTimer = () => {
    if (!timerRef.current) {
      setTimer(0);
      timerRef.current = setInterval(() => setTimer((prev) => prev + 0.1), 100);
    }
  };

  const handleSwap = async () => {
    if (
      !nexusSDK ||
      !areInputsValid ||
      !inputs.fromToken ||
      !inputs.toToken ||
      !inputs.fromAmount
    )
      return;
    setLoading(true);
    setTxError(null);
    // Seed steps with expected swap steps, all incomplete initially
    setSteps(
      EXPECTED_SWAP_STEPS.map((st, idx) => ({
        id: idx,
        completed: false,
        step: st,
      }))
    );
    startTimer();
    try {
      const amountWei = parseUnits(
        inputs.fromAmount,
        inputs.fromToken.decimals
      );
      const swapInput: ExactInSwapInput = {
        from: [
          {
            chainId: inputs.fromChainID,
            amount: amountWei,
            tokenAddress: inputs.fromToken.contractAddress,
          },
        ],
        toChainId: inputs.toChainID,
        toTokenAddress: inputs.toToken.tokenAddress,
      };

      console.log("swapInput", swapInput);

      const result: SwapResult = await nexusSDK.swapWithExactIn(swapInput, {
        onEvent: (event) => {
          console.log("swap event", event);
          if (event.name === NEXUS_EVENTS.SWAP_STEP_COMPLETE) {
            const step = event.args as SwapStepType & {
              explorerURL?: string;
              completed?: boolean;
            };

            // Capture explorer URLs for source and destination txs
            if (step?.type === "SOURCE_SWAP_HASH" && step.explorerURL) {
              setSourceExplorerUrl(step.explorerURL);
            }
            if (step?.type === "DESTINATION_SWAP_HASH" && step.explorerURL) {
              setDestinationExplorerUrl(step.explorerURL);
            }

            // Update progress by matching on step.type (ignore dynamic typeID suffixes)
            setSteps((prev) => {
              const existingIndex = prev.findIndex(
                (s) => s.step?.type === step?.type
              );
              if (existingIndex !== -1) {
                const updated = [...prev];
                const wasCompleted = updated[existingIndex].completed;
                const nowCompleted = step.completed === true;
                updated[existingIndex] = {
                  ...updated[existingIndex],
                  completed: wasCompleted || nowCompleted,
                  step: { ...updated[existingIndex].step, ...step },
                };
                return updated;
              }
              // Step not in expected list; append it
              return [
                ...prev,
                {
                  id: prev.length,
                  completed: step.completed === true,
                  step: step as SwapStepType,
                },
              ];
            });
          }
        },
      });

      if (!result?.success) {
        throw new Error(result?.error || "Swap failed");
      }
      setExplorerUrl(result.result.explorerURL);
      onComplete?.(swapIntent?.intent?.destination?.amount);
      await fetchUnifiedBalance();
    } catch (error) {
      const { message } = handleNexusError(error);
      setTxError(message);
      setSwapIntent(null);
      setIsDialogOpen(false);
    } finally {
      setLoading(false);
      stopTimer();
    }
  };

  const reset = () => {
    setInputs({
      fromChainID:
        (prefill?.fromChainID as SUPPORTED_CHAINS_IDS) ?? SUPPORTED_CHAINS.BASE,
      toChainID:
        (prefill?.toChainID as SUPPORTED_CHAINS_IDS) ??
        SUPPORTED_CHAINS.OPTIMISM,
      fromAmount: prefill?.fromAmount ?? undefined,
      fromToken: undefined,
      toToken: undefined,
    });
    setIsDialogOpen(false);
    setTxError(null);
    setSteps([]);
    setExplorerUrl("");
    setSourceExplorerUrl("");
    setDestinationExplorerUrl("");
    setLoading(false);
    stopTimer();
  };

  useEffect(() => {
    return () => {
      stopTimer();
    };
  }, []);

  // Apply token selections when prefilled and data is available
  useEffect(() => {
    // Source token prefill
    if (
      prefill?.fromToken &&
      inputs.fromChainID !== undefined &&
      !inputs.fromToken &&
      unifiedBalance
    ) {
      const targetAddr = prefill.fromToken.toLowerCase();
      let matchedAsset: (typeof unifiedBalance)[number] | undefined;
      let matchedBreakdown:
        | {
            chain?: { id?: number };
            contractAddress: `0x${string}`;
            decimals?: number;
          }
        | undefined;
      for (const a of unifiedBalance) {
        const candidate = a.breakdown?.find(
          (b) =>
            b.contractAddress?.toLowerCase() === targetAddr &&
            (b.chain?.id as number | undefined) === inputs.fromChainID
        );
        if (candidate) {
          matchedAsset = a;
          matchedBreakdown = candidate as any;
          break;
        }
      }
      if (matchedAsset && matchedBreakdown) {
        setInputs((prev) => ({
          ...prev,
          fromToken: {
            contractAddress: matchedBreakdown.contractAddress,
            decimals: matchedBreakdown.decimals ?? matchedAsset.decimals,
            logo: TOKEN_IMAGES[matchedAsset.symbol] ?? "",
            name: matchedAsset.symbol,
            symbol: matchedAsset.symbol,
          },
        }));
      }
    }
    // Destination token prefill
    if (prefill?.toToken && inputs.toChainID !== undefined && !inputs.toToken) {
      const list = DESTINATION_SWAP_TOKENS.get(inputs.toChainID);
      const targetAddr = prefill.toToken.toLowerCase();
      const tok = list?.find(
        (t) => t.tokenAddress?.toLowerCase() === targetAddr
      );
      if (tok) {
        setInputs((prev) => ({
          ...prev,
          toToken: tok,
        }));
      }
    }
  }, [
    prefill,
    unifiedBalance,
    inputs.fromChainID,
    inputs.toChainID,
    inputs.fromToken,
    inputs.toToken,
  ]);

  useEffect(() => {
    // Do not refresh after the user has accepted the intent (dialog open)
    if (!swapIntent || isDialogOpen) return;
    const id = setInterval(async () => {
      try {
        const updated = await swapIntent.refresh();
        setSwapIntent({ ...swapIntent, intent: updated });
      } catch (e) {
        console.error(e);
      }
    }, 15000);
    return () => clearInterval(id);
  }, [swapIntent, setSwapIntent, isDialogOpen]);

  return {
    inputs,
    setInputs,
    loading,
    isDialogOpen,
    setIsDialogOpen,
    txError,
    setTxError,
    timer,
    steps,
    explorerUrl,
    sourceExplorerUrl,
    destinationExplorerUrl,
    handleSwap,
    reset,
  };
};

