export const INTENT_HISTORY_REFRESH_EVENT = "nexus:intent-history:refresh";

export const notifyIntentHistoryRefresh = () => {
  if (typeof window === "undefined") return;
  window.dispatchEvent(new Event(INTENT_HISTORY_REFRESH_EVENT));
};

import { type RFF } from "@avail-project/nexus-core";
import { useNexus } from "../../nexus/NexusProvider";
import { useCallback, useEffect, useState } from "react";
import { INTENT_HISTORY_REFRESH_EVENT } from "../history-events";

const ITEMS_PER_PAGE = 10;

function formatExpiryDate(timestamp: number) {
  const date = new Date(timestamp * 1000);
  const formatted = date.toLocaleString("en-US", {
    month: "short",
    day: "2-digit",
    year: "numeric",
  });
  return formatted.replace(" ", ", ");
}

const useViewHistory = () => {
  const { nexusSDK } = useNexus();
  const [history, setHistory] = useState<RFF[] | null>(null);
  const [loadError, setLoadError] = useState<string | null>(null);
  const [displayedHistory, setDisplayedHistory] = useState<RFF[]>([]);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [sentinelNode, setSentinelNode] = useState<HTMLDivElement | null>(null);

  const observerTarget = useCallback((node: HTMLDivElement | null) => {
    setSentinelNode(node);
  }, []);

  const fetchIntentHistory = useCallback(async () => {
    if (!nexusSDK) return;
    try {
      const nextHistory = (await nexusSDK.getMyIntents()) ?? [];
      setLoadError(null);
      setHistory(nextHistory);
      const firstPage = nextHistory.slice(0, ITEMS_PER_PAGE);
      setDisplayedHistory(firstPage);
      setPage(0);
      setHasMore(nextHistory.length > ITEMS_PER_PAGE);
    } catch (error) {
      console.error("Error fetching intent history:", error);
      setLoadError("Please check your wallet connection and try again.");
      setHistory([]);
      setDisplayedHistory([]);
      setPage(0);
      setHasMore(false);
    }
  }, [nexusSDK]);

  useEffect(() => {
    void fetchIntentHistory();
  }, [fetchIntentHistory]);

  useEffect(() => {
    if (typeof window === "undefined") return;

    const handleRefresh = () => {
      void fetchIntentHistory();
    };

    window.addEventListener(INTENT_HISTORY_REFRESH_EVENT, handleRefresh);
    return () => {
      window.removeEventListener(INTENT_HISTORY_REFRESH_EVENT, handleRefresh);
    };
  }, [fetchIntentHistory]);

  const loadMore = useCallback(() => {
    if (!history || isLoadingMore || !hasMore) return;
    setIsLoadingMore(true);

    setTimeout(() => {
      const nextPage = page + 1;
      const startIndex = nextPage * ITEMS_PER_PAGE;
      const endIndex = startIndex + ITEMS_PER_PAGE;
      const newItems = history.slice(startIndex, endIndex);

      if (newItems.length > 0) {
        setDisplayedHistory((prev) => [...prev, ...newItems]);
        setPage(nextPage);
        setHasMore(endIndex < history.length);
      } else {
        setHasMore(false);
      }

      setIsLoadingMore(false);
    }, 300);
  }, [history, page, isLoadingMore, hasMore]);

  useEffect(() => {
    if (!sentinelNode) {
      return;
    }

    const rootElement = sentinelNode.parentElement;

    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasMore && !isLoadingMore) {
          loadMore();
        }
      },
      { threshold: 0.1, root: rootElement ?? null }
    );

    observer.observe(sentinelNode);

    return () => {
      observer.disconnect();
    };
  }, [sentinelNode, loadMore, hasMore, isLoadingMore, displayedHistory.length]);

  const getStatus = (pastIntent: RFF) => {
    if (pastIntent?.fulfilled) {
      return "Fulfilled";
    } else if (pastIntent?.deposited) {
      return "Deposited";
    } else if (pastIntent?.refunded) {
      return "Refunded";
    } else {
      return "Failed";
    }
  };

  return {
    history,
    loadError,
    displayedHistory,
    page,
    hasMore,
    isLoadingMore,
    getStatus,
    observerTarget,
    refreshHistory: fetchIntentHistory,
    ITEMS_PER_PAGE,
    formatExpiryDate,
  };
};

export default useViewHistory;

"use client";

import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTrigger,
  DialogTitle,
} from "@/registry/nexus-elements/ui/dialog";
import { Clock, LoaderPinwheel, SquareArrowOutUpRight } from "lucide-react";
import { TOKEN_METADATA, type RFF } from "@avail-project/nexus-core";
import { cn } from "@/lib/utils";
import { Badge } from "@/registry/nexus-elements/ui/badge";
import { Button } from "@/registry/nexus-elements/ui/button";
import { Card } from "@/registry/nexus-elements/ui/card";
import { Separator } from "@/registry/nexus-elements/ui/separator";
import useViewHistory from "./hooks/useViewHistory";
import { useEffect, useState } from "react";

const SourceChains = ({ sources }: { sources: RFF["sources"] }) => {
  const sourceList = sources ?? [];
  return (
    <div className="flex items-center">
      {sourceList.map((source, index) => (
        <div
          key={source?.chain?.id}
          className={cn(
            "rounded-full transition-transform hover:scale-110",
            index > 0 && "-ml-2"
          )}
          style={{ zIndex: sourceList.length - index }}
        >
          <img
            src={source?.chain?.logo}
            alt={source?.chain?.name}
            width={24}
            height={24}
            className="rounded-full"
          />
        </div>
      ))}
    </div>
  );
};

const StatusBadge = ({ status }: { status: string }) => {
  const getVariant = (status: string) => {
    if (status === "Fulfilled") {
      return "default";
    } else if (status === "Deposited") {
      return "secondary";
    } else if (status === "Refunded") {
      return "outline";
    } else if (status === "Failed") {
      return "destructive";
    } else {
      return "default";
    }
  };

  return (
    <Badge variant={getVariant(status)} className="px-3 py-1">
      <p className="text-xs font-semibold tracking-wide">{status}</p>
    </Badge>
  );
};

const DestinationToken = ({
  destination,
}: {
  destination: RFF["destinations"];
}) => {
  return (
    <div className="flex items-center">
      {destination.map((dest, index) => (
        <div
          key={dest.token.symbol}
          className={cn(
            "rounded-full transition-transform hover:scale-110",
            index > 0 && "-ml-2"
          )}
          style={{ zIndex: destination.length - index }}
        >
          <img
            src={TOKEN_METADATA[dest.token.symbol]?.icon ?? ""}
            alt={TOKEN_METADATA[dest.token.symbol]?.name}
            width={24}
            height={24}
            className="rounded-full"
          />
        </div>
      ))}
    </div>
  );
};

const ViewHistory = ({
  viewAsModal = true,
  className,
}: {
  viewAsModal?: boolean;
  className?: string;
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const {
    history,
    loadError,
    displayedHistory,
    hasMore,
    isLoadingMore,
    getStatus,
    observerTarget,
    refreshHistory,
    ITEMS_PER_PAGE,
    formatExpiryDate,
  } = useViewHistory();

  useEffect(() => {
    if (!viewAsModal || !isOpen) return;
    void refreshHistory();
  }, [isOpen, refreshHistory, viewAsModal]);

  const renderHistoryContent = () => {
    if (displayedHistory.length > 0) {
      return (
        <>
          {displayedHistory?.map((pastIntent) => (
            <Card
              key={pastIntent.id}
              className="p-4 hover:shadow-md transition-shadow duration-200 border-border/50 gap-3"
            >
              <div className="flex items-start justify-between gap-4">
                <div className="flex items-center gap-3">
                  <DestinationToken destination={pastIntent?.destinations} />
                  <div className="flex flex-col">
                    <p className="text-sm font-medium">
                      {pastIntent?.destinations
                        .map((d) => d?.token?.symbol)
                        .join(", ")}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      Intent #{pastIntent?.id}
                    </p>
                  </div>
                </div>
                <StatusBadge status={getStatus(pastIntent)} />
              </div>

              <Separator className="my-1" />

              <div className="flex flex-col sm:flex-row  items-start sm:items-center justify-between gap-4">
                <div className="flex items-center justify-between gap-x-3 flex-1 w-full sm:min-w-fit">
                  <SourceChains sources={pastIntent?.sources} />
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <div className="h-px w-8 bg-border" />
                    <span className="text-xs">â†’</span>
                    <div className="h-px w-8 bg-border" />
                  </div>
                  <div className="rounded-full hover:scale-110">
                    <img
                      src={pastIntent?.destinationChain?.logo ?? ""}
                      alt={pastIntent?.destinationChain?.name}
                      width={24}
                      height={24}
                      className="rounded-full"
                    />
                  </div>
                </div>

                <div className="flex items-center justify-between sm:justify-end  gap-x-2 w-full">
                  <div className="text-left sm:text-right">
                    <p className="text-xs text-muted-foreground">Expiry</p>
                    <p className="text-xs font-medium">
                      {formatExpiryDate(pastIntent?.expiry)}
                    </p>
                  </div>
                  <a
                    href={pastIntent?.explorerUrl}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <Button variant="outline" size="icon">
                      <SquareArrowOutUpRight className="size-4" />
                    </Button>
                  </a>
                </div>
              </div>
            </Card>
          ))}

          {hasMore && (
            <div ref={observerTarget} className="flex justify-center py-4">
              {isLoadingMore && (
                <div className="flex items-center gap-2 text-muted-foreground">
                  <LoaderPinwheel className="size-4 animate-spin" />
                  <span className="text-sm">Loading more...</span>
                </div>
              )}
            </div>
          )}

          {!hasMore && displayedHistory?.length > ITEMS_PER_PAGE && (
            <div className="flex justify-center py-4">
              <p className="text-sm text-muted-foreground">
                No more transactions to load
              </p>
            </div>
          )}
        </>
      );
    }

    if (history === null) {
      return (
        <div className="flex flex-col items-center justify-center py-16 gap-4">
          <div className="relative">
            <div className="absolute inset-0 bg-primary/10 blur-xl rounded-full" />
            <LoaderPinwheel className="relative animate-spin size-12 text-primary" />
          </div>
          <div className="text-center space-y-1">
            <p className="text-base font-medium">Loading your history</p>
            <p className="text-sm text-muted-foreground">
              Fetching your past transactions...
            </p>
          </div>
        </div>
      );
    }

    if (loadError) {
      return (
        <div className="flex flex-col items-center justify-center py-16 gap-4">
          <Clock className="size-16 text-muted-foreground/30" />
          <div className="text-center space-y-1">
            <p className="text-base font-medium">Unable to load history</p>
            <p className="text-sm text-muted-foreground">{loadError}</p>
          </div>
          <Button
            variant="outline"
            onClick={() => {
              void refreshHistory();
            }}
          >
            Retry
          </Button>
        </div>
      );
    }

    return (
      <div className="flex flex-col items-center justify-center py-16 gap-4">
        <Clock className="size-16 text-muted-foreground/30" />
        <div className="text-center space-y-1">
          <p className="text-base font-medium">No history yet</p>
          <p className="text-sm text-muted-foreground">
            Your transaction history will appear here
          </p>
        </div>
      </div>
    );
  };

  if (!viewAsModal) {
    return (
      <div className="flex flex-col gap-y-3 max-h-96 no-scrollbar overflow-y-auto w-full max-w-md">
        {renderHistoryContent()}
      </div>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button
          variant="ghost"
          size="icon"
          className={cn("relative group", className)}
        >
          <Clock className="size-5 text-primary transition-transform group-hover:scale-110" />
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl flex flex-col">
        <DialogHeader>
          <DialogTitle className="text-2xl font-semibold">
            Transaction History
          </DialogTitle>
        </DialogHeader>
        <div className="flex flex-col gap-y-3 max-h-96 no-scrollbar overflow-y-auto w-full">
          {renderHistoryContent()}
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default ViewHistory;

"use client";

import { Component, type ErrorInfo, type ReactNode } from "react";

interface ErrorBoundaryProps {
  children: ReactNode;
  fallback?: ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
}

interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
}

/**
 * Error boundary component that catches JavaScript errors in child components.
 * Displays a fallback UI instead of crashing the entire widget.
 *
 * @example
 * <ErrorBoundary
 *   fallback={<div>Something went wrong</div>}
 *   onError={(error) => console.error(error)}
 * >
 *   <MyComponent />
 * </ErrorBoundary>
 */
export class ErrorBoundary extends Component<
  ErrorBoundaryProps,
  ErrorBoundaryState
> {
  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo): void {
    console.error("ErrorBoundary caught an error:", error, errorInfo);
    this.props.onError?.(error, errorInfo);
  }

  render(): ReactNode {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <div className="flex flex-col items-center justify-center p-6 text-center">
          <div className="text-destructive font-medium mb-2">
            Something went wrong
          </div>
          <p className="text-muted-foreground text-sm mb-4">
            An unexpected error occurred. Please try again.
          </p>
          <button
            onClick={() => this.setState({ hasError: false, error: null })}
            className="px-4 py-2 text-sm font-medium text-primary-foreground bg-primary rounded-md hover:bg-primary/90 transition-colors"
          >
            Try again
          </button>
        </div>
      );
    }

    return this.props.children;
  }
}

/**
 * A more specific error boundary for widget containers with reset capability.
 */
interface WidgetErrorBoundaryProps extends ErrorBoundaryProps {
  widgetName?: string;
  onReset?: () => void;
}

export class WidgetErrorBoundary extends Component<
  WidgetErrorBoundaryProps,
  ErrorBoundaryState
> {
  constructor(props: WidgetErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo): void {
    console.error(
      `WidgetErrorBoundary [${this.props.widgetName ?? "Unknown"}]:`,
      error,
      errorInfo
    );
    this.props.onError?.(error, errorInfo);
  }

  handleReset = (): void => {
    this.props.onReset?.();
    this.setState({ hasError: false, error: null });
  };

  render(): ReactNode {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <div className="flex flex-col items-center justify-center p-6 text-center bg-destructive/5 rounded-lg border border-destructive/20">
          <div className="text-destructive font-medium mb-2">
            {this.props.widgetName
              ? `${this.props.widgetName} encountered an error`
              : "Widget error"}
          </div>
          <p className="text-muted-foreground text-sm mb-4">
            {this.state.error?.message || "An unexpected error occurred."}
          </p>
          <button
            onClick={this.handleReset}
            className="px-4 py-2 text-sm font-medium text-primary-foreground bg-primary rounded-md hover:bg-primary/90 transition-colors"
          >
            Reset widget
          </button>
        </div>
      );
    }

    return this.props.children;
  }
}

import { useEffect, useMemo, useRef } from "react";
import { useStableCallback } from "./useStableCallback";

type AnyFn = (...args: any[]) => any;

export interface Debounced<T extends AnyFn> {
  (...args: Parameters<T>): void;
  cancel: () => void;
  flush: () => void;
}

/**
 * Returns a debounced function that delays invoking `fn` until after `delay`
 * milliseconds have elapsed since the last call.
 */
export function useDebouncedCallback<T extends AnyFn>(
  fn: T,
  delay: number
): Debounced<T> {
  const latest = useStableCallback(fn);
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const lastArgsRef = useRef<Parameters<T> | null>(null);

  const cancel = () => {
    if (timerRef.current) {
      clearTimeout(timerRef.current);
      timerRef.current = null;
    }
  };

  const flush = () => {
    if (timerRef.current && lastArgsRef.current) {
      clearTimeout(timerRef.current);
      timerRef.current = null;

      latest(...lastArgsRef.current);
      lastArgsRef.current = null;
    }
  };

  // cancel when delay changes/unmounts
  useEffect(() => cancel, [delay]);

  return useMemo(() => {
    const debounced = ((...args: Parameters<T>) => {
      lastArgsRef.current = args;
      cancel();
      timerRef.current = setTimeout(() => {

        latest(...lastArgsRef.current!);
        lastArgsRef.current = null;
        timerRef.current = null;
      }, delay);
    }) as Debounced<T>;
    debounced.cancel = cancel;
    debounced.flush = flush;
    return debounced;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [delay, latest]);
}

import { useEffect, useState } from "react";
import { useDebouncedCallback } from "./useDebouncedCallback";

/**
 * Derives a debounced value from an input value and delay.
 */
export function useDebouncedValue<T>(value: T, delay: number): T {
  const [debounced, setDebounced] = useState<T>(value);
  const setter = useDebouncedCallback((v: T) => setDebounced(v), delay);

  useEffect(() => {
    setter(value);
    return setter.cancel;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [value, delay]);

  return debounced;
}

import { useEffect, useRef } from "react";
import { useStableCallback } from "./useStableCallback";

interface UseIntervalOptions {
  enabled?: boolean;
  immediate?: boolean;
}

/**
 * Declarative setInterval with pause/resume and latest-callback semantics.
 * Pass delay=null to pause.
 */
export function useInterval(
  callback: () => void,
  delay: number | null,
  options: UseIntervalOptions = {}
) {
  const { enabled = true, immediate = false } = options;
  const savedCallback = useStableCallback(callback);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (!enabled || delay == null) return;
    if (immediate) {
      savedCallback();
    }
    intervalRef.current = setInterval(savedCallback, delay);
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, [delay, enabled, immediate, savedCallback]);
}

import { useRef, useLayoutEffect } from "react";

/**
 * Returns a ref that always contains the latest value.
 * Useful for accessing current values in callbacks without causing re-renders.
 *
 * @example
 * const countRef = useLatest(count);
 * const handleClick = useCallback(() => {
 *   console.log(countRef.current); // Always the latest count
 * }, []); // No dependency needed!
 */
export function useLatest<T>(value: T): React.MutableRefObject<T> {
  const ref = useRef<T>(value);

  // Use useLayoutEffect to update synchronously before any effects run
  useLayoutEffect(() => {
    ref.current = value;
  });

  return ref;
}

import { NexusError } from "@avail-project/nexus-core";

function handler(err: unknown) {
  if (err instanceof NexusError) {
    return {
      code: err?.code,
      message: err?.message,
      context: err?.data?.context,
      details: err?.data?.details,
    };
  } else {
    console.error("Unexpected error:", err);
    return {
      code: "unexpected_error",
      message: "Oops! Something went wrong. Please try again.",
      context: undefined,
      details: undefined,
    };
  }
}
export function useNexusError() {
  return handler;
}

import { useRef } from "react";
import { useInterval } from "./useInterval";
import { useStableCallback } from "./useStableCallback";

/**
 * Declarative polling with in-flight protection (no overlap).
 * When enabled becomes true, an immediate run is executed,
 * followed by interval-based runs.
 */
export function usePolling(
  enabled: boolean,
  fn: () => Promise<void> | void,
  intervalMs: number
) {
  const inFlightRef = useRef(false);
  const wrapped = useStableCallback(async () => {
    if (inFlightRef.current) return;
    try {
      inFlightRef.current = true;
      await fn();
    } catch (error) {
      console.error(error);
    } finally {
      inFlightRef.current = false;
    }
  });

  useInterval(wrapped, enabled ? intervalMs : null, {
    enabled,
    immediate: enabled,
  });
}

import { useCallback, useRef } from "react";

/**
 * Returns a stable function identity that always calls the latest implementation.
 * Useful when passing callbacks to memoized children without re-creating handlers.
 */
export function useStableCallback<Args extends readonly unknown[], Return>(
  fn: (...args: Args) => Return
): (...args: Args) => Return {
  const fnRef = useRef<(...args: Args) => Return>(fn);
  fnRef.current = fn;


  const stable = useCallback(
    ((...args: Args) => {
      return fnRef.current(...args);
    }) as (...args: Args) => Return,
    []
  );

  return stable;
}

import { useEffect, useRef, useState } from "react";

interface UseStopwatchOptions {
  running?: boolean;
  intervalMs?: number;
}

/**
 * Simple stopwatch that increments elapsed seconds while running.
 * Designed to replace scattered timer effects.
 */
export function useStopwatch(options: UseStopwatchOptions = {}) {
  const { running = false, intervalMs = 100 } = options;
  const [elapsedSeconds, setElapsedSeconds] = useState(0);
  const timerRef = useRef<ReturnType<typeof setInterval> | null>(null);

  const reset = () => {
    setElapsedSeconds(0);
  };

  const stop = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const start = () => {
    if (timerRef.current) return;
    timerRef.current = setInterval(() => {
      // 1s == 1000ms; we add fractional seconds per tick
      setElapsedSeconds((prev) => prev + intervalMs / 1000);
    }, intervalMs);
  };

  useEffect(() => {
    if (running) {
      start();
    } else {
      stop();
    }
    return stop;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [running, intervalMs]);

  return {
    seconds: elapsedSeconds,
    start,
    stop,
    reset,
    running: Boolean(timerRef.current),
  };
}

import {
  type BridgeStepType,
  NEXUS_EVENTS,
  type NexusSDK,
  type OnAllowanceHookData,
  type OnIntentHookData,
} from "@avail-project/nexus-core";
import {
  type Dispatch,
  type RefObject,
  type SetStateAction,
  useCallback,
  useRef,
} from "react";
import { type TransactionStatus } from "../tx/types";
import {
  type SourceSelectionValidation,
  type TransactionFlowEvent,
  type TransactionFlowExecutor,
  type TransactionFlowInputs,
} from "../types/transaction-flow";

interface NexusErrorInfo {
  code: string;
  message: string;
  context?: unknown;
  details?: unknown;
}

type NexusErrorHandler = (error: unknown) => NexusErrorInfo;

interface UseTransactionExecutionProps {
  operationName: "bridge" | "transfer";
  nexusSDK: NexusSDK | null;
  intent: RefObject<OnIntentHookData | null>;
  allowance: RefObject<OnAllowanceHookData | null>;
  inputs: TransactionFlowInputs;
  configuredMaxAmount?: string;
  allAvailableSourceChainIds: number[];
  sourceChainsForSdk?: number[];
  sourceSelectionKey: string;
  sourceSelection: SourceSelectionValidation;
  loading: boolean;
  txError: string | null;
  areInputsValid: boolean;
  executeTransaction: TransactionFlowExecutor;
  getMaxForCurrentSelection: () => Promise<string | undefined>;
  onStepsList: (steps: BridgeStepType[]) => void;
  onStepComplete: (step: BridgeStepType) => void;
  resetSteps: () => void;
  setStatus: (status: TransactionStatus) => void;
  resetInputs: () => void;
  setRefreshing: Dispatch<SetStateAction<boolean>>;
  setIsDialogOpen: Dispatch<SetStateAction<boolean>>;
  setTxError: Dispatch<SetStateAction<string | null>>;
  setLastExplorerUrl: Dispatch<SetStateAction<string>>;
  setSelectedSourceChains: Dispatch<SetStateAction<number[] | null>>;
  setAppliedSourceSelectionKey: Dispatch<SetStateAction<string>>;
  stopwatch: {
    start: () => void;
    stop: () => void;
    reset: () => void;
  };
  handleNexusError: NexusErrorHandler;
  onStart?: () => void;
  onComplete?: () => void;
  onError?: (message: string) => void;
  fetchBalance: () => Promise<void>;
  notifyHistoryRefresh?: () => void;
}

export function useTransactionExecution({
  operationName,
  nexusSDK,
  intent,
  allowance,
  inputs,
  configuredMaxAmount,
  allAvailableSourceChainIds,
  sourceChainsForSdk,
  sourceSelectionKey,
  sourceSelection,
  loading,
  txError,
  areInputsValid,
  executeTransaction,
  getMaxForCurrentSelection,
  onStepsList,
  onStepComplete,
  resetSteps,
  setStatus,
  resetInputs,
  setRefreshing,
  setIsDialogOpen,
  setTxError,
  setLastExplorerUrl,
  setSelectedSourceChains,
  setAppliedSourceSelectionKey,
  stopwatch,
  handleNexusError,
  onStart,
  onComplete,
  onError,
  fetchBalance,
  notifyHistoryRefresh,
}: UseTransactionExecutionProps) {
  const commitLockRef = useRef(false);
  const runIdRef = useRef(0);

  const refreshIntent = async (options?: { reportError?: boolean }) => {
    if (!intent.current) return false;
    const activeRunId = runIdRef.current;
    setRefreshing(true);
    try {
      const updated = await intent.current.refresh(sourceChainsForSdk);
      if (activeRunId !== runIdRef.current) return false;
      if (updated) {
        intent.current.intent = updated;
      }
      setAppliedSourceSelectionKey(sourceSelectionKey);
      return true;
    } catch (error) {
      if (activeRunId !== runIdRef.current) return false;
      console.error("Transaction failed:", error);
      if (options?.reportError) {
        const message = "Unable to refresh source selection. Please try again.";
        setTxError(message);
        onError?.(message);
      }
      return false;
    } finally {
      if (activeRunId !== runIdRef.current) return;
      setRefreshing(false);
    }
  };

  const onSuccess = async () => {
    stopwatch.stop();
    setStatus("success");
    onComplete?.();
    intent.current = null;
    allowance.current = null;
    resetInputs();
    setRefreshing(false);
    setSelectedSourceChains(null);
    setAppliedSourceSelectionKey("ALL");
    await fetchBalance();
    notifyHistoryRefresh?.();
  };

  const handleTransaction = async () => {
    if (commitLockRef.current) return;
    commitLockRef.current = true;
    const currentRunId = ++runIdRef.current;
    let didEnterExecutingState = false;
    const cleanupSupersededExecution = () => {
      if (!didEnterExecutingState) return;
      setRefreshing(false);
      setIsDialogOpen(false);
      setLastExplorerUrl("");
      stopwatch.stop();
      stopwatch.reset();
      resetSteps();
      setStatus("idle");
    };

    try {
      if (
        !inputs?.amount ||
        !inputs?.recipient ||
        !inputs?.chain ||
        !inputs?.token
      ) {
        console.error("Missing required inputs");
        return;
      }
      if (!nexusSDK) {
        const message = "Nexus SDK not initialized";
        setTxError(message);
        onError?.(message);
        return;
      }
      if (allAvailableSourceChainIds.length === 0) {
        const message =
          "No eligible source chains available for the selected token and destination.";
        setTxError(message);
        onError?.(message);
        setStatus("error");
        return;
      }

      const parsedAmount = Number(inputs.amount);
      if (!Number.isFinite(parsedAmount) || parsedAmount <= 0) {
        const message = "Enter a valid amount greater than 0.";
        setTxError(message);
        onError?.(message);
        setStatus("error");
        return;
      }

      const amountBigInt = nexusSDK.convertTokenReadableAmountToBigInt(
        inputs.amount,
        inputs.token,
        inputs.chain,
      );

      if (configuredMaxAmount) {
        const configuredMaxRaw = nexusSDK.convertTokenReadableAmountToBigInt(
          configuredMaxAmount,
          inputs.token,
          inputs.chain,
        );
        if (amountBigInt > configuredMaxRaw) {
          const message = `Amount exceeds maximum limit of ${configuredMaxAmount} ${inputs.token}.`;
          setTxError(message);
          onError?.(message);
          setStatus("error");
          return;
        }
      }

      const maxForCurrentSelection = await getMaxForCurrentSelection();
      if (currentRunId !== runIdRef.current) return;
      if (!maxForCurrentSelection) {
        const message = `Unable to determine max ${operationName} amount for selected sources. Please try again.`;
        setTxError(message);
        onError?.(message);
        setStatus("error");
        return;
      }
      const maxForSelectionRaw = nexusSDK.convertTokenReadableAmountToBigInt(
        maxForCurrentSelection,
        inputs.token,
        inputs.chain,
      );
      if (amountBigInt > maxForSelectionRaw) {
        const message = `Selected sources can provide up to ${maxForCurrentSelection} ${inputs.token}. Reduce amount or enable more sources.`;
        setTxError(message);
        onError?.(message);
        setStatus("error");
        return;
      }

      setStatus("executing");
      didEnterExecutingState = true;
      setTxError(null);
      onStart?.();
      setLastExplorerUrl("");
      setAppliedSourceSelectionKey(sourceSelectionKey);

      const onEvent = (event: TransactionFlowEvent) => {
        if (currentRunId !== runIdRef.current) return;
        if (event.name === NEXUS_EVENTS.STEPS_LIST) {
          const list = Array.isArray(event.args) ? event.args : [];
          onStepsList(list as BridgeStepType[]);
        }
        if (event.name === NEXUS_EVENTS.STEP_COMPLETE) {
          if (
            !Array.isArray(event.args) &&
            "type" in event.args &&
            event.args.type === "INTENT_HASH_SIGNED"
          ) {
            stopwatch.start();
          }
          if (!Array.isArray(event.args)) {
            onStepComplete(event.args as BridgeStepType);
          }
        }
      };

      const transactionResult = await executeTransaction({
        token: inputs.token,
        amount: amountBigInt,
        toChainId: inputs.chain,
        recipient: inputs.recipient,
        sourceChains: sourceChainsForSdk,
        onEvent,
      });

      if (currentRunId !== runIdRef.current) {
        cleanupSupersededExecution();
        return;
      }
      if (!transactionResult) {
        throw new Error("Transaction rejected by user");
      }
      setLastExplorerUrl(transactionResult.explorerUrl);
      await onSuccess();
    } catch (error) {
      if (currentRunId !== runIdRef.current) {
        cleanupSupersededExecution();
        return;
      }
      const { message, code, context, details } = handleNexusError(error);
      console.error(`Fast ${operationName} transaction failed:`, {
        code,
        message,
        context,
        details,
      });
      intent.current?.deny();
      intent.current = null;
      allowance.current = null;
      setTxError(message);
      onError?.(message);
      setIsDialogOpen(false);
      setSelectedSourceChains(null);
      setRefreshing(false);
      stopwatch.stop();
      stopwatch.reset();
      resetSteps();
      void fetchBalance();
      setStatus("error");
    } finally {
      commitLockRef.current = false;
    }
  };

  const reset = () => {
    runIdRef.current += 1;
    intent.current?.deny();
    intent.current = null;
    allowance.current = null;
    resetInputs();
    setStatus("idle");
    setRefreshing(false);
    setSelectedSourceChains(null);
    setAppliedSourceSelectionKey("ALL");
    setLastExplorerUrl("");
    stopwatch.stop();
    stopwatch.reset();
    resetSteps();
  };

  const startTransaction = () => {
    if (!intent.current) return;
    if (allAvailableSourceChainIds.length === 0) {
      const message =
        "No eligible source chains available for the selected token and destination.";
      setTxError(message);
      onError?.(message);
      return;
    }
    if (sourceSelection.isBelowRequired && inputs?.token) {
      const message = `Selected sources are not enough. Add ${sourceSelection.missingToProceed} ${inputs.token} more to make this transaction.`;
      setTxError(message);
      onError?.(message);
      return;
    }
    void (async () => {
      const refreshed = await refreshIntent({ reportError: true });
      if (!refreshed || !intent.current) return;
      intent.current.allow();
      setIsDialogOpen(true);
      setTxError(null);
    })();
  };

  const commitAmount = async () => {
    if (intent.current || loading || txError || !areInputsValid) return;
    await handleTransaction();
  };

  const invalidatePendingExecution = useCallback(() => {
    runIdRef.current += 1;
    if (intent.current) {
      intent.current.deny();
      intent.current = null;
    }
    setRefreshing(false);
    setAppliedSourceSelectionKey("ALL");
  }, [intent, setAppliedSourceSelectionKey, setRefreshing]);

  return {
    refreshIntent,
    handleTransaction,
    startTransaction,
    commitAmount,
    reset,
    invalidatePendingExecution,
  };
}

import {
  type BridgeStepType,
  type NexusNetwork,
  NexusSDK,
  type OnAllowanceHookData,
  type OnIntentHookData,
  parseUnits,
  type UserAsset,
} from "@avail-project/nexus-core";
import {
  useEffect,
  useMemo,
  useCallback,
  useRef,
  useState,
  useReducer,
  type RefObject,
} from "react";
import { type Address, isAddress } from "viem";
import { useNexusError } from "./useNexusError";
import { useTransactionExecution } from "./useTransactionExecution";
import { usePolling } from "./usePolling";
import { useStopwatch } from "./useStopwatch";
import { useDebouncedCallback } from "./useDebouncedCallback";
import { type TransactionStatus } from "../tx/types";
import { useTransactionSteps } from "../tx/useTransactionSteps";
import {
  type SourceCoverageState,
  type TransactionFlowExecutor,
  type TransactionFlowInputs,
  type TransactionFlowPrefill,
  type TransactionFlowType,
} from "../types/transaction-flow";
import {
  MAX_AMOUNT_DEBOUNCE_MS,
  buildInitialInputs,
  clampAmountToMax,
  formatAmountForDisplay,
  getCoverageDecimals,
  normalizeMaxAmount,
} from "../utils/transaction-flow";

interface BaseTransactionFlowProps {
  type: TransactionFlowType;
  network: NexusNetwork;
  nexusSDK: NexusSDK | null;
  intent: RefObject<OnIntentHookData | null>;
  allowance: RefObject<OnAllowanceHookData | null>;
  bridgableBalance: UserAsset[] | null;
  prefill?: TransactionFlowPrefill;
  onComplete?: () => void;
  onStart?: () => void;
  onError?: (message: string) => void;
  fetchBalance: () => Promise<void>;
  maxAmount?: string | number;
  isSourceMenuOpen?: boolean;
  notifyHistoryRefresh?: () => void;
  executeTransaction: TransactionFlowExecutor;
}

export interface UseTransactionFlowProps extends BaseTransactionFlowProps {
  connectedAddress?: Address;
}

type State = {
  inputs: TransactionFlowInputs;
  status: TransactionStatus;
};

type Action =
  | { type: "setInputs"; payload: Partial<TransactionFlowInputs> }
  | { type: "resetInputs" }
  | { type: "setStatus"; payload: TransactionStatus };

export function useTransactionFlow(props: UseTransactionFlowProps) {
  const {
    type,
    network,
    nexusSDK,
    intent,
    bridgableBalance,
    prefill,
    onComplete,
    onStart,
    onError,
    fetchBalance,
    allowance,
    maxAmount,
    isSourceMenuOpen = false,
    notifyHistoryRefresh,
    executeTransaction,
  } = props;

  const connectedAddress = props.connectedAddress;
  const operationName = type === "bridge" ? "bridge" : "transfer";
  const handleNexusError = useNexusError();
  const initialState: State = {
    inputs: buildInitialInputs({ type, network, connectedAddress, prefill }),
    status: "idle",
  };

  function reducer(state: State, action: Action): State {
    switch (action.type) {
      case "setInputs":
        return { ...state, inputs: { ...state.inputs, ...action.payload } };
      case "resetInputs":
        return {
          ...state,
          inputs: buildInitialInputs({
            type,
            network,
            connectedAddress,
            prefill,
          }),
        };
      case "setStatus":
        return { ...state, status: action.payload };
      default:
        return state;
    }
  }

  const [state, dispatch] = useReducer(reducer, initialState);
  const inputs = state.inputs;
  const setInputs = (
    next: TransactionFlowInputs | Partial<TransactionFlowInputs>,
  ) => {
    dispatch({
      type: "setInputs",
      payload: next as Partial<TransactionFlowInputs>,
    });
  };

  const loading = state.status === "executing";
  const [refreshing, setRefreshing] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [txError, setTxError] = useState<string | null>(null);
  const [lastExplorerUrl, setLastExplorerUrl] = useState<string>("");
  const previousConnectedAddressRef = useRef<Address | undefined>(
    connectedAddress,
  );
  const maxAmountRequestIdRef = useRef(0);
  const [selectedSourceChains, setSelectedSourceChains] = useState<
    number[] | null
  >(null);
  const [selectedSourcesMaxAmount, setSelectedSourcesMaxAmount] = useState<
    string | null
  >(null);
  const [appliedSourceSelectionKey, setAppliedSourceSelectionKey] =
    useState("ALL");
  const {
    steps,
    onStepsList,
    onStepComplete,
    reset: resetSteps,
  } = useTransactionSteps<BridgeStepType>();
  const configuredMaxAmount = useMemo(
    () => normalizeMaxAmount(maxAmount),
    [maxAmount],
  );

  const areInputsValid = useMemo(() => {
    const hasToken = inputs?.token !== undefined && inputs?.token !== null;
    const hasChain = inputs?.chain !== undefined && inputs?.chain !== null;
    const hasAmount = Boolean(inputs?.amount) && Number(inputs?.amount) > 0;
    const hasValidRecipient =
      Boolean(inputs?.recipient) && isAddress(inputs.recipient as string);
    return hasToken && hasChain && hasAmount && hasValidRecipient;
  }, [inputs]);

  const filteredBridgableBalance = useMemo(() => {
    return bridgableBalance?.find((bal) =>
      inputs?.token === "USDM"
        ? bal?.symbol === "USDC"
        : bal?.symbol === inputs?.token,
    );
  }, [bridgableBalance, inputs?.token]);

  const availableSources = useMemo(() => {
    const breakdown = filteredBridgableBalance?.breakdown ?? [];
    const destinationChainId = inputs?.chain;
    const nonZero = breakdown.filter((source) => {
      if (Number.parseFloat(source.balance ?? "0") <= 0) return false;
      if (typeof destinationChainId === "number") {
        return source.chain.id !== destinationChainId;
      }
      return true;
    });
    const decimals = filteredBridgableBalance?.decimals;
    if (!nexusSDK || typeof decimals !== "number") {
      return nonZero.sort(
        (a, b) => Number.parseFloat(b.balance) - Number.parseFloat(a.balance),
      );
    }
    return nonZero.sort((a, b) => {
      try {
        const aRaw = parseUnits(a.balance ?? "0", decimals);
        const bRaw = parseUnits(b.balance ?? "0", decimals);
        if (aRaw === bRaw) return 0;
        return aRaw > bRaw ? -1 : 1;
      } catch {
        return Number.parseFloat(b.balance) - Number.parseFloat(a.balance);
      }
    });
  }, [
    inputs?.chain,
    filteredBridgableBalance?.breakdown,
    filteredBridgableBalance?.decimals,
    nexusSDK,
  ]);

  const allAvailableSourceChainIds = useMemo(
    () => availableSources.map((source) => source.chain.id),
    [availableSources],
  );

  const effectiveSelectedSourceChains = useMemo(() => {
    if (selectedSourceChains && selectedSourceChains.length > 0) {
      const availableSet = new Set(allAvailableSourceChainIds);
      const filteredSelection = selectedSourceChains.filter((id) =>
        availableSet.has(id),
      );
      if (filteredSelection.length > 0) {
        return filteredSelection;
      }
    }
    return allAvailableSourceChainIds;
  }, [selectedSourceChains, allAvailableSourceChainIds]);

  const sourceChainsForSdk =
    effectiveSelectedSourceChains.length > 0
      ? effectiveSelectedSourceChains
      : undefined;

  const sourceSelectionKey = useMemo(() => {
    if (allAvailableSourceChainIds.length === 0) return "NONE";
    if (!selectedSourceChains || selectedSourceChains.length === 0) {
      return "ALL";
    }
    return [...effectiveSelectedSourceChains].sort((a, b) => a - b).join("|");
  }, [
    allAvailableSourceChainIds.length,
    effectiveSelectedSourceChains,
    selectedSourceChains,
  ]);
  const hasPendingSourceSelectionChanges =
    sourceSelectionKey !== appliedSourceSelectionKey;
  const intentSourceSpendAmount = intent.current?.intent?.sourcesTotal;

  const getMaxForCurrentSelection = useCallback(async () => {
    if (!nexusSDK || !inputs?.token || !inputs?.chain) return undefined;
    const maxBalAvailable = await nexusSDK.calculateMaxForBridge({
      token: inputs.token,
      toChainId: inputs.chain,
      recipient: inputs.recipient,
      sourceChains: sourceChainsForSdk,
    });
    if (!maxBalAvailable?.amount) return "0";
    return clampAmountToMax({
      amount: maxBalAvailable.amount,
      maxAmount: configuredMaxAmount,
      nexusSDK,
      token: inputs.token,
      chainId: inputs.chain,
    });
  }, [
    configuredMaxAmount,
    inputs?.chain,
    inputs?.recipient,
    inputs?.token,
    nexusSDK,
    sourceChainsForSdk,
  ]);

  const toggleSourceChain = useCallback(
    (chainId: number) => {
      setSelectedSourceChains((prev) => {
        if (allAvailableSourceChainIds.length === 0) return prev;
        const current =
          prev && prev.length > 0 ? prev : allAvailableSourceChainIds;
        const next = current.includes(chainId)
          ? current.filter((id) => id !== chainId)
          : [...current, chainId];
        if (next.length === 0) {
          return current;
        }
        const isAllSelected =
          next.length === allAvailableSourceChainIds.length &&
          allAvailableSourceChainIds.every((id) => next.includes(id));
        return isAllSelected ? null : next;
      });
    },
    [allAvailableSourceChainIds],
  );

  const sourceSelection = useMemo(() => {
    const amount =
      intentSourceSpendAmount?.trim() ?? inputs?.amount?.trim() ?? "";
    const decimals = getCoverageDecimals({
      type,
      token: inputs?.token,
      chainId: inputs?.chain,
      fallback: filteredBridgableBalance?.decimals,
    });
    const selectedChainSet = new Set(effectiveSelectedSourceChains);
    const selectedTotalRaw =
      !nexusSDK || typeof decimals !== "number"
        ? BigInt(0)
        : availableSources.reduce((sum, source) => {
            if (!selectedChainSet.has(source.chain.id)) return sum;
            try {
              return sum + parseUnits(source.balance ?? "0", decimals);
            } catch {
              return sum;
            }
          }, BigInt(0));
    const selectedTotal =
      !nexusSDK || typeof decimals !== "number"
        ? "0"
        : formatAmountForDisplay(selectedTotalRaw, decimals, nexusSDK);
    const baseSelection = {
      selectedTotal,
      requiredTotal: amount || "0",
      requiredSafetyTotal: amount || "0",
      missingToProceed: "0",
      missingToSafety: "0",
      coverageState: "healthy" as SourceCoverageState,
      coverageToSafetyPercent: 100,
      isBelowRequired: false,
      isBelowSafetyBuffer: false,
    };

    if (!nexusSDK || !inputs?.token || !inputs?.chain || !amount) {
      return baseSelection;
    }

    try {
      const requiredRaw = nexusSDK.convertTokenReadableAmountToBigInt(
        amount,
        inputs.token,
        inputs.chain,
      );
      if (requiredRaw <= BigInt(0)) {
        return baseSelection;
      }

      const missingToProceedRaw =
        selectedTotalRaw >= requiredRaw
          ? BigInt(0)
          : requiredRaw - selectedTotalRaw;
      const missingToSafetyRaw = missingToProceedRaw;

      const coverageState: SourceCoverageState =
        selectedTotalRaw < requiredRaw ? "error" : "healthy";

      const coverageBasisPoints =
        requiredRaw === BigInt(0)
          ? 10_000
          : selectedTotalRaw >= requiredRaw
            ? 10_000
            : Number((selectedTotalRaw * BigInt(10_000)) / requiredRaw);

      return {
        selectedTotal,
        requiredTotal: amount,
        requiredSafetyTotal: amount,
        missingToProceed: formatAmountForDisplay(
          missingToProceedRaw,
          decimals,
          nexusSDK,
        ),
        missingToSafety: formatAmountForDisplay(
          missingToSafetyRaw,
          decimals,
          nexusSDK,
        ),
        coverageState,
        coverageToSafetyPercent: coverageBasisPoints / 100,
        isBelowRequired: coverageState === "error",
        isBelowSafetyBuffer: coverageState === "error",
      };
    } catch {
      return baseSelection;
    }
  }, [
    type,
    filteredBridgableBalance?.decimals,
    nexusSDK,
    inputs?.chain,
    inputs?.amount,
    inputs?.token,
    intentSourceSpendAmount,
    availableSources,
    effectiveSelectedSourceChains,
  ]);

  const stopwatch = useStopwatch({ intervalMs: 100 });
  const setStatus = useCallback(
    (status: TransactionStatus) =>
      dispatch({ type: "setStatus", payload: status }),
    [],
  );

  const resetInputs = useCallback(() => {
    dispatch({ type: "resetInputs" });
  }, []);

  const {
    refreshIntent,
    handleTransaction,
    startTransaction,
    commitAmount,
    reset,
    invalidatePendingExecution,
  } = useTransactionExecution({
    operationName,
    nexusSDK,
    intent,
    allowance,
    inputs,
    configuredMaxAmount,
    allAvailableSourceChainIds,
    sourceChainsForSdk,
    sourceSelectionKey,
    sourceSelection,
    loading,
    txError,
    areInputsValid,
    executeTransaction,
    getMaxForCurrentSelection,
    onStepsList,
    onStepComplete,
    resetSteps,
    setStatus,
    resetInputs,
    setRefreshing,
    setIsDialogOpen,
    setTxError,
    setLastExplorerUrl,
    setSelectedSourceChains,
    setAppliedSourceSelectionKey,
    stopwatch,
    handleNexusError,
    onStart,
    onComplete,
    onError,
    fetchBalance,
    notifyHistoryRefresh,
  });

  usePolling(
    Boolean(intent.current) &&
      !isDialogOpen &&
      !isSourceMenuOpen &&
      !hasPendingSourceSelectionChanges,
    async () => {
      await refreshIntent();
    },
    15000,
  );

  const debouncedRefreshMaxForSelection = useDebouncedCallback(
    async (requestId: number) => {
      try {
        const maxForCurrentSelection = await getMaxForCurrentSelection();
        if (requestId !== maxAmountRequestIdRef.current) return;
        setSelectedSourcesMaxAmount(maxForCurrentSelection ?? "0");
      } catch (error) {
        if (requestId !== maxAmountRequestIdRef.current) return;
        console.error("Unable to calculate max for selected sources:", error);
        setSelectedSourcesMaxAmount("0");
      }
    },
    MAX_AMOUNT_DEBOUNCE_MS,
  );

  useEffect(() => {
    debouncedRefreshMaxForSelection.cancel();
    if (!nexusSDK || !inputs?.token || !inputs?.chain) {
      maxAmountRequestIdRef.current += 1;
      setSelectedSourcesMaxAmount(null);
      return;
    }
    if (allAvailableSourceChainIds.length === 0) {
      maxAmountRequestIdRef.current += 1;
      setSelectedSourcesMaxAmount("0");
      return;
    }
    const requestId = ++maxAmountRequestIdRef.current;
    debouncedRefreshMaxForSelection(requestId);
  }, [
    allAvailableSourceChainIds.length,
    configuredMaxAmount,
    debouncedRefreshMaxForSelection,
    inputs?.recipient,
    sourceSelectionKey,
    inputs?.chain,
    inputs?.token,
    nexusSDK,
  ]);

  useEffect(() => {
    if (type !== "bridge" || !connectedAddress) return;
    const previousConnectedAddress = previousConnectedAddressRef.current;
    if (!previousConnectedAddress) {
      previousConnectedAddressRef.current = connectedAddress;
      return;
    }
    if (connectedAddress === previousConnectedAddress) return;
    previousConnectedAddressRef.current = connectedAddress;
    if (prefill?.recipient) return;
    if (!inputs?.recipient || inputs.recipient === previousConnectedAddress) {
      dispatch({ type: "setInputs", payload: { recipient: connectedAddress } });
    }
  }, [type, connectedAddress, inputs?.recipient, prefill?.recipient]);

  useEffect(() => {
    invalidatePendingExecution();
  }, [inputs, invalidatePendingExecution]);

  useEffect(() => {
    setSelectedSourceChains(null);
  }, [inputs?.token]);

  useEffect(() => {
    if (!isDialogOpen) {
      stopwatch.stop();
      stopwatch.reset();
    }
  }, [isDialogOpen, stopwatch]);

  useEffect(() => {
    if (txError) {
      setTxError(null);
    }
  }, [inputs, txError]);

  return {
    inputs,
    setInputs,
    timer: stopwatch.seconds,
    setIsDialogOpen,
    setTxError,
