export function seedSteps<T>(expected: T[]): Array<GenericStep<T>> {
  return expected.map((st, index) => ({
    id: index,
    completed: false,
    step: st,
  }));
}

export function computeAllCompleted<T>(steps: Array<GenericStep<T>>): boolean {
  return steps.length > 0 && steps.every((s) => s.completed);
}

/**
 * Replace the current list of steps with a new list, preserving completion
 * for any steps that were already marked completed (matched by key).
 */
export function mergeStepsList<T>(
  prev: Array<GenericStep<T>>,
  list: T[]
): Array<GenericStep<T>> {
  const completedKeys = new Set<string>();
  for (const prevStep of prev) {
    if (prevStep.completed) {
      completedKeys.add(getStepKey(prevStep.step));
    }
  }
  const next: Array<GenericStep<T>> = [];
  for (let index = 0; index < list.length; index++) {
    const step = list[index];
    const key = getStepKey(step);
    next.push({
      id: index,
      completed: completedKeys.has(key),
      step,
    });
  }
  return next;
}

/**
 * Mark a step complete in-place; if the step doesn't yet exist, append it.
 */
export function mergeStepComplete<T>(
  prev: Array<GenericStep<T>>,
  step: T
): Array<GenericStep<T>> {
  const key = getStepKey(step);
  const updated: Array<GenericStep<T>> = [];
  let found = false;
  for (const s of prev) {
    if (getStepKey(s.step) === key) {
      updated.push({ ...s, completed: true, step: { ...s.step, ...step } });
      found = true;
    } else {
      updated.push(s);
    }
  }
  if (!found) {
    updated.push({
      id: updated.length,
      completed: true,
      step,
    });
  }
  return updated;
}

export type TransactionStatus =
  | "idle"
  | "preview"
  | "awaiting-approval"
  | "executing"
  | "success"
  | "error";

export type GenericStep<TStep> = {
  id: number;
  completed: boolean;
  step: TStep;
};

/**
 * Normalizes a step to a stable key. Prefers typeID, then type, otherwise JSON.
 */
export function getStepKey(step: any): string {
  if (!step) return "";
  if (typeof step.typeID === "string" && step.typeID.length > 0) {
    return step.typeID;
  }
  if (typeof step.type === "string" && step.type.length > 0) {
    return step.type;
  }
  try {
    return JSON.stringify(step);
  } catch {
    return String(step);
  }
}

import { useMemo, useRef, useState } from "react";
import type { GenericStep } from "./types";
import { getStepKey } from "./types";
import {
  computeAllCompleted,
  mergeStepComplete,
  mergeStepsList,
  seedSteps,
} from "./steps";

interface UseTransactionStepsOptions<T> {
  expected?: T[];
}

/**
 * Manages transaction steps with utilities to seed from expected steps,
 * replace the list on "steps list" events, and mark individual steps complete.
 */
export function useTransactionSteps<
  T extends { typeID?: string; type?: string }
>(options: UseTransactionStepsOptions<T> = {}) {
  const { expected } = options;
  const [steps, setSteps] = useState<Array<GenericStep<T>>>(() =>
    expected ? seedSteps(expected) : []
  );
  const lastSignatureRef = useRef<string>("");

  const onStepsList = (list: T[]) => {
    const signature = list.map((step) => getStepKey(step)).join("|");
    if (lastSignatureRef.current === signature) {
      setSteps((prev) => mergeStepsList(prev, list));
      return;
    }
    lastSignatureRef.current = signature;
    setSteps((prev) => mergeStepsList(prev, list));
  };

  const onStepComplete = (step: T) => {
    setSteps((prev) => mergeStepComplete(prev, step));
  };

  const seed = (expectedSteps: T[]) => {
    setSteps(seedSteps(expectedSteps));
  };

  const reset = () => {
    setSteps(expected ? seedSteps(expected) : []);
    lastSignatureRef.current = "";
  };

  const allCompleted = useMemo(() => computeAllCompleted(steps), [steps]);

  return {
    steps,
    allCompleted,
    onStepsList,
    onStepComplete,
    seed,
    reset,
  };
}

